var custom_content = `<meta charset='utf-8'/><html><head></head><body><div class="oald" id="entryContent"><div class="entry" hclass="entry" hlength="4" htag="section" id="call_1" idm_id="000008295" sk="call: :0" sum="8894"><div class="top-container"><div class="top-g" id="call_topg_3"><div class="webtop"><h1 class="headword" hclass="headword" htag="h1" id="call_h_2" opal_spoken="y" ox3000="y">call</h1> <span class="pos" hclass="pos" htag="span">verb</span><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a1"><span class="ox3ksym_a1"> </span></a><a href="https://www.oxfordlearnersdictionaries.com/wordlists/opal?dataset=english&amp;list=opal_spoken&amp;level=sublist_3"><span class="opal_symbol" href="OPAL_Spoken::Sublist_3">OPAL S</span></a></div><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="call"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_call__gb_3').play();" style="cursor: pointer" title="call pronunciationEnglish" valign="top"></a><span class="phon">/kɔːl/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="call"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_call__us_1').play();" style="cursor: pointer" title="call pronunciationAmerican" valign="top"></a><span class="phon">/kɔːl/</span></div></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="call_vpgs_1" unbox="verbforms"><span class="box_title" onclick="toggle_active(this)">Verb Forms</span><span class="body"><table class="verb_forms_table"><tbody><tr class="verb_form" form="root"><td class="verb_form"> <span class="vf_prefix">present simple I / you / we / they</span> call</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="call"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_call__gb_3').play();" style="cursor: pointer" title="call pronunciationEnglish" valign="top"></a><span class="phon">/kɔːl/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="call"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_call__us_1').play();" style="cursor: pointer" title="call pronunciationAmerican" valign="top"></a><span class="phon">/kɔːl/</span></div></span></td></tr><tr class="verb_form" form="thirdps"><td class="verb_form"> <span class="vf_prefix">he / she / it</span> calls</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="calls"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_calls__gb_1').play();" style="cursor: pointer" title="calls pronunciationEnglish" valign="top"></a><span class="phon">/kɔːlz/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="calls"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_calls__us_1').play();" style="cursor: pointer" title="calls pronunciationAmerican" valign="top"></a><span class="phon">/kɔːlz/</span></div></span></td></tr><tr class="verb_form" form="past"><td class="verb_form"> <span class="vf_prefix">past simple</span> called</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="called"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_called__gb_1').play();" style="cursor: pointer" title="called pronunciationEnglish" valign="top"></a><span class="phon">/kɔːld/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="called"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_called__us_1').play();" style="cursor: pointer" title="called pronunciationAmerican" valign="top"></a><span class="phon">/kɔːld/</span></div></span></td></tr><tr class="verb_form" form="pastpart"><td class="verb_form"> <span class="vf_prefix">past participle</span> called</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="called"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_called__gb_1').play();" style="cursor: pointer" title="called pronunciationEnglish" valign="top"></a><span class="phon">/kɔːld/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="called"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_called__us_1').play();" style="cursor: pointer" title="called pronunciationAmerican" valign="top"></a><span class="phon">/kɔːld/</span></div></span></td></tr><tr class="verb_form" form="prespart"><td class="verb_form"> <span class="vf_prefix">-ing form</span> calling</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="calling"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_calling__gb_2').play();" style="cursor: pointer" title="calling pronunciationEnglish" valign="top"></a><span class="phon">/ˈkɔːlɪŋ/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="calling"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_calling__us_2').play();" style="cursor: pointer" title="calling pronunciationAmerican" valign="top"></a><span class="phon">/ˈkɔːlɪŋ/</span></div></span></td></tr></tbody></table></span></span></div><span class="jumplinks"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/call#call_idmgs_1" title="Idioms definition"><span class="jumplink" href="call_idmgs_1" name="Idioms">Idioms</span></a> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/call#call_pvgs_1" title="Phrasal Verbs definition"><span class="jumplink" href="call_pvgs_1" name="Phrasal Verbs">Phrasal Verbs</span></a></span></div></div></div><ol class="senses_multiple" htag="ol"><span class="shcut-g" id="call_sngs_2"> <h2 class="shcut" hclass="shcut" htag="h2" id="call_shcut_1">give name</h2><li cefr="a1" class="sense" hclass="sense" htag="li" id="call_sng_2" ox3000="y" sensenum="1"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a1"><span class="ox3ksym_a1"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[transitive, often passive]</span> <span class="def" hclass="def" htag="span">to give somebody/something a particular name; to use a particular name or title when you are talking to somebody</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__1"> <span class="cf" hclass="cf" htag="span">call somebody/something + noun</span> <span class="x">They decided to call the baby Mark.</span></li><li class="" htag="li" id="OALD10_call__2"><span class="x">His name's Hiroshi but everyone calls him Hiro.</span></li><li class="" htag="li" id="OALD10_call__3"> <span class="cf" hclass="cf" htag="span">be called + noun</span> <span class="x">What's their son called?</span></li><li class="" htag="li" id="OALD10_call__4"><span class="x">I don't know anyone called Scott.</span></li><li class="" htag="li" id="OALD10_call__5"><span class="x">I've forgotten what the firm he works for is called.</span></li><li class="" htag="li" id="OALD10_call__6"><span class="x">What's it called again? Oh yes, a router.</span></li><li class="" htag="li" id="OALD10_call__7"><span class="x">to be <span class="cl">commonly/often called</span> something</span></li><li class="" htag="li" id="OALD10_call__8"><span class="x">This group of artists and poets is <span class="cl">sometimes called</span> the New York School. </span></li><li class="" htag="li" id="OALD10_call__9"><span class="cf" hclass="cf" htag="span">call somebody/something after somebody/something</span> <span class="x">They called their first daughter after her grandmother.</span></li><li class="" htag="li" id="OALD10_call__10"><span class="x">The cheese is called after the town of the same name.</span></li><li class="" htag="li" id="OALD10_call__11"> <span class="cf" hclass="cf" htag="span">call somebody/something by something</span> <span class="x">We call each other by our first names here.</span></li><li class="" htag="li" id="OALD10_call__12"><span class="x">The plant is called by different names in different parts of the country.</span></li></ul> <span class="xrefs" hclass="xrefs" htag="span" xt="see"><span class="prefix">see also</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/so-called" title="so-called definition"><span bord="n" class="xr-g" href="socalled_e"><span class="xh">so-called</span></span></a></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="call2_unbox_2" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__13"><span class="unx">His friends affectionately call him ‘Bear’.</span></li><li class="" htag="li" id="OALD10_call__14"><span class="unx">We usually call him by his nickname.</span></li><li class="" htag="li" id="OALD10_call__15"><span class="unx">What do they call that new fabric?</span></li><li class="" htag="li" id="OALD10_call__16"><span class="unx">He is writing a novel, tentatively called ‘My Future’.</span></li><li class="" htag="li" id="OALD10_call__17"><span class="unx">The area was formerly called West Meadow.</span></li><li class="" htag="li" id="OALD10_call__18"><span class="unx">The plant is variously called ‘cow parsley’ and ‘Queen Anne's lace’.</span></li><li class="" htag="li" id="OALD10_call__19"><span class="unx">The prison is euphemistically called a ‘rehabilitation centre’.</span></li><li class="" htag="li" id="OALD10_call__20"><span class="unx">The system is officially called the NPV System.</span></li></ul></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="call2_unbox_3" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adverb</span><ul class="collocs_list"><li class="li">commonly</li><li class="li">frequently</li><li class="li">formerly</li><li class="li">…</li></ul><span class="unbox">preposition</span><ul class="collocs_list"><li class="li">by</li></ul><span class="unbox">phrases</span><ul class="collocs_list"><li class="li">you could hardly call something…</li><li class="li">you would hardly call something…</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/call_2" title=" definition in Collocations">full entry</a></span></span></span></div> </li></span><span class="shcut-g" id="call_sngs_5"> <h2 class="shcut" hclass="shcut" htag="h2" id="call_shcut_4">phone</h2><li cefr="a1" class="sense" hclass="sense" htag="li" id="call_sng_8" ox3000="y" sensenum="2"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a1"><span class="ox3ksym_a1"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[intransitive, transitive]</span> <span class="def" hclass="def" htag="span">to phone somebody</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__21"><span class="x">I'll call again later.</span></li><li class="" htag="li" id="OALD10_call__22"><span class="x">I got a message to <span class="cl">call home</span> immediately.</span></li><li class="" htag="li" id="OALD10_call__23"> <span class="cf" hclass="cf" htag="span">call somebody/something to do something</span> <span class="x">I called the office to tell them I'd be late.</span></li><li class="" htag="li" id="OALD10_call__24"> <span class="cf" hclass="cf" htag="span">call to do something</span> <span class="x">People called to ask how they could help.</span></li><li class="" htag="li" id="OALD10_call__25"> <span class="cf" hclass="cf" htag="span">call somebody/something</span> <span class="x">My brother called me from Spain last night.</span></li><li class="" htag="li" id="OALD10_call__26"><span class="x">Call us toll-free on this number.</span></li><li class="" htag="li" id="OALD10_call__27"><span class="x">Call 0800 33344 for a free copy.</span></li></ul> <span class="xrefs" hclass="xrefs" htag="span" xt="see"><span class="prefix">see also</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/cold-call#cold-call_2" title="cold-call definition"><span bord="n" class="xr-g" href="coldcall_e"><span class="xh">cold-call</span></span></a></span><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/phones-email-and-the-internet?level=a1" title="Topic phones-email-and-the-internet"><span class="topic" href="l2:communication:phones_email_and_the_internet?cefr=a1"><span class="topic_name">Phones, email and the internet</span><span class="topic_cefr">a1</span></span></a></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="call2_unbox_4" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adverb</span><ul class="collocs_list"><li class="li">back</li><li class="li">ahead</li><li class="li">free</li><li class="li">…</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/call_2" title=" definition in Collocations">full entry</a></span></span></span></div> <div class=""> <div class="am-default contentslot" id="ad_contentslot_1"></div></div> </li><li cefr="a1" class="sense" hclass="sense" htag="li" id="call_sng_7" ox3000="y" sensenum="3"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a1"><span class="ox3ksym_a1"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[transitive]</span> <span class="def" hclass="def" htag="span">to ask somebody/something to come quickly to a particular place by phoning</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__28"> <span class="cf" hclass="cf" htag="span">call somebody/something</span> <span class="x">Someone <span class="cl">called the police</span>.</span></li><li class="" htag="li" id="OALD10_call__29"><span class="x">to call an ambulance/a doctor</span></li><li class="" htag="li" id="OALD10_call__30"> <span class="cf" hclass="cf" htag="span">call something for somebody</span> <span class="x">I'll call a taxi for you.</span></li><li class="" htag="li" id="OALD10_call__31"> <span class="cf" hclass="cf" htag="span">call somebody something</span> <span class="x">I'll call you a taxi.</span></li><li class="" htag="li" id="OALD10_call__32"> <span class="cf" hclass="cf" htag="span">call somebody/something to something</span> <span class="x">Fire crews were called to the building at 9 p.m.</span></li><li class="" htag="li" id="OALD10_call__33"><span class="x">The doctor has been called to an urgent case.</span></li></ul><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/phones-email-and-the-internet?level=a1" title="Topic phones-email-and-the-internet"><span class="topic" href="l2:communication:phones_email_and_the_internet?cefr=a1"><span class="topic_name">Phones, email and the internet</span><span class="topic_cefr">a1</span></span></a></span> </li></span><span class="shcut-g" id="call_sngs_3"> <h2 class="shcut" hclass="shcut" htag="h2" id="call_shcut_2">describe</h2><li cefr="b1" class="sense" hclass="sense" htag="li" id="call_sng_3" ox3000="y" sensenum="4"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=b1"><span class="ox3ksym_b1"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[transitive]</span> <span class="def" hclass="def" htag="span">to describe somebody/something in a particular way; to consider somebody/something to be something</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__34"> <span class="cf" hclass="cf" htag="span">call somebody/something + noun</span> <span class="x">I wouldn't call German an easy language.</span></li><li class="" htag="li" id="OALD10_call__35"><span class="x">Are you calling me a liar?</span></li><li class="" htag="li" id="OALD10_call__36"><span class="x">She now calls Southern California home <span class="gloss" hclass="gloss" htag="span">(= she lives there)</span>.</span></li><li class="" htag="li" id="OALD10_call__37"><span class="x">He was in the front room, or the lounge or whatever you want to call it.</span></li><li class="" htag="li" id="OALD10_call__38"><span class="x">I make it ten pounds forty-three you owe me. Let's call it ten pounds.</span></li><li class="" htag="li" id="OALD10_call__39"> <span class="cf" hclass="cf" htag="span">call yourself + noun</span> <span class="x">They call themselves ‘The Gang’.</span></li><li class="" htag="li" id="OALD10_call__40"> <span class="cf" hclass="cf" htag="span">call somebody/something + adj.</span> <span class="x">Would you call it blue or green?</span></li><li class="" htag="li" id="OALD10_call__41"><span class="x">You could hardly call the show perfect, but it was successful.</span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="call2_unbox_5" unbox="synonyms"><span class="box_title" onclick="toggle_active(this)">Synonyms <span class="closed">regard</span></span><span class="body"><span class="unbox">regard</span><ul class="inline"><li class="li">call</li><li class="li">find</li><li class="li">consider</li><li class="li">see</li><li class="li">view</li></ul><span class="p">These words all mean to think about somebody/​something in a particular way.</span><ul class="deflist"><li class="li"><span class="dt">regard</span> <span class="dd">to think of somebody/​something in a particular way: <span class="unx">He seemed to regard the whole thing as a joke.</span></span></li><li class="li"><span class="dt">call</span> <span class="dd">to say that somebody/​something has particular qualities or characteristics: <span class="unx">I wouldn’t call German an easy language.</span></span></li><li class="li"><span class="dt">find</span> <span class="dd">to have a particular feeling or opinion about something: <span class="unx">You may find your illness hard to accept.</span></span></li><li class="li"><span class="dt">consider</span> <span class="dd">to think of somebody/​something in a particular way: <span class="unx">Who do you consider (to be) responsible for the accident?</span></span></li></ul> <span class="un"><span class="h4">regard or consider?</span><span class="p">These two words have the same meaning, but they are used in different patterns and structures. In this meaning <span class="eb">consider</span> must be used with a complement or clause: you can <span class="ei">consider somebody/​something to be something</span> or <span class="ei">consider somebody/​something as something</span>, although very often the <span class="ei">to be</span> or <span class="ei">as</span> is left out: <span class="ei">He considers himself an expert.</span><span class="ei">They are considered a high-risk group.</span> You can also <span class="ei">consider that somebody/​something is something</span> and again, the <span class="ei">that</span> can be left out. <span class="eb">Regard</span> is used in a narrower range of structures. The most frequent structure is <span class="ei">regard somebody/​something as something</span>; the <span class="ei">as</span> cannot be left out: <span class="wx">I regard him a close friend.</span> You cannot <span class="wx">regard somebody/​something to be something</span> or <span class="wx">regard that somebody/​something is something.</span> However, <span class="eb">regard</span> (but not <span class="eb">consider</span> in this meaning) can also be used without a noun or adjective complement but with just an object and adverb <span class="ei">(somebody/​something is highly regarded)</span> or adverbial phrase (<span class="ei">regard somebody/​something with suspicion/​jealousy/​admiration)</span>.</span></span><ul class="deflist"><li class="li"><span class="dt">see</span> <span class="dd">to have an opinion of something: <span class="unx">Try to see things from her point of view.</span></span></li><li class="li"><span class="dt">view</span> <span class="dd">to think of somebody/​something in a particular way: <span class="unx">How do you view your position within the company?</span> <span class="un" un="note"><span class="eb">View</span> has the same meaning as <span class="eb">regard</span> and <span class="eb">consider</span> but is slightly less frequent and slightly less formal. The main structures are <span class="ei">view somebody/​something as somebody/​something</span> (you cannot leave out the <span class="ei">as</span>) and <span class="ei">view somebody/​something with something</span>.</span></span></li></ul><span class="patterns">Patterns</span><ul class="bullet"><li class="li">to regard/​consider/​see/​view somebody/​something <span class="eb">as</span> something </li><li class="li">to regard/​consider/​see/​view somebody/​something <span class="eb">from</span> a particular point of view</li><li class="li">to find/​consider somebody/​something <span class="eb">to be</span> something</li><li class="li"><span class="eb">generally/​usually</span> regarded/​considered/​seen/​viewed as something</li><li class="li">to regard/​consider/​view somebody/​something <span class="eb">favourably/​unfavourably</span></li></ul></span></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="call2_unbox_6" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adverb</span><ul class="collocs_list"><li class="li">commonly</li><li class="li">frequently</li><li class="li">formerly</li><li class="li">…</li></ul><span class="unbox">preposition</span><ul class="collocs_list"><li class="li">by</li></ul><span class="unbox">phrases</span><ul class="collocs_list"><li class="li">you could hardly call something…</li><li class="li">you would hardly call something…</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/call_2" title=" definition in Collocations">full entry</a></span></span></span></div> </li><li cefr="b1" class="sense" hclass="sense" htag="li" id="call_sng_4" ox3000="y" sensenum="5"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=b1"><span class="ox3ksym_b1"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[transitive]</span> <span class="cf" hclass="cf" htag="span">call yourself + noun</span> <span class="def" hclass="def" htag="span">to claim that you are a particular type of person, especially when other people question whether this is true</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__42"><span class="x">Call yourself a friend? So why won't you help me, then?</span></li><li class="" htag="li" id="OALD10_call__43"><span class="x">She's no right to call herself a feminist.</span></li></ul> </li></span><span class="shcut-g" id="call_sngs_4"> <h2 class="shcut" hclass="shcut" htag="h2" id="call_shcut_3">shout</h2><li cefr="b1" class="sense" hclass="sense" htag="li" id="call_sng_5" ox3000="y" sensenum="6"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=b1"><span class="ox3ksym_b1"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[intransitive, transitive]</span> <span class="def" hclass="def" htag="span">to shout or say something loudly to attract somebody’s attention</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__44"><span class="x">I thought I heard somebody calling.</span></li><li class="" htag="li" id="OALD10_call__45"> <span class="cf" hclass="cf" htag="span">call (out) to somebody (for something)</span> <span class="x">She called out to her father for help.</span></li><li class="" htag="li" id="OALD10_call__46"> <span class="cf" hclass="cf" htag="span">call (out) to somebody to do something</span> <span class="x">Someone called to him to wait.</span></li><li class="" htag="li" id="OALD10_call__47"> <span class="cf" hclass="cf" htag="span">call something</span> <span class="x">Did somebody call my name?</span></li><li class="" htag="li" id="OALD10_call__48"> <span class="cf" hclass="cf" htag="span">call out (something) </span><span class="x">People in the audience called out requests.</span></li><li class="" htag="li" id="OALD10_call__49"> <span class="cf" hclass="cf" htag="span">+ speech</span> <span class="x">‘See you later!’ she called.</span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="call2_unbox_7" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__50"><span class="unx">He called for help, but no one could hear.</span></li><li class="" htag="li" id="OALD10_call__51"><span class="unx">She called out in pain.</span></li><li class="" htag="li" id="OALD10_call__52"><span class="unx">‘Don't forget what I said!’ she called after him.</span></li><li class="" htag="li" id="OALD10_call__53"><span class="unx">He called out a warning from the kitchen.</span></li></ul></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="call2_unbox_8" unbox="synonyms"><span class="box_title" onclick="toggle_active(this)">Synonyms <span class="closed">call</span></span><span class="body"><span class="unbox">call</span><ul class="inline"><li class="li">cry out</li><li class="li">exclaim</li><li class="li">blurt</li><li class="li">burst out</li></ul><span class="p">These words all mean to shout or say something loudly or suddenly.</span><ul class="deflist"><li class="li"><span class="dt">call</span> <span class="dd">to shout or say something loudly to attract somebody’s attention:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__54"><span class="unx">I thought I heard someone calling.</span></li></ul></span></li><li class="li"><span class="dt">cry out (something)</span> <span class="dd">to shout something loudly, especially when you need help or are in trouble:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__55"><span class="unx">She cried out for help.</span></li><li class="" htag="li" id="OALD10_call__56"><span class="unx">I cried out his name.</span></li></ul></span></li><li class="li"><span class="dt">exclaim</span> <span class="dd">to say something suddenly and loudly, especially because of a strong emotion:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__57"><span class="unx">‘It isn’t fair!’ he exclaimed angrily.</span></li></ul></span></li><li class="li"><span class="dt">blurt</span> <span class="dd">to say something suddenly and without thinking carefully enough:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__58"><span class="unx">He <span class="eb">blurted out</span> the answer without thinking.</span></li></ul></span></li><li class="li"><span class="dt">burst out</span> <span class="dd">to say something suddenly and loudly, especially with a lot of emotion:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__59"><span class="unx">‘He’s a bully!’ the little boy burst out.</span></li></ul></span></li></ul><span class="patterns">Patterns</span><ul class="bullet"><li class="li">to call/​cry out/​exclaim/​blurt out (something) <span class="eb">to</span> somebody</li><li class="li">to call/​cry out <span class="eb">for</span> something</li><li class="li">to cry out/​exclaim <span class="eb">in/​with</span> something</li><li class="li">to call/​cry out/​exclaim/​blurt out/​burst out <span class="eb">suddenly</span></li><li class="li">to call/​cry out/​exclaim/​burst out <span class="eb">loudly</span></li></ul></span></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="call2_unbox_9" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adverb</span><ul class="collocs_list"><li class="li">softly</li><li class="li">loudly</li><li class="li">out</li><li class="li">…</li></ul><span class="unbox">preposition</span><ul class="collocs_list"><li class="li">for</li><li class="li">to</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/call_2" title=" definition in Collocations">full entry</a></span></span></span></div> </li><li cefr="b1" class="sense" hclass="sense" htag="li" id="call_sng_6" ox3000="y" sensenum="7"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=b1"><span class="ox3ksym_b1"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[transitive, intransitive]</span> <span class="def" hclass="def" htag="span">to ask somebody to come by shouting or speaking loudly</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__60"> <span class="cf" hclass="cf" htag="span">call somebody + adv./prep.</span> <span class="x">Will you call the kids in for lunch?</span></li><li class="" htag="li" id="OALD10_call__61"> <span class="cf" hclass="cf" htag="span">call (somebody)</span> <span class="x">He heard me call him and came to the door.</span></li><li class="" htag="li" id="OALD10_call__62"><span class="x">Did you call?</span></li></ul> <div class=""> <div class="am-default contentslot" id="ad_contentslot_2"></div></div> </li></span><span class="shcut-g" id="call_sngs_6"> <h2 class="shcut" hclass="shcut" htag="h2" id="call_shcut_5">order somebody to come</h2><li cefr="b1" class="sense" hclass="sense" htag="li" id="call_sng_9" ox3000="y" sensenum="8"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=b1"><span class="ox3ksym_b1"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[transitive, often passive]</span> <span class="labels" hclass="labels" htag="span">(formal)</span> <span class="def" hclass="def" htag="span">to order somebody to come to a place</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__63"> <span class="cf" hclass="cf" htag="span">be called + adv./prep.</span> <span class="x">Several candidates were called for a second interview.</span></li><li class="" htag="li" id="OALD10_call__64"><span class="x">The ambassador was called back to London by the prime minister.</span></li><li class="" htag="li" id="OALD10_call__65"><span class="x">He felt called to the priesthood <span class="gloss" hclass="gloss" htag="span">(= had a strong feeling that he must become a priest)</span>.</span></li><li class="" htag="li" id="OALD10_call__66"> <span class="cf" hclass="cf" htag="span">be called to do something</span> <span class="x">You may be called to give evidence.</span></li><li class="" htag="li" id="OALD10_call__67"><span class="x">She was called to testify before a Senate subcommittee.</span></li><li class="" htag="li" id="OALD10_call__68"> <span class="cf" hclass="cf" htag="span">call somebody</span> <span class="x">The defence says it plans to call four witnesses.</span></li></ul> </li></span><span class="shcut-g" id="call_sngs_8"> <h2 class="shcut" hclass="shcut" htag="h2" id="call_shcut_7">meeting/strike, etc.</h2><li cefr="b1" class="sense" hclass="sense" htag="li" id="call_sng_11" ox3000="y" sensenum="9"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=b1"><span class="ox3ksym_b1"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[transitive]</span> <span class="def" hclass="def" htag="span">to order something to happen; to announce that something will happen</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__69"> <span class="cf" hclass="cf" htag="span">call something</span> <span class="x">to <span class="cl">call a meeting/strike</span></span></li><li class="" htag="li" id="OALD10_call__70"> <span class="cf" hclass="cf" htag="span">call something for…</span> <span class="x">The prime minister has <span class="cl">called an election</span> for 8 June.</span></li></ul><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/working-life?level=b1" title="Topic working-life"><span class="topic" href="l2:work_and_business:working_life?cefr=b1"><span class="topic_name">Working life</span><span class="topic_cefr">b1</span></span></a></span> </li></span><span class="shcut-g" id="call_sngs_7"> <h2 class="shcut" hclass="shcut" htag="h2" id="call_shcut_6">visit</h2><li class="sense" hclass="sense" htag="li" id="call_sng_10" sensenum="10"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[intransitive]</span></span> <span class="labels" hclass="labels" htag="span">(especially British English)</span> <span class="def" hclass="def" htag="span">to make a short visit to a person or place</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__71"><span class="x">I'll call round and see you on my way home.</span></li><li class="" htag="li" id="OALD10_call__72"> <span class="cf" hclass="cf" htag="span">call on somebody</span> <span class="x">Let's call on John.</span></li><li class="" htag="li" id="OALD10_call__73"> <span class="cf" hclass="cf" htag="span">call to do something</span> <span class="x">He was out when I called to see him.</span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="call2_unbox_10" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__74"><span class="unx">We called but they were out.</span></li><li class="" htag="li" id="OALD10_call__75"><span class="unx">She said she was very lonely and it was kind of them to call on her.</span></li><li class="" htag="li" id="OALD10_call__76"><span class="unx">Could you call by later today?</span></li><li class="" htag="li" id="OALD10_call__77"><span class="unx">I called round at the house to check how things were.</span></li></ul></span></div> </li></span><span class="shcut-g" id="call_sngs_9"> <h2 class="shcut" hclass="shcut" htag="h2" id="call_shcut_8">of bird/animal</h2><li cefr="b2" class="sense" hclass="sense" htag="li" id="call_sng_12" sensenum="11"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[intransitive]</span></span> <span class="def" hclass="def" htag="span">to make the noise that is typical for it</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__78"><span class="x">The cuckoo called across the trees to its mate.</span></li></ul><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/birds?level=b2" title="Topic birds"><span class="topic" href="l2:animals:birds?cefr=b2"><span class="topic_name">Birds</span><span class="topic_cefr">b2</span></span></a></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="call2_unbox_11" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adverb</span><ul class="collocs_list"><li class="li">softly</li><li class="li">loudly</li><li class="li">out</li><li class="li">…</li></ul><span class="unbox">preposition</span><ul class="collocs_list"><li class="li">for</li><li class="li">to</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/call_2" title=" definition in Collocations">full entry</a></span></span></span></div> </li></span><span class="shcut-g" id="call_shcutg_1"> <h2 class="shcut" hclass="shcut" htag="h2" id="call_shcut_20">predict</h2><li class="sense" hclass="sense" htag="li" id="call_sng_66" new="y" sensenum="12"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to predict what the result of something will be</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__79"> <span class="cf" hclass="cf" htag="span">call something</span> <span class="x">It's a very open game that is very hard to call.</span></li><li class="" htag="li" id="OALD10_call__80"><span class="x">to be difficult/impossible to call</span></li><li class="" htag="li" id="OALD10_call__81"><span class="x">The result of this election is going to be <span class="cl">too close to call</span>.</span></li><li class="" htag="li" id="OALD10_call__82"> <span class="cf" hclass="cf" htag="span">call it for somebody</span> <span class="x">Most media pundits called it for the Conservatives. </span></li></ul> <div class=""> <div class="am-default contentslot" id="ad_contentslot_3"></div></div> </li></span><span class="shcut-g" id="call_sngs_10"><h2 class="shcut" hclass="shcut" htag="h2" id="call_shcut_9">in games</h2><li class="sense" hclass="sense" htag="li" id="call_sng_13" sensenum="13"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[transitive, intransitive]</span></span> <span class="cf" hclass="cf" htag="span">call (something)</span> <span class="def" hclass="def" htag="span">to say which side of a coin you think will face upwards after it is thrown</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__83"><span class="x">to <span class="cl">call heads/tails</span></span></li></ul> </li></span><span class="shcut-g" id="call_sngs_11"> <h2 class="shcut" hclass="shcut" htag="h2" id="call_shcut_10">in dances</h2><li class="sense" hclass="sense" htag="li" id="call_sng_14" sensenum="14"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[intransitive, transitive]</span></span> <span class="cf" hclass="cf" htag="span">call (something)</span> <span class="def" hclass="def" htag="span">to shout out the steps for people performing a <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/square-dance" title="square dance definition"><span class="ndv">square dance</span></a> or <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/country-dance" title="country dance definition"><span class="ndv">country dance</span></a></span> </li></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="call2_unbox_12" unbox="wordorigin"><span class="box_title" onclick="toggle_active(this)">Word Origin</span><span class="body"><span class="p">late Old English <span class="ei">ceallian</span>, from Old Norse <span class="ei">kalla</span> ‘summon loudly’.</span></span></span></div></ol><div class="idioms" hclass="idioms" htag="div" id="call_idmgs_1"><span class="idioms_heading">Idioms</span> <span class="idm-g" id="mind_idmg_44" sk="bringtomind"><div class="top-container"><div class="top-g" id="mind_topg_11"><div class="webtop"><span class="idm" id="mind_idm_9">bring/call somebody/something to mind</span> <span class="labels" hclass="labels" htag="span">(formal)</span></div></div></div><ol class="senses_multiple" htag="ol"><li class="sense" hclass="sense" htag="li" id="mind_sng_65" sensenum="1"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to remember somebody/something</span></span> <span class="xrefs" hclass="xrefs" htag="span" xt="syn"><span class="prefix">synonym</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/recall#recall_2" title="recall definition"><span bord="n" class="xr-g" href="recall_subentryg_2:recall_topg_3"><span class="xh">recall</span></span></a></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__84"><span class="x">She couldn't call to mind where she had seen him before.</span></li></ul> </li><li class="sense" hclass="sense" htag="li" id="mind_sng_66" sensenum="2"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to remind you of somebody/something</span></span> <span class="xrefs" hclass="xrefs" htag="span" xt="syn"><span class="prefix">synonym</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/recall#recall_2" title="recall definition"><span bord="n" class="xr-g" href="recall_subentryg_2:recall_topg_3"><span class="xh">recall</span></span></a></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__85"><span class="x">The painting brings to mind some of Picasso's early works.</span></li></ul> </li></ol><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="call_unbox_4" unbox="british_american"><span class="box_title" onclick="toggle_active(this)">British/American <span class="closed">phone / call / ring</span></span><span class="body"><span class="unbox">phone / call / ring</span><span class="unbox">Verbs</span><ul class="bullet"><li class="li">In <span class="labels" hclass="labels" htag="span">British English</span>, <span class="eb">to phone, to ring</span> and <span class="eb">to call</span> are the usual ways of saying <span class="eb">to telephone</span>. In <span class="labels" hclass="labels" htag="span">North American English</span> the most common word is <span class="eb">call</span>, but <span class="eb">phone</span> is also used. Speakers of <span class="labels" hclass="labels" htag="span">North American English</span> do not say <span class="eb">ring</span>. <span class="eb">Telephone</span> is very formal and is used mainly in <span class="labels" hclass="labels" htag="span">British English</span>.</li></ul><span class="unbox">Nouns</span><ul class="bullet"><li class="li">You can use <span class="eb">call</span> or <span class="eb">phone call</span> (more formal) in both <span class="labels" hclass="labels" htag="span">British English</span> and <span class="labels" hclass="labels" htag="span">North American English</span>:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__86"><span class="unx">Were there any phone calls for me?</span></li><li class="" htag="li" id="OALD10_call__87"><span class="unx">How do I make a local call?</span></li></ul> The idiom <span class="eb">give somebody a call</span> is also common:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__88"><span class="unx">I’ll give you a call tonight.</span></li></ul> In informal <span class="labels" hclass="labels" htag="span">British English</span> you could also say:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__89"><span class="unx">I’ll give you a ring tonight.</span></li></ul></li></ul></span></span></div></span> <span class="idm-g" id="call_idmg_1" sk="callbluff"><div class="top-container"><div class="top-g" id="call_topg_2"><div class="webtop"><span class="idm" id="call_idm_15">call somebody’s bluff</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="call_sng_15"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to tell somebody to do what they are threatening to do, because you believe that they will not be cruel or brave enough to do it</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__90"><span class="x">She was tempted to call his bluff, hardly able to believe he’d carry out his threat.</span></li></ul> <div class=""> <div class="am-default contentslot" id="ad_contentslot_4"></div></div> </li></ol></span> <span class="idm-g" id="call_idmg_2" sk="callintoplay"><div class="top-container"><div class="top-g" id="call_topg_5"><div class="webtop"><span class="idm" id="call_idm_2">call something into play</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="call_sng_16"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(formal)</span></span> <span class="def" hclass="def" htag="span">to make use of something</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__91"><span class="x">Chess is a game that calls into play all your powers of concentration.</span></li></ul> </li></ol></span> <span class="idm-g" id="call_idmg_3" sk="callintoquestion"><div class="top-container"><div class="top-g" id="call_topg_6"><div class="webtop"><span class="idm" id="call_idm_3">call something into question</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="call_sng_17"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to doubt something or make others doubt something</span></span> <span class="xrefs" hclass="xrefs" htag="span" xt="syn"><span class="prefix">synonym</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/question#question_2" title="question definition"><span bord="n" class="xr-g" href="question_subentryg_2:question_topg_10"><span class="xh">question</span></span></a></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__92"><span class="x">His honesty has never been called into question.</span></li></ul> </li></ol></span> <span class="idm-g" id="call_idmg_4" sk="callitday"><div class="top-container"><div class="top-g" id="call_topg_7"><div class="webtop"><span class="idm" id="call_idm_4">call it a day</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="call_sng_18"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">to decide or agree to stop doing something</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__93"><span class="x">After forty years in politics I think it's time for me to call it a day <span class="gloss" hclass="gloss" htag="span">(= to retire)</span>.</span></li></ul> </li></ol></span> <span class="idm-g" id="call_idmg_5" sk="callitquits"><div class="top-container"><div class="top-g" id="call_topg_8"><div class="webtop"><span class="idm" id="call_idm_5">call it quits</span> <span class="labels" hclass="labels" htag="span">(informal)</span></div></div></div><ol class="senses_multiple" htag="ol"><li cefr="c2" class="sense" hclass="sense" htag="li" id="call_sng_19" sensenum="1"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to agree to end a contest, argument, etc. because both sides seem equal</span></span><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/discussion-and-agreement?level=c2" title="Topic discussion-and-agreement"><span class="topic" href="l2:functions:discussion_and_agreement?cefr=c2"><span class="topic_name">Discussion and agreement</span><span class="topic_cefr">c2</span></span></a></span> </li><li class="sense" hclass="sense" htag="li" id="call_sng_20" sensenum="2"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to decide to stop doing something</span></span> <div class=""> <div class="am-default contentslot" id="ad_contentslot_5"></div></div> </li></ol></span> <span class="idm-g" id="call_idmg_6" sk="callnames"><div class="top-container"><div class="top-g" id="call_topg_9"><div class="webtop"><span class="idm" id="call_idm_6">call somebody names</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="call_sng_21"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to use offensive words about somebody</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__94"><span class="x">Stop calling me names!</span></li></ul> </li></ol></span> <span class="idm-g" id="call_idmg_7" sk="callshots"><div class="top-container"><div class="top-g" id="call_topg_10"><div class="webtop"><span class="idm" id="call_idm_7">call the shots/tune</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="call_sng_22"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">to be the person who controls a situation</span> </li></ol></span> <span class="idm-g" id="call_idmg_8" sk="callspadespade"><div class="top-container"><div class="top-g" id="call_topg_11"><div class="webtop"><span cefr="c2" class="idm" id="call_idm_8">call a spade a spade</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" fkcefr="c2" hclass="sense" htag="li" id="call_sng_23"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to say exactly what you think without trying to hide your opinion</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__95"><span class="x">I believe in calling a spade a spade.</span></li></ul><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/opinion-and-argument?level=c2" title="Topic opinion-and-argument"><span class="topic" href="l2:functions:opinion_and_argument?cefr=c2"><span class="topic_name">Opinion and argument</span><span class="topic_cefr">c2</span></span></a></span> </li></ol></span> <span bord="y" class="idm-g" id="call_idmg_9" sk="calltime"><div class="top-container"><div class="top-g" id="call_topg_12"><div class="webtop"><span class="idm" corpus_freq="5" corpus_src="unknown" id="call_idm_9">call time (on something)</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="call_sng_24"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(British English)</span></span> <span class="def" hclass="def" htag="span">to say or decide that it is time for something to finish</span> </li></ol></span> <span class="idm-g" id="call_idmg_10" sk="calltoaccount"><div class="top-container"><div class="top-g" id="call_topg_37"><div class="webtop"><span class="idm" id="call_idm_16">call somebody to account (for/over something)</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="call_sng_25"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to make somebody explain a mistake, etc. because they are responsible for it</span></span> <div class=""> <div class="am-default contentslot" id="ad_contentslot_6"></div></div> </li></ol></span> <span class="idm-g" id="call_idmg_11" sk="calltoorder"><div class="top-container"><div class="top-g" id="call_topg_14"><div class="webtop"><span class="idm" id="call_idm_11">call somebody/something to order</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="call_sng_26"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to ask people in a meeting to be quiet so that the meeting can start or continue</span></span> </li></ol></span> <span class="idm-g" id="order_idmg_20" new="y" sk="calltoorder"><div class="top-container"><div class="top-g" id="order_topg_16"><div class="webtop"><span class="idm" id="call_idm_17">call/bring somebody/something to order</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="order_sng_46"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to order somebody to obey the formal rules of a meeting; to start a formal meeting</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__96"><span class="x">The argument continued until the chairman called them both to order.</span></li><li class="" htag="li" id="OALD10_call__97"><span class="x">The president brought the meeting to order at 9.15 a.m. </span></li><li class="" htag="li" id="OALD10_call__98"><span class="x">As the meeting was brought to order, the councillor immediately stood and began to protest.</span></li></ul> </li></ol></span> <span class="idm-g" id="pay_idmg_18" sk="hewhopayspipercallstune"><div class="top-container"><div class="top-g" id="pay_topg_4"><div class="webtop"><span class="idm" id="pay_idm_2">he who pays the piper calls the tune</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="pay_sng_37"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(saying)</span></span> <span class="def" hclass="def" htag="span">the person who provides the money for something can also control how it is spent</span> </li></ol></span> <span class="idm-g" id="carpet_idmg_2" sk="oncarpet"><div class="top-container"><div class="top-g" id="carpet_topg_3"><div class="webtop"><span class="idm" id="carpet_idm_1">(be/get called) on the carpet</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="carpet_sng_8"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal, especially North American English)</span></span> <span class="def" hclass="def" htag="span">called to see somebody in authority because you have done something wrong</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__99"><span class="x">I got called on the carpet for being late.</span></li></ul> </li></ol></span> <span class="idm-g" id="call_idmg_17" sk="potcallingkettleblack"><div class="top-container"><div class="top-g" id="call_topg_38"><div class="webtop"><span class="idm" id="call_idm_18">the pot calling the kettle black</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="call_sng_27"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(saying, informal)</span></span> <span class="def" hclass="def" htag="span">used to say that you should not criticize somebody for a fault that you have yourself</span> </li></ol></span> <span class="idm-g" id="what_idmg_15" sk="whatdyoucallhim"><div class="top-container"><div class="top-g" id="what_topg_7"><div class="webtop"><span class="idm" id="what_idm_7">what-d’you-call-him/-her/-it/-them <span class="idmsep">|</span> what’s-his/-her/-its/-their-name</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="what_sng_22"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used instead of a name that you cannot remember</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__100"><span class="x">She's just gone out with old what-d'you-call-him.</span></li></ul> </li></ol></span></div><aside class="phrasal_verb_links" hclass="phrasal_verb_links" htag="aside" id="call_pvgs_1" xt="pvlist"><span class="unbox">Phrasal Verbs</span><ul class="pvrefs"><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/call-at#callat2_e" title="call at definition"><span bord="n" class="xr-g" href="callat2_e" id="call_xrg_4"><span class="xh">call at</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/call-away#callaway2_e" title="call away definition"><span bord="n" class="xr-g" href="callaway2_e" id="call_xrg_5"><span class="xh">call away</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/call-back#callback3_e" title="call back definition"><span bord="n" class="xr-g" href="callback3_e" id="call_xrg_6"><span class="xh">call back</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/call-for#callfor2_e" title="call for definition"><span bord="n" class="xr-g" href="callfor2_e" id="call_xrg_7"><span class="xh">call for</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/call-forth#callforth2_e" title="call forth definition"><span bord="n" class="xr-g" href="callforth2_e" id="call_xrg_8"><span class="xh">call forth</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/call-in#callin3_e" title="call in definition"><span bord="n" class="xr-g" href="callin3_e" id="call_xrg_9"><span class="xh">call in</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/call-off#calloff2_e" title="call off definition"><span bord="n" class="xr-g" href="calloff2_e" id="call_xrg_10"><span class="xh">call off</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/call-on#callon2_e" title="call on definition"><span bord="n" class="xr-g" href="callon2_e" id="call_xrg_11"><span class="xh">call on</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/call-out#callout3_e" title="call out definition"><span bord="n" class="xr-g" href="callout3_e" id="call_xrg_12"><span class="xh">call out</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/call-up#callup3_e" title="call up definition"><span bord="n" class="xr-g" href="callup3_e" id="call_xrg_13"><span class="xh">call up</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/call-upon#callupon2_e" title="call upon definition"><span bord="n" class="xr-g" href="callupon2_e" id="call_xrg_14"><span class="xh">call upon</span></span></a></li></ul></aside></div><div class="entry" hclass="entry" hlength="4" htag="section" id="call_2" idm_id="000008296" sk="call: :0" sum="4619"><div class="top-container"><div class="top-g" id="call_topg_31"><div class="webtop"><h1 class="headword" hclass="headword" htag="h1" id="call_h_3" ox3000="y">call</h1> <span class="pos" hclass="pos" htag="span">noun</span><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a1"><span class="ox3ksym_a1"> </span></a></div><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="call"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_call__gb_3').play();" style="cursor: pointer" title="call pronunciationEnglish" valign="top"></a><span class="phon">/kɔːl/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="call"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_call__us_1').play();" style="cursor: pointer" title="call pronunciationAmerican" valign="top"></a><span class="phon">/kɔːl/</span></div></span><span class="jumplinks"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/call#call_idmgs_2" title="Idioms definition"><span class="jumplink" href="call_idmgs_2" name="Idioms">Idioms</span></a> </span></div></div></div><ol class="senses_multiple" htag="ol"><span class="shcut-g" id="call_sngs_39"> <h2 class="shcut" hclass="shcut" htag="h2" id="call_shcut_11">on phone</h2><li cefr="a1" class="sense" hclass="sense" htag="li" id="call_sng_48" ox3000="y" sensenum="1"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a1"><span class="ox3ksym_a1"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[countable]</span> <div class="variants" hclass="variants" htag="div" type="vf">(also <span class="v-g"><span class="v">phone call</span></span>, <span class="v-g"><span class="labels" hclass="labels" htag="span">formal</span> <span class="v">telephone call</span></span>)</div> <span class="def" hclass="def" htag="span">the act of speaking to somebody on the phone</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__101"><span class="x">to <span class="cl">get/receive a call</span> from somebody</span></li><li class="" htag="li" id="OALD10_call__102"><span class="x">to <span class="cl">give somebody/make a call</span></span></li><li class="" htag="li" id="OALD10_call__103"><span class="x">I'll <span class="cl">take</span> <span class="gloss" hclass="gloss" htag="span">(= answer)</span> <span class="cl">the call</span> upstairs.</span></li><li class="" htag="li" id="OALD10_call__104"><span class="x">I left a message but he didn't <span class="cl">return my call</span>.</span></li><li class="" htag="li" id="OALD10_call__105"><span class="x">a local/long-distance call</span></li><li class="" htag="li" id="OALD10_call__106"><span class="x">An emergency call was made at 9 p.m.</span></li><li class="" htag="li" id="OALD10_call__107"><span class="x">They put out a distress call and were towed into harbour </span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="call4_unbox_1" unbox="british_american"><span class="box_title" onclick="toggle_active(this)">British/American <span class="closed">phone / call / ring</span></span><span class="body"><span class="unbox">phone / call / ring</span><span class="unbox">Verbs</span><ul class="bullet"><li class="li">In <span class="labels" hclass="labels" htag="span">British English</span>, <span class="eb">to phone, to ring</span> and <span class="eb">to call</span> are the usual ways of saying <span class="eb">to telephone</span>. In <span class="labels" hclass="labels" htag="span">North American English</span> the most common word is <span class="eb">call</span>, but <span class="eb">phone</span> is also used. Speakers of <span class="labels" hclass="labels" htag="span">North American English</span> do not say <span class="eb">ring</span>. <span class="eb">Telephone</span> is very formal and is used mainly in <span class="labels" hclass="labels" htag="span">British English</span>.</li></ul><span class="unbox">Nouns</span><ul class="bullet"><li class="li">You can use <span class="eb">call</span> or <span class="eb">phone call</span> (more formal) in both <span class="labels" hclass="labels" htag="span">British English</span> and <span class="labels" hclass="labels" htag="span">North American English</span>:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__108"><span class="unx">Were there any phone calls for me?</span></li><li class="" htag="li" id="OALD10_call__109"><span class="unx">How do I make a local call?</span></li></ul> The idiom <span class="eb">give somebody a call</span> is also common:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__110"><span class="unx">I’ll give you a call tonight.</span></li></ul> In informal <span class="labels" hclass="labels" htag="span">British English</span> you could also say:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__111"><span class="unx">I’ll give you a ring tonight.</span></li></ul></li></ul></span></span></div> <span class="xrefs" hclass="xrefs" htag="span" xt="see"><span class="prefix">see also</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/alarm-call" title="alarm call definition"><span bord="n" class="xr-g" href="alarmcall_e"><span class="xh">alarm call</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/blank-call" title="blank call definition"><span bord="n" class="xr-g" href="blankcall_e"><span class="xh">blank call</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/cold-call#cold-call_1" title="cold call definition"><span bord="n" class="xr-g" href="coldcall2_e"><span class="xw">cold call</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/conference-call" title="conference call definition"><span bord="y" class="xr-g" href="conferencecall_e"><span class="xh">conference call</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/courtesy-call" title="courtesy call definition"><span bord="n" class="xr-g" href="courtesycall_e"><span class="xh">courtesy call</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/nuisance-call" title="nuisance call definition"><span bord="n" class="xr-g" href="nuisancecall_e"><span class="xh">nuisance call</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/wake-up-call" title="wake-up call definition"><span bord="n" class="xr-g" href="wakeupcall_e"><span class="xh">wake-up call</span></span></a></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="call4_unbox_2" unbox="wordfinder"><span class="box_title" onclick="toggle_active(this)">Wordfinder</span><span class="body"><ul class="bullet"><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/area-code" title="area code definition"><span class="xref">area code</span></a></li><li class="li"><span class="xref" href="call_subentryg_2:call_topg_31" tofix="">call</span></li><li class="li"><span class="xref" href="dial_subentryg_2:dial_topg_3" tofix="">dial</span></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/engaged#engaged_sng_3" title="engaged definition"><span class="xref">engaged</span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/hold#hold_sng_22" title="hold definition"><span class="xref">hold</span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/line#line_sng_18" title="line definition"><span class="xref">line</span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/message#message_sng_1" title="message definition"><span class="xref">message</span></a></li><li class="li"><span class="xref" href="phone_unbox_1" tofix="">phone</span></li><li class="li"><span class="xref" href="ring_hm2_pvg_5:ring_topg_16" tofix="">ring off</span></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/voicemail" title="voicemail definition"><span class="xref">voicemail</span></a></li></ul></span></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="call4_unbox_3" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__112"><span class="unx">Give us a call to say when you have arrived.</span></li><li class="" htag="li" id="OALD10_call__113"><span class="unx">I'll just make a quick phone call.</span></li><li class="" htag="li" id="OALD10_call__114"><span class="unx">I made a call to a friend in London.</span></li><li class="" htag="li" id="OALD10_call__115"><span class="unx">Could you get off the phone? I'm expecting a call.</span></li><li class="" htag="li" id="OALD10_call__116"><span class="unx">Ask the receptionist to put your call through to my room.</span></li><li class="" htag="li" id="OALD10_call__117"><span class="unx">He received an urgent call and had to leave.</span></li><li class="" htag="li" id="OALD10_call__118"><span class="unx">He spent the whole day fielding calls from concerned parents.</span></li><li class="" htag="li" id="OALD10_call__119"><span class="unx">I ended the call as quickly as possible.</span></li><li class="" htag="li" id="OALD10_call__120"><span class="unx">She placed an anonymous call to the Dutch Embassy in Dublin.</span></li><li class="" htag="li" id="OALD10_call__121"><span class="unx">She was out and missed an important call.</span></li><li class="" htag="li" id="OALD10_call__122"><span class="unx">The police managed to trace the call.</span></li><li class="" htag="li" id="OALD10_call__123"><span class="unx">The screen display said ‘8 missed calls’.</span></li><li class="" htag="li" id="OALD10_call__124"><span class="unx">We always make follow-up calls to ensure customer satisfaction.</span></li><li class="" htag="li" id="OALD10_call__125"><span class="unx">We're not supposed to make personal calls from work.</span></li><li class="" htag="li" id="OALD10_call__126"><span class="unx">You had a call from Fred.</span></li><li class="" htag="li" id="OALD10_call__127"><span class="unx">I received an unexpected call from the police yesterday.</span></li><li class="" htag="li" id="OALD10_call__128"><span class="unx">Were there any calls for me while I was out?</span></li></ul></span></div><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/phones-email-and-the-internet?level=a1" title="Topic phones-email-and-the-internet"><span class="topic" href="l2:communication:phones_email_and_the_internet?cefr=a1"><span class="topic_name">Phones, email and the internet</span><span class="topic_cefr">a1</span></span></a></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="call4_unbox_4" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adjective</span><ul class="collocs_list"><li class="li">phone</li><li class="li">telephone</li><li class="li">long</li><li class="li">…</li></ul><span class="unbox">verb + call</span><ul class="collocs_list"><li class="li">give somebody</li><li class="li">make</li><li class="li">place</li><li class="li">…</li></ul><span class="unbox">call + noun</span><ul class="collocs_list"><li class="li">box</li><li class="li">centre/​center</li></ul><span class="unbox">preposition</span><ul class="collocs_list"><li class="li">call for</li><li class="li">call from</li><li class="li">call to</li><li class="li">…</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/call_1" title=" definition in Collocations">full entry</a></span></span></span></div> </li></span><span class="shcut-g" id="call_shcutg_2"> <h2 class="shcut" hclass="shcut" htag="h2" id="call_shcut_21">shout</h2><li cefr="b2" class="sense" hclass="sense" htag="li" id="call_sng_89" new="y" ox3000="y" sensenum="2"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=b2"><span class="ox3ksym_b2"> </span></a></div></span> <span class="cf" hclass="cf" htag="span">call (for something)</span> <span class="def" hclass="def" htag="span">a shout or loud sound made to attract attention</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__129"><span class="x">a call for help</span></li></ul> <div class=""> <div class="am-default contentslot" id="ad_contentslot_1"></div></div> </li></span><span class="shcut-g" id="call_sngs_40"> <h2 class="shcut" hclass="shcut" htag="h2" id="call_shcut_12">of bird/animal</h2><li cefr="b2" class="sense" hclass="sense" htag="li" id="call_sng_49" sensenum="3"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[countable]</span></span> <span class="def" hclass="def" htag="span">a loud sound made by a bird or an animal</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__130"><span class="x">the distinctive call of the cuckoo</span></li><li class="" htag="li" id="OALD10_call__131"><span class="x">The call of the barn owl is often described as a screech or scream.</span></li></ul><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/birds?level=b2" title="Topic birds"><span class="topic" href="l2:animals:birds?cefr=b2"><span class="topic_name">Birds</span><span class="topic_cefr">b2</span></span></a></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="call4_unbox_5" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adjective</span><ul class="collocs_list"><li class="li">loud</li><li class="li">distinctive</li><li class="li">alarm</li><li class="li">…</li></ul><span class="unbox">verb + call</span><ul class="collocs_list"><li class="li">let out</li><li class="li">make</li><li class="li">sound</li><li class="li">…</li></ul><span class="unbox">preposition</span><ul class="collocs_list"><li class="li">call for</li><li class="li">call to</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/call_1" title=" definition in Collocations">full entry</a></span></span></span></div> </li></span><span class="shcut-g" id="call_sngs_42"> <h2 class="shcut" hclass="shcut" htag="h2" id="call_shcut_14">request/demand</h2><li cefr="b2" class="sense" hclass="sense" htag="li" id="call_sng_51" ox3000="y" sensenum="4"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=b2"><span class="ox3ksym_b2"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[countable]</span> <span class="def" hclass="def" htag="span">a request, an order or a demand for somebody to do something or to go somewhere</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__132"><span class="x">The government has resisted the calls of the international community.</span></li><li class="" htag="li" id="OALD10_call__133"> <span class="cf" hclass="cf" htag="span">call for something</span> <span class="x">calls for national unity</span></li><li class="" htag="li" id="OALD10_call__134"><span class="x">This is the last call for passengers travelling on British Airways flight 199 to Rome.</span></li><li class="" htag="li" id="OALD10_call__135"> <span class="cf" hclass="cf" htag="span">call for somebody to do something</span> <span class="x">calls for the minister to resign</span></li><li class="" htag="li" id="OALD10_call__136"> <span class="cf" hclass="cf" htag="span">call to something</span> <span class="labels" hclass="labels" htag="span">(formal)</span> <span class="x">The book is a call to action.</span></li><li class="" htag="li" id="OALD10_call__137"><span class="x">The mosque was sounding the call to prayer.</span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="call4_unbox_6" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__138"><span class="unx">Campaigners have renewed their call for an independent inquiry.</span></li><li class="" htag="li" id="OALD10_call__139"><span class="unx">The charity issued a call for donations to assist victims of the earthquake.</span></li><li class="" htag="li" id="OALD10_call__140"><span class="unx">It is unlikely that they will heed calls for a crackdown.</span></li><li class="" htag="li" id="OALD10_call__141"><span class="unx">This election is a clarion call for our country to face the challenges of the new era.</span></li></ul></span></div> <span class="xrefs" hclass="xrefs" htag="span" xt="see"><span class="prefix">see also</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/cattle-call" title="cattle call definition"><span bord="n" class="xr-g" href="cattlecall_e"><span class="xh">cattle call</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/clarion-call" title="clarion call definition"><span bord="y" class="xr-g" href="clarioncall_e"><span class="xh">clarion call</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/curtain-call" title="curtain call definition"><span bord="n" class="xr-g" href="curtaincall_e"><span class="xh">curtain call</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/roll-call" title="roll call definition"><span bord="n" class="xr-g" href="rollcall_e"><span class="xh">roll call</span></span></a></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="call4_unbox_7" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adjective</span><ul class="collocs_list"><li class="li">renewed</li><li class="li">repeated</li><li class="li">last</li><li class="li">…</li></ul><span class="unbox">verb + call</span><ul class="collocs_list"><li class="li">issue</li><li class="li">renew</li><li class="li">repeat</li><li class="li">…</li></ul><span class="unbox">call + noun</span><ul class="collocs_list"><li class="li">button</li></ul><span class="unbox">preposition</span><ul class="collocs_list"><li class="li">call for</li></ul><span class="unbox">phrases</span><ul class="collocs_list"><li class="li">a call to action</li><li class="li">a call to arms</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/call_1" title=" definition in Collocations">full entry</a></span></span></span></div> </li><li class="sense" hclass="sense" htag="li" id="call_sng_52" sensenum="5"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[uncountable]</span></span> <span class="def" hclass="def" htag="span">no demand for something; no reason for somebody’s behaviour</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__142"> <span class="cf" hclass="cf" htag="span">no call for something</span> <span class="x">There isn't a lot of call for small specialist shops nowadays.</span></li><li class="" htag="li" id="OALD10_call__143"> <span class="cf" hclass="cf" htag="span">no call (for somebody) to do something</span> <span class="x">There was no call for you to go upsetting everyone like that.</span></li></ul> </li><li bord="y" class="sense" hclass="sense" htag="li" id="call_sng_53" sensenum="6"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[countable]</span></span> <span class="cf" hclass="cf" htag="span">call on somebody/something</span> <span class="def" hclass="def" htag="span">a demand or pressure placed on somebody/something</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__144"><span class="x">She is a busy woman with many calls on her time.</span></li></ul> </li></span><span class="shcut-g" id="call_sngs_41"> <h2 class="shcut" hclass="shcut" htag="h2" id="call_shcut_13">visit</h2><li class="sense" hclass="sense" htag="li" id="call_sng_50" sensenum="7"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[countable]</span></span> <span class="def" hclass="def" htag="span">a short visit to somebody’s house</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__145"><span class="x">The doctor has five calls to make this morning.</span></li><li class="" htag="li" id="OALD10_call__146"><span class="x">I'm afraid this isn't a social call.</span></li><li class="" htag="li" id="OALD10_call__147"> <span class="cf" hclass="cf" htag="span">call on somebody</span> <span class="labels" hclass="labels" htag="span">(old-fashioned)</span> <span class="x"><span class="cl">to pay a call</span> on an old friend</span></li><li class="" htag="li" id="OALD10_call__148"> <span class="cf" hclass="cf" htag="span">on a call</span> <span class="x">The doctor is out on a call.</span></li></ul> <span class="xrefs" hclass="xrefs" htag="span" xt="see"><span class="prefix">see also</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/courtesy-call" title="courtesy call definition"><span bord="n" class="xr-g" href="courtesycall_e"><span class="xh">courtesy call</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/house-call" title="house call definition"><span bord="n" class="xr-g" href="housecall_e"><span class="xh">house call</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/port-of-call" title="port of call definition"><span bord="n" class="xr-g" href="portofcall_e"><span class="xh">port of call</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/will-call" title="will-call definition"><span bord="n" class="xr-g" href="willcall_e"><span class="xh">will-call</span></span></a></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="call4_unbox_8" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adjective</span><ul class="collocs_list"><li class="li">business</li><li class="li">courtesy</li><li class="li">sales</li><li class="li">…</li></ul><span class="unbox">verb + call</span><ul class="collocs_list"><li class="li">make</li><li class="li">pay (somebody)</li></ul><span class="unbox">preposition</span><ul class="collocs_list"><li class="li">on a call</li><li class="li">call on</li></ul><span class="unbox">phrases</span><ul class="collocs_list"><li class="li">first port of call</li><li class="li">last port of call</li><li class="li">next port of call</li><li class="li">…</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/call_1" title=" definition in Collocations">full entry</a></span></span></span></div> <div class=""> <div class="am-default contentslot" id="ad_contentslot_2"></div></div> </li></span><span class="shcut-g" id="call_sngs_43"> <h2 class="shcut" hclass="shcut" htag="h2" id="call_shcut_15">of a place</h2><li class="sense" hclass="sense" htag="li" id="call_sng_54" sensenum="8"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[singular]</span></span> <span class="cf" hclass="cf" htag="span">call (of something)</span> <span class="labels" hclass="labels" htag="span">(literary)</span> <span class="def" hclass="def" htag="span">a strong feeling of attraction that a particular place has for you</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__149"><span class="x">the call of the sea/your homeland</span></li></ul> </li></span><span class="shcut-g" id="call_sngs_44"> <h2 class="shcut" hclass="shcut" htag="h2" id="call_shcut_16">to a particular job</h2><li class="sense" hclass="sense" htag="li" id="call_sng_55" sensenum="9"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[singular]</span></span> <span class="cf" hclass="cf" htag="span">call (to something)</span> <span class="def" hclass="def" htag="span">a strong feeling that you want to do something, especially a particular job</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__150"><span class="x">He felt the call to the priesthood early on in his life.</span></li></ul> </li></span><span class="shcut-g" id="call_sngs_45"> <h2 class="shcut" hclass="shcut" htag="h2" id="call_shcut_17">decision</h2><li cefr="c2" class="sense" hclass="sense" htag="li" id="call_sng_56" sensenum="10"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[countable]</span></span> <span class="labels" hclass="labels" htag="span">(informal)</span> <span class="def" hclass="def" htag="span">a decision</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__151"><span class="x">It's your call!</span></li><li class="" htag="li" id="OALD10_call__152"><span class="x">a <span class="cl">good/bad call</span></span></li><li class="" htag="li" id="OALD10_call__153"><span class="x">That's a tough call.</span></li></ul> <span class="xrefs" hclass="xrefs" htag="span" xt="see"><span class="prefix">see also</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/judgement-call" title="judgement call definition"><span bord="n" class="xr-g" href="judgementcall_e"><span class="xh">judgement call</span></span></a></span><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/preferences-and-decisions?level=c2" title="Topic preferences-and-decisions"><span class="topic" href="l2:functions:preferences_and_decisions?cefr=c2"><span class="topic_name">Preferences and decisions</span><span class="topic_cefr">c2</span></span></a></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="call4_unbox_9" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adjective</span><ul class="collocs_list"><li class="li">tough</li><li class="li">close</li><li class="li">judgement/​judgment</li><li class="li">…</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/call_1" title=" definition in Collocations">full entry</a></span></span></span></div> </li></span><span class="shcut-g" id="call_sngs_46"> <h2 class="shcut" hclass="shcut" htag="h2" id="call_shcut_18">in tennis</h2><li bord="y" cefr="c2" class="sense" hclass="sense" htag="li" id="call_sng_57" sensenum="11"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[countable]</span></span> <span class="def" hclass="def" htag="span">a decision made by the <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/umpire#umpire_1" title="umpire definition"><span class="ndv">umpire</span></a></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__154"><span class="x">There was a disputed call in the second set.</span></li></ul><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/preferences-and-decisions?level=c2" title="Topic preferences-and-decisions"><span class="topic" href="l2:functions:preferences_and_decisions?cefr=c2"><span class="topic_name">Preferences and decisions</span><span class="topic_cefr">c2</span></span></a></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="call4_unbox_10" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adjective</span><ul class="collocs_list"><li class="li">tough</li><li class="li">close</li><li class="li">judgement/​judgment</li><li class="li">…</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/call_1" title=" definition in Collocations">full entry</a></span></span></span></div> </li></span><span class="shcut-g" id="call_sngs_47"> <h2 class="shcut" hclass="shcut" htag="h2" id="call_shcut_19">in card games</h2><li bord="y" class="sense" hclass="sense" htag="li" id="call_sng_58" sensenum="12"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[countable]</span></span> <span class="def" hclass="def" htag="span">a player’s <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/bid#bid1_1" title="bid definition"><span class="ndv">bid</span></a> or turn to <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/bid#bid1_1" title="bid definition"><span class="ndv">bid</span></a></span> <div class=""> <div class="am-default contentslot" id="ad_contentslot_3"></div></div> </li></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" dup="fky" id="call_unbox_18" unbox="wordorigin"><span class="box_title" onclick="toggle_active(this)">Word Origin</span><span class="body"><span class="p">late Old English <span class="ei">ceallian</span>, from Old Norse <span class="ei">kalla</span> ‘summon loudly’.</span></span></span></div></ol><div class="idioms" hclass="idioms" htag="div" id="call_idmgs_2"><span class="idioms_heading">Idioms</span> <span class="idm-g" id="call_idmg_18" sk="atbeckandcall"><div class="top-container"><div class="top-g" id="call_topg_39"><div class="webtop"><span class="idm" id="call_idm_19">at somebody’s beck and call</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="call_sng_28"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">always ready to obey somebody’s orders</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__155"><span class="x">She is constantly at the beck and call of her invalid father.</span></li><li class="" htag="li" id="OALD10_call__156"><span class="x">Don't expect to have me at your beck and call.</span></li></ul> </li></ol></span> <span class="idm-g" id="call_idmg_19" new="y" sk="beyondcallofduty"><div class="top-container"><div class="top-g" id="call_topg_35"><div class="webtop"><span class="idm" id="call_idm_20">(above and) beyond the call of duty</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="call_sng_90"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to a higher standard or level than is required for a job or task</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__157"><span class="x">She went above and beyond the call of duty in her efforts to save his life.</span></li></ul> </li></ol></span> <span class="idm-g" id="call_idmg_12" sk="callofnature"><div class="top-container"><div class="top-g" id="call_topg_32"><div class="webtop"><span class="idm" id="call_idm_12">the call of nature</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="call_sng_59"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(humorous)</span></span> <span class="def" hclass="def" htag="span">the need to go to the toilet</span> </li></ol></span> <span class="idm-g" id="call_idmg_21" sk="calltoarms"><div class="top-container"><div class="top-g" id="call_topg_40"><div class="webtop"><span class="idm" id="call_idm_21">a call to arms</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="call_sng_29"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">a strong request to fight in the army; a strong request to defend something or get ready for a fight about something</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__158"><span class="x">A leading environmentalist has issued a call to arms to companies and the public to do more about plastic waste.</span></li><li class="" htag="li" id="OALD10_call__159"><span class="x">The president's speech was a call to arms to restore the vitality of the American dream.</span></li></ul> </li></ol></span> <span class="idm-g" id="close2_idmg_3" sk="closecall"><div class="top-container"><div class="top-g" id="close_topg_21"><div class="webtop"><span class="idm" id="close_idm_7">a close call/shave</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="close2_sng_21"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">a situation in which you only just manage to avoid an accident, etc.</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__160"><span class="x">He had a close shave when his brakes failed on the mountain road.</span></li></ul> <div class=""> <div class="am-default contentslot" id="ad_contentslot_4"></div></div> </li></ol></span> <span class="idm-g" id="call_idmg_13" sk="havefirstcall"><div class="top-container"><div class="top-g" id="call_topg_33"><div class="webtop"><span class="idm" id="call_idm_13">have first call (on somebody/something)</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="call_sng_60"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to be the most important person or thing competing for somebody’s time, money, etc. and to be dealt with or paid for before other people or things</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__161"><span class="x">The children always have first call on her time.</span></li></ul> </li></ol></span> <span class="idm-g" id="call_idmg_14" sk="oncall"><div class="top-container"><div class="top-g" id="call_topg_34"><div class="webtop"><span class="idm" id="call_idm_14">(be) on call</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="call_sng_61"><span class="sensetop" hclass="sensetop" htag="span"><span class="dis-g"><span class="wrap">(</span><span class="dtxt">of a doctor, police officer, etc.</span><span class="wrap">)</span></span></span> <span class="def" hclass="def" htag="span">available for work if necessary, especially in an emergency</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_call__162"><span class="x">I'll be on call the night of the party.</span></li><li class="" htag="li" id="OALD10_call__163"><span class="x">The doctor had been on call for 48 hours and was exhausted.</span></li></ul> <span class="xrefs" hclass="xrefs" htag="span" xt="see"><span class="prefix">see also</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/on-call" title="on-call definition"><span bord="n" class="xr-g" href="oncall_e"><span class="xh">on-call</span></span></a></span> </li></ol></span></div></div><div bord="y" class="entry" hclass="entry" hlength="4" htag="section" id="call_3" idm_id="000008297" sk="call: :10" sum="198"><div class="top-container"><div class="top-g" id="call_topg_1"><div class="webtop"><h1 class="headword" hclass="headword" htag="h1" id="call_h_1">CALL</h1> <span class="pos" hclass="pos" htag="span">abbreviation</span><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="call"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_call__gb_3').play();" style="cursor: pointer" title="call pronunciationEnglish" valign="top"></a><span class="phon">/kɔːl/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="call"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_call__us_1').play();" style="cursor: pointer" title="call pronunciationAmerican" valign="top"></a><span class="phon">/kɔːl/</span></div></span></div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="call_sng_1"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span"><div class="variants" hclass="variants" htag="div" type="ff"><span class="v-g"><span class="v">computer assisted language learning</span></span></div></span></span> <span class="xrefs" hclass="xrefs" htag="span" xt="cp"><span class="prefix">compare</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/cal" title="CAL definition"><span bord="y" class="xr-g" href="cal_e"><span class="xh">CAL</span></span></a></span></li></ol></div></div></body></html><audio id="OALD10_pron_call__gb_3" preload="none" src="OALD10_pron_call__gb_3.mp3"></audio><audio id="OALD10_pron_call__us_1" preload="none" src="OALD10_pron_call__us_1.mp3"></audio><audio id="OALD10_pron_calls__gb_1" preload="none" src="OALD10_pron_calls__gb_1.mp3"></audio><audio id="OALD10_pron_calls__us_1" preload="none" src="OALD10_pron_calls__us_1.mp3"></audio><audio id="OALD10_pron_called__gb_1" preload="none" src="OALD10_pron_called__gb_1.mp3"></audio><audio id="OALD10_pron_called__us_1" preload="none" src="OALD10_pron_called__us_1.mp3"></audio><audio id="OALD10_pron_calling__gb_2" preload="none" src="OALD10_pron_calling__gb_2.mp3"></audio><audio id="OALD10_pron_calling__us_2" preload="none" src="OALD10_pron_calling__us_2.mp3"></audio>`;
