var custom_content = `<meta charset='utf-8'/><html><head></head><body><div class="oald" id="entryContent"><div class="entry" hclass="entry" hlength="3" htag="section" id="say_1" idm_id="000051754" sk="say: :0" sum="15043"><div class="top-container"><div class="top-g" id="say_topg_2"><div class="webtop"><h1 class="headword" hclass="headword" htag="h1" id="say_h_1" ox3000="y" random="y">say</h1> <span class="pos" hclass="pos" htag="span">verb</span><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a1"><span class="ox3ksym_a1"> </span></a></div><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="say"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_say__gb_1').play();" style="cursor: pointer" title="say pronunciationEnglish" valign="top"></a><span class="phon">/seɪ/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="say"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_say__us_1').play();" style="cursor: pointer" title="say pronunciationAmerican" valign="top"></a><span class="phon">/seɪ/</span></div></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="say3_unbox_1" unbox="verbforms"><span class="box_title" onclick="toggle_active(this)">Verb Forms</span><span class="body"><table class="verb_forms_table"><tbody><tr class="verb_form" form="root"><td class="verb_form"> <span class="vf_prefix">present simple I / you / we / they</span> say</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="say"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_say__gb_1').play();" style="cursor: pointer" title="say pronunciationEnglish" valign="top"></a><span class="phon">/seɪ/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="say"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_say__us_1').play();" style="cursor: pointer" title="say pronunciationAmerican" valign="top"></a><span class="phon">/seɪ/</span></div></span></td></tr><tr class="verb_form" form="thirdps"><td class="verb_form"> <span class="vf_prefix">he / she / it</span> says</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="says"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_says__gb_1').play();" style="cursor: pointer" title="says pronunciationEnglish" valign="top"></a><span class="phon">/sez/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="says"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_says__us_1').play();" style="cursor: pointer" title="says pronunciationAmerican" valign="top"></a><span class="phon">/sez/</span></div></span></td></tr><tr class="verb_form" form="past"><td class="verb_form"> <span class="vf_prefix">past simple</span> said</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="said"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_said__gb_1').play();" style="cursor: pointer" title="said pronunciationEnglish" valign="top"></a><span class="phon">/sed/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="said"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_said__us_1').play();" style="cursor: pointer" title="said pronunciationAmerican" valign="top"></a><span class="phon">/sed/</span></div></span></td></tr><tr class="verb_form" form="pastpart"><td class="verb_form"> <span class="vf_prefix">past participle</span> said</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="said"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_said__gb_1').play();" style="cursor: pointer" title="said pronunciationEnglish" valign="top"></a><span class="phon">/sed/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="said"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_said__us_1').play();" style="cursor: pointer" title="said pronunciationAmerican" valign="top"></a><span class="phon">/sed/</span></div></span></td></tr><tr class="verb_form" form="prespart"><td class="verb_form"> <span class="vf_prefix">-ing form</span> saying</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="saying"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_saying__gb_2').play();" style="cursor: pointer" title="saying pronunciationEnglish" valign="top"></a><span class="phon">/ˈseɪɪŋ/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="saying"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_saying__us_2').play();" style="cursor: pointer" title="saying pronunciationAmerican" valign="top"></a><span class="phon">/ˈseɪɪŋ/</span></div></span></td></tr></tbody></table></span></span></div><span class="jumplinks"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/say#say_idmgs_1" title="Idioms definition"><span class="jumplink" href="say_idmgs_1" name="Idioms">Idioms</span></a> </span></div></div></div><ol class="senses_multiple" htag="ol"><span class="shcut-g" id="say_sngs_1"> <h2 class="shcut" hclass="shcut" htag="h2" id="say_shcut_1">speak</h2><li cefr="a1" class="sense" hclass="sense" htag="li" id="say_sng_1" ox3000="y" sensenum="1"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a1"><span class="ox3ksym_a1"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[intransitive, transitive]</span> <span class="def" hclass="def" htag="span">to speak or tell somebody something, using words</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__1"> <span class="cf" hclass="cf" htag="span">+ speech</span> <span class="x">‘Hello!’ she said.</span></li><li class="" htag="li" id="OALD10_say__2"><span class="x">‘That was marvellous,’ said Daniel.</span></li><li class="" htag="li" id="OALD10_say__3"><span class="x">A legendary football coach <span class="cl">once said</span>, ‘Winning is everything.’ </span></li></ul><span class="un" un="help">In stories the subject often comes after <span class="eb">said</span>, <span class="eb">says</span> or <span class="eb">say</span> when it follows the actual words spoken, unless it is a pronoun.</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__4"> <span class="cf" hclass="cf" htag="span">say something</span> <span class="x">Be quiet, I have something to say.</span></li><li class="" htag="li" id="OALD10_say__5"><span class="x">Come and say hello.</span></li><li class="" htag="li" id="OALD10_say__6"><span class="x">Please say yes!</span></li><li class="" htag="li" id="OALD10_say__7"><span class="x">I didn't believe a word she said.</span></li><li class="" htag="li" id="OALD10_say__8"><span class="x">That's a terrible <span class="cl">thing to say</span>.</span></li><li class="" htag="li" id="OALD10_say__9"> <span class="cf" hclass="cf" htag="span">say something to somebody</span> <span class="x">She said nothing to me about it.</span></li><li class="" htag="li" id="OALD10_say__10"> <span class="cf" hclass="cf" htag="span">say to somebody/yourself + speech</span> <span class="x">I <span class="cl">said to myself</span> <span class="gloss" hclass="gloss" htag="span">(= thought)</span>, ‘That can't be right!’</span></li><li class="" htag="li" id="OALD10_say__11"> <span class="cf" hclass="cf" htag="span">say (that)…</span> <span class="x">He said (that) his name was Sam.</span></li><li class="" htag="li" id="OALD10_say__12"><span class="x">Can I <span class="cl">just say</span> I'm not happy about this.</span></li><li class="" htag="li" id="OALD10_say__13"><span class="x"><span class="cl">I have to say</span> I was pleasantly surprised.</span></li><li class="" htag="li" id="OALD10_say__14"> <span class="cf" hclass="cf" htag="span">it is said that…</span> <span class="x">It is said that she lived to be over 100.</span></li><li class="" htag="li" id="OALD10_say__15"> <span class="cf" hclass="cf" htag="span">say what, how, etc…</span> <span class="x">She finds it hard to say what she feels.</span></li><li class="" htag="li" id="OALD10_say__16"><span class="x">‘That's impossible!’ ‘So you say <span class="gloss" hclass="gloss" htag="span">(= but I think you may be wrong)</span>.’</span></li><li class="" htag="li" id="OALD10_say__17"><span class="x">‘Why can't I go out now?’ ‘Because I <span class="cl">say so</span>.’</span></li><li class="" htag="li" id="OALD10_say__18"><span class="x">‘What do you want it for?’ ‘I'd rather not say.’</span></li><li class="" htag="li" id="OALD10_say__19"> <span class="cf" hclass="cf" htag="span">say to do something</span> <span class="x">He said to meet him here.</span></li><li class="" htag="li" id="OALD10_say__20"> <span class="cf" hclass="cf" htag="span">be said to be/have something</span> <span class="x">He is said to have been a brilliant scholar.</span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="say3_unbox_2" unbox="express"><span class="box_title" onclick="toggle_active(this)">Express Yourself <span class="closed">Interrupting</span></span><span class="body"><span class="unbox">Interrupting</span><span class="p">You may need to say something when somebody else is speaking, or you may be chairing a discussion where you have to stop one person talking too much. If you start talking at the same time as someone else, this will seem rude. To interrupt politely, you can say, for example:</span><ul class="bullet"><li class="li"><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__21"><span class="unx"><span class="ebi">Sorry to interrupt,</span> but I have to disagree with that.</span></li></ul></li><li class="li"><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__22"><span class="unx"><span class="ebi">Could I just say something here?</span></span></li></ul></li><li class="li"><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__23"><span class="unx"><span class="ebi">If I could, let me stop you there for a moment</span> and go back to your previous point.</span></li></ul></li><li class="li"><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__24"><span class="unx"><span class="ebi">Actually, we seem to have strayed a bit from the topic. Can we go back to</span> the first point?</span></li></ul></li><li class="li"><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__25"><span class="unx"><span class="ebi">Just a moment,</span> Sue. Can we hear what Jack has to say on this?</span></li></ul></li><li class="li"><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__26"><span class="unx"><span class="ebi">May I interrupt you there?</span> I don't think that's true.</span></li></ul> <span class="labels" hclass="labels" htag="span">(formal)</span></li><li class="li"><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__27"><span class="unx"><span class="ebi">I’m sorry, but we’re running short on time.</span> Can you please summarize very quickly so we can finish up?</span></li></ul> <span class="labels" hclass="labels" htag="span">(formal)</span></li><li class="li"><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__28"><span class="unx"><span class="ebi">I appreciate your enthusiasm on this topic, but I’m afraid we have a couple more people to hear from.</span></span></li></ul> <span class="labels" hclass="labels" htag="span">(formal)</span></li><li class="li"><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__29"><span class="unx"><span class="ebi">Could you two please discuss that issue privately after the meeting? We have several more items to cover and need to move on at this point.</span></span></li></ul> <span class="labels" hclass="labels" htag="span">(formal)</span></li><li class="li"><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__30"><span class="unx"><span class="ebi">I’m sorry, I really have to stop you</span> there. We've run out of time.</span></li></ul> <span class="labels" hclass="labels" htag="span">(formal)</span></li><li class="li"><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__31"><span class="unx"><span class="ebi">Let’s save that conversation for another time.</span></span></li></ul></li></ul></span></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="say3_unbox_3" unbox="which_word"><span class="box_title" onclick="toggle_active(this)">Which Word? <span class="closed">say / tell</span></span><span class="body"><span class="unbox">say / tell</span><ul class="bullet"><li class="li"><span class="eb">Say</span> never has a person as the object. You <span class="eb">say something</span> or <span class="eb">say something to somebody</span>. <span class="eb">Say</span> is often used when you are giving somebody’s exact words:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__32"><span class="unx">‘Sit down,’ she said.</span></li><li class="" htag="li" id="OALD10_say__33"><span class="unx">Anne said, ‘I’m tired.’</span></li><li class="" htag="li" id="OALD10_say__34"><span class="unx">Anne said (that) she was tired.</span></li><li class="" htag="li" id="OALD10_say__35"><span class="unx">What did he say to you?</span></li></ul> You cannot use ‘say about’, but <span class="eb">say something about</span> is correct:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__36"><span class="unx">I want to say something/​a few words/​a little about my family.</span></li></ul> <span class="eb">Say</span> can also be used with a clause when the person you are talking to is not mentioned:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__37"><span class="unx">She didn’t say what she intended to do.</span></li></ul></li><li class="li"><span class="eb">Tell</span> usually has a person as the object and often has two objects:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__38"><span class="unx">Have you told him the news yet?</span></li></ul> It is often used with ‘that’ clauses:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__39"><span class="unx">Anne told me (that) she was tired.</span></li></ul> <span class="eb">Tell</span> is usually used when somebody is giving facts or information, often with <span class="ei">what</span>, <span class="ei">where</span>, etc:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__40"><span class="unx">Can you tell me when the movie starts?</span></li></ul> (BUT:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__41"><span class="unx">Can you give me some information about the school?</span></li></ul>) <span class="eb">Tell</span> is also used when you are giving somebody instructions:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__42"><span class="unx">The doctor told me to stay in bed.</span></li><li class="" htag="li" id="OALD10_say__43"><span class="unx">The doctor told me (that) I had to stay in bed.</span></li></ul> OR<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__44"><span class="unx">The doctor said (that) I had to stay in bed.</span></li></ul> NOT<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li"><span class="wx">The doctor said me to stay in bed.</span></li></ul></li></ul></span></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="say3_unbox_4" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__45"><span class="unx">‘I am home, ’ he said simply.</span></li><li class="" htag="li" id="OALD10_say__46"><span class="unx">‘I don't know,’ she said crossly.</span></li><li class="" htag="li" id="OALD10_say__47"><span class="unx">‘There's nothing wrong with him, ’ she said airily.</span></li><li class="" htag="li" id="OALD10_say__48"><span class="unx">‘Well, at least we tried!’ he said with a shrug.</span></li><li class="" htag="li" id="OALD10_say__49"><span class="unx">‘You'll see!’ Lianne said with a smile.</span></li><li class="" htag="li" id="OALD10_say__50"><span class="unx">A government spokesman was quoted as saying that they would take steps to restore order.</span></li><li class="" htag="li" id="OALD10_say__51"><span class="unx">Can you honestly say you're sorry?</span></li><li class="" htag="li" id="OALD10_say__52"><span class="unx">I dared not say a word about it to anyone.</span></li><li class="" htag="li" id="OALD10_say__53"><span class="unx">I heard him say they were leaving tomorrow.</span></li><li class="" htag="li" id="OALD10_say__54"><span class="unx">I've forgotten what I was going to say.</span></li></ul></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="say3_unbox_5" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adverb</span><ul class="collocs_list"><li class="li">aloud</li><li class="li">out loud</li><li class="li">loudly</li><li class="li">…</li></ul><span class="unbox">verb + say</span><ul class="collocs_list"><li class="li">be about to</li><li class="li">be going to</li><li class="li">hasten to</li><li class="li">…</li></ul><span class="unbox">preposition</span><ul class="collocs_list"><li class="li">about</li><li class="li">to</li></ul><span class="unbox">phrases</span><ul class="collocs_list"><li class="li">be quoted as saying something</li><li class="li">a thing to say</li><li class="li">I have to say</li><li class="li">…</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/say" title=" definition in Collocations">full entry</a></span></span></span></div> </li></span><span class="shcut-g" id="say_sngs_2"> <h2 class="shcut" hclass="shcut" htag="h2" id="say_shcut_2">repeat words</h2><li cefr="a1" class="sense" hclass="sense" htag="li" id="say_sng_2" ox3000="y" sensenum="2"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a1"><span class="ox3ksym_a1"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[transitive]</span> <span class="cf" hclass="cf" htag="span">say something</span> <span class="def" hclass="def" htag="span">to repeat words, phrases, etc.</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__55"><span class="x">to say a prayer</span></li><li class="" htag="li" id="OALD10_say__56"><span class="x">Try to say that line with more conviction.</span></li></ul> <div class=""> <div class="am-default contentslot" id="ad_contentslot_1"></div></div> </li></span><span class="shcut-g" id="say_sngs_6"> <h2 class="shcut" hclass="shcut" htag="h2" id="say_shcut_6">give written information</h2><li cefr="a2" class="sense" hclass="sense" htag="li" id="say_sng_6" ox3000="y" sensenum="3"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a2"><span class="ox3ksym_a2"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[transitive, no passive]</span> <span class="dis-g"><span class="wrap">(</span><span class="dtxt">of something that is written or can be seen</span><span class="wrap">)</span></span> <span class="def" hclass="def" htag="span">to give particular information or instructions</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__57"> <span class="cf" hclass="cf" htag="span">+ speech</span> <span class="x">The notice said ‘Keep Out’.</span></li><li class="" htag="li" id="OALD10_say__58"> <span class="cf" hclass="cf" htag="span">say something</span> <span class="x">The clock said three o'clock.</span></li><li class="" htag="li" id="OALD10_say__59"> <span class="cf" hclass="cf" htag="span">say (that)…</span> <span class="x">The instructions say (that) we should leave it to set for four hours.</span></li><li class="" htag="li" id="OALD10_say__60"> <span class="cf" hclass="cf" htag="span">say where, why, etc…</span> <span class="x">The book doesn't say where he was born.</span></li><li class="" htag="li" id="OALD10_say__61"> <span class="cf" hclass="cf" htag="span">say to do something</span> <span class="x">The guidebook says to turn left.</span></li></ul> </li></span><span class="shcut-g" id="say_sngs_3"> <h2 class="shcut" hclass="shcut" htag="h2" id="say_shcut_3">express opinion</h2><li cefr="b1" class="sense" hclass="sense" htag="li" id="say_sng_3" ox3000="y" sensenum="4"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=b1"><span class="ox3ksym_b1"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[transitive, intransitive]</span> <span class="def" hclass="def" htag="span">to express an opinion on something</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__62"> <span class="cf" hclass="cf" htag="span">say something</span> <span class="x"><span class="cl">Say what you like</span> <span class="gloss" hclass="gloss" htag="span">(= although you disagree)</span> about her, she's a fine singer.</span></li><li class="" htag="li" id="OALD10_say__63"><span class="x"><span class="cl">I'll say this for them</span>, they're a very efficient company.</span></li><li class="" htag="li" id="OALD10_say__64"><span class="x">Anna thinks I'm lazy—<span class="cl">what do you say</span> <span class="gloss" hclass="gloss" htag="span">(= what is your opinion)</span>?</span></li><li class="" htag="li" id="OALD10_say__65"> <span class="cf" hclass="cf" htag="span">say (that)…</span> <span class="x"><span class="cl">I can't say</span> I blame her for resigning <span class="gloss" hclass="gloss" htag="span">(= I think she was right)</span>.</span></li><li class="" htag="li" id="OALD10_say__66"><span class="x"><span class="cl">I have to say</span> I enjoyed every minute.</span></li><li class="" htag="li" id="OALD10_say__67"><span class="x"><span class="cl">I say</span> <span class="gloss" hclass="gloss" htag="span">(= suggest)</span> we go without them.</span></li><li class="" htag="li" id="OALD10_say__68"><span class="x"><span class="cl">I wouldn't say</span> they were rich <span class="gloss" hclass="gloss" htag="span">(= in my opinion they are not rich)</span>.</span></li><li class="" htag="li" id="OALD10_say__69"><span class="x"><span class="cl">That's not to say</span> it's a bad movie <span class="gloss" hclass="gloss" htag="span">(= it is good but it is not without faults)</span>.</span></li><li class="" htag="li" id="OALD10_say__70"> <span class="cf" hclass="cf" htag="span">say what, how, etc…</span> <span class="x"><span class="cl">It's hard to say</span> what caused the accident.</span></li><li class="" htag="li" id="OALD10_say__71"><span class="x"><span class="cl">It's difficult to say</span> whether the story is true.</span></li><li class="" htag="li" id="OALD10_say__72"><span class="x">‘When will it be finished?’ ‘<span class="cl">I couldn't say</span> <span class="gloss" hclass="gloss" htag="span">(= I don't know)</span>.’</span></li><li class="" htag="li" id="OALD10_say__73"><span class="x">Is it worth it? I would say not.</span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="say3_unbox_6" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__74"><span class="unx">Do you have anything to say about this?</span></li><li class="" htag="li" id="OALD10_say__75"><span class="unx">I wouldn't like to say what the impact might be on my business.</span></li><li class="" htag="li" id="OALD10_say__76"><span class="unx">She wasn't at her best, it has to be said.</span></li><li class="" htag="li" id="OALD10_say__77"><span class="unx">It's difficult to say exactly where it will land.</span></li><li class="" htag="li" id="OALD10_say__78"><span class="unx">I hate to say it, but I think Stephen may be right.</span></li><li class="" htag="li" id="OALD10_say__79"><span class="unx">I have to say I didn't expect it to be so good.</span></li><li class="" htag="li" id="OALD10_say__80"><span class="unx">It is fair to say a considerable amount of effort went into the project.</span></li></ul></span></div><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/opinion-and-argument?level=b1" title="Topic opinion-and-argument"><span class="topic" href="l2:functions:opinion_and_argument?cefr=b1"><span class="topic_name">Opinion and argument</span><span class="topic_cefr">b1</span></span></a></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="say3_unbox_7" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adverb</span><ul class="collocs_list"><li class="li">aloud</li><li class="li">out loud</li><li class="li">loudly</li><li class="li">…</li></ul><span class="unbox">verb + say</span><ul class="collocs_list"><li class="li">be about to</li><li class="li">be going to</li><li class="li">hasten to</li><li class="li">…</li></ul><span class="unbox">preposition</span><ul class="collocs_list"><li class="li">about</li><li class="li">to</li></ul><span class="unbox">phrases</span><ul class="collocs_list"><li class="li">be quoted as saying something</li><li class="li">a thing to say</li><li class="li">I have to say</li><li class="li">…</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/say" title=" definition in Collocations">full entry</a></span></span></span></div> </li></span><span class="shcut-g" id="say_sngs_5"> <h2 class="shcut" hclass="shcut" htag="h2" id="say_shcut_5">show thoughts/feelings</h2><li cefr="b1" class="sense" hclass="sense" htag="li" id="say_sng_5" ox3000="y" sensenum="5"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=b1"><span class="ox3ksym_b1"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[transitive]</span> <span class="def" hclass="def" htag="span">to make thoughts, feelings, etc. clear to somebody by using words, looks, movements, etc.</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__81"> <span class="cf" hclass="cf" htag="span">say something</span> <span class="x">His angry glance <span class="cl">said it all</span>.</span></li><li class="" htag="li" id="OALD10_say__82"><span class="x">Just what is the artist trying to say in her work?</span></li><li class="" htag="li" id="OALD10_say__83"> <span class="cf" hclass="cf" htag="span">say something about somebody/something</span> <span class="x">The museum's architects have managed to say something about our national story. </span></li></ul> </li></span><span class="shcut-g" id="say_shcutg_1"><h2 class="shcut" hclass="shcut" htag="h2" id="say_shcut_7">show what somebody/something is like</h2><li cefr="b1" class="sense" hclass="sense" htag="li" id="say_sng_69" new="y" ox3000="y" sensenum="6"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=b1"><span class="ox3ksym_b1"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[transitive]</span> <span class="def" hclass="def" htag="span">to show, sometimes indirectly, what somebody/something is like</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__84"> <span class="cf" hclass="cf" htag="span">say something</span> <span class="x">That <span class="cl">says it all</span> really, doesn't it? <span class="gloss" hclass="gloss" htag="span">(= it shows clearly what is true)</span></span></li><li class="" htag="li" id="OALD10_say__85"> <span class="cf" hclass="cf" htag="span">say something about somebody/something</span> <span class="x">The incident says an awful lot about his character.</span></li><li class="" htag="li" id="OALD10_say__86"> <span class="cf" hclass="cf" htag="span">say something to somebody</span> <span class="x">This says to me that there is more here than you are letting on.</span></li></ul> </li></span><span class="shcut-g" id="say_sngs_4"> <h2 class="shcut" hclass="shcut" htag="h2" id="say_shcut_4">give example</h2><li cefr="b2" class="sense" hclass="sense" htag="li" id="say_sng_4" ox3000="y" sensenum="7"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=b2"><span class="ox3ksym_b2"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[transitive, no passive]</span> <span class="def" hclass="def" htag="span">to suggest or give something as an example or a possibility</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__87"> <span class="cf" hclass="cf" htag="span">say something/somebody</span> <span class="x">You could learn the basics in, <span class="cl">let's say</span>, three months.</span></li><li class="" htag="li" id="OALD10_say__88"><span class="x">Let’s take any writer, say <span class="gloss" hclass="gloss" htag="span">(= for example)</span> Dickens…</span></li><li class="" htag="li" id="OALD10_say__89"> <span class="cf" hclass="cf" htag="span">say (that)…</span> <span class="x">Say you lose your job: what would you do then?</span></li><li class="" htag="li" id="OALD10_say__90"><span class="x">Just say he refuses to talk to you.</span></li></ul><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/suggestions-and-advice?level=b2" title="Topic suggestions-and-advice"><span class="topic" href="l2:functions:suggestions_and_advice?cefr=b2"><span class="topic_name">Suggestions and advice</span><span class="topic_cefr">b2</span></span></a></span> <div class=""> <div class="am-default contentslot" id="ad_contentslot_2"></div></div> </li></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="say_unbox_3" unbox="wordorigin"><span class="box_title" onclick="toggle_active(this)">Word Origin</span><span class="body"><span class="p">Old English <span class="ei">secgan</span>, of Germanic origin; related to Dutch <span class="ei">zeggen</span> and German <span class="ei">sagen</span>.</span></span></span></div></ol><div class="idioms" hclass="idioms" htag="div" id="say_idmgs_1"><span class="idioms_heading">Idioms</span> <span class="idm-g" id="say_idmg_10" sk="beeasiersaidthandone"><div class="top-container"><div class="top-g" id="say_topg_1"><div class="webtop"><span class="idm" id="say_idm_41">be easier said than done</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_17"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(saying)</span></span> <span class="def" hclass="def" htag="span">to be much more difficult to do than to talk about</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__91"><span class="x">‘Why don't you get yourself a job?’ ‘That's easier said than done.’</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_1" sk="beforeyoucansayjackrobinson"><div class="top-container"><div class="top-g" id="say_topg_3"><div class="webtop"><span class="idm" id="say_idm_1">before you can say Jack Robinson</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_7"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(old-fashioned)</span></span> <span class="def" hclass="def" htag="span">very quickly; in a very short time</span> </li></ol></span> <span class="idm-g" id="say_idmg_11" sk="enoughsaid"><div class="top-container"><div class="top-g" id="say_topg_46"><div class="webtop"><span class="idm" id="say_idm_42">enough said</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_18"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used to say that you understand a situation and there is no need to say any more</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__92"><span class="x">‘He's a politician, remember.’ ‘Enough said.’</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_2" sk="gowithoutsaying"><div class="top-container"><div class="top-g" id="say_topg_4"><div class="webtop"><span class="idm" id="say_idm_2">go without saying</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_8"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to be very obvious or easy to predict</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__93"><span class="x">Of course I'll help you. <span class="cl">That goes without saying</span>.</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_3" sk="havesomethingnothingtosayforyourself"><div class="top-container"><div class="top-g" id="say_topg_5"><div class="webtop"><span class="idm" id="say_idm_3">have something, nothing, etc. to say for yourself</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_9"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to be ready, unwilling, etc. to talk or give your views on something</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__94"><span class="x">She doesn't have much to say for herself <span class="gloss" hclass="gloss" htag="span">(= doesn't take part in conversation)</span>.</span></li><li class="" htag="li" id="OALD10_say__95"><span class="x">He had plenty to say for himself <span class="gloss" hclass="gloss" htag="span">(= he had a lot of opinions and was willing to talk)</span>.</span></li><li class="" htag="li" id="OALD10_say__96"><span class="x">Late again—what have you got to say for yourself <span class="gloss" hclass="gloss" htag="span">(= what is your excuse)</span>?</span></li></ul> <div class=""> <div class="am-default contentslot" id="ad_contentslot_3"></div></div> </li></ol></span> <span class="idm-g" id="say_idmg_58" new="y" sk="havesomethingtosayabout"><div class="top-container"><div class="top-g" id="say_topg_45"><div class="webtop"><span class="idm" id="say_idm_43">have something to say (to somebody) about something</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_70"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to be angry</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__97"><span class="x">He knew that if he wasn't back by midnight, his parents would have something to say about it.</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_4" sk="havingsaidthat"><div class="top-container"><div class="top-g" id="say_topg_6"><div class="webtop"><span cefr="c1" class="idm" id="say_idm_4">having said that <span class="idmsep">|</span> that said</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" fkcefr="c1" hclass="sense" htag="li" id="say_sng_10"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">used to introduce an opinion that makes what you have just said seem less strong</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__98"><span class="x">I sometimes get worried in this job. Having said that, I enjoy doing it, it's a challenge.</span></li></ul><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/opinion-and-argument?level=c1" title="Topic opinion-and-argument"><span class="topic" href="l2:functions:opinion_and_argument?cefr=c1"><span class="topic_name">Opinion and argument</span><span class="topic_cefr">c1</span></span></a></span> </li></ol></span> <span class="idm-g" id="say_idmg_12" sk="idaresay"><div class="top-container"><div class="top-g" id="say_topg_47"><div class="webtop"><span cefr="c2" class="idm" id="say_idm_45">I dare say</span> <div class="variants" hclass="variants" htag="div" id="say_vgs_2" type="vs">(also <span class="v-g" id="say_vg_2"><span class="v" id="say_v_2">I daresay</span> <span class="labels" hclass="labels" htag="span">especially in British English</span></span>)</div></div></div></div><ol class="sense_single" htag="ol"><li class="sense" fkcefr="c2" hclass="sense" htag="li" id="say_sng_19"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used when you are saying that something is likely</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__99"><span class="x">I dare say you know about it already.</span></li></ul><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/doubt-guessing-and-certainty?level=c2" title="Topic doubt-guessing-and-certainty"><span class="topic" href="l2:functions:doubt_guessing_and_certainty?cefr=c2"><span class="topic_name">Doubt, guessing and certainty</span><span class="topic_cefr">c2</span></span></a></span> </li></ol></span> <span class="idm-g" id="say_idmg_13" sk="ifyoudontmindmesayingso"><div class="top-container"><div class="top-g" id="say_topg_48"><div class="webtop"><span class="idm" id="say_idm_46">if you don’t mind me/my saying so…</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_20"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used when you are going to criticize somebody or say something that might upset them</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__100"><span class="x">That colour doesn't really suit you, if you don't mind my saying so.</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_18" sk="ihearwhatyouresaying"><div class="top-container"><div class="top-g" id="say_topg_49"><div class="webtop"><span class="idm" id="say_idm_47">I hear what you're saying</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_25"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used to tell somebody that you have listened to their opinion, especially when you are going to express a different opinion</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__101"><span class="x">I hear what you're saying, but you're wrong.</span></li></ul> <div class=""> <div class="am-default contentslot" id="ad_contentslot_4"></div></div> </li></ol></span> <span class="idm-g" id="say_idmg_5" sk="illsay"><div class="top-container"><div class="top-g" id="say_topg_7"><div class="webtop"><span class="idm" id="say_idm_5">I’ll say!</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_11"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(old-fashioned, informal)</span></span> <span class="def" hclass="def" htag="span">used for emphasis to say ‘yes’</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__102"><span class="x">‘Does she see him often?’ ‘I'll say! Nearly every day.’</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_26" sk="imgladtosay"><div class="top-container"><div class="top-g" id="say_topg_50"><div class="webtop"><span class="idm" id="say_idm_48">I’m glad to say (that…)</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_33"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">used when you are commenting on a situation and saying that you are happy about it</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__103"><span class="x">Most teachers, I'm glad to say, take their jobs very seriously.</span></li></ul> </li></ol></span> <span class="idm-g" id="sorry_idmg_6" sk="imsorrytosay"><div class="top-container"><div class="top-g" id="sorry_topg_6"><div class="webtop"><span class="idm" id="sorry_idm_4">I’m sorry to say</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="sorry_sng_15"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used for saying that something is disappointing</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__104"><span class="x">He didn't accept the job, I'm sorry to say.</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_6" sk="imustsay"><div class="top-container"><div class="top-g" id="say_topg_8"><div class="webtop"><span cefr="c1" class="idm" id="say_idm_6">I must say</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" fkcefr="c1" hclass="sense" htag="li" id="say_sng_12"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">used to emphasize an opinion</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__105"><span class="x">Well, I must say, that's the funniest thing I've heard all week.</span></li></ul><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/opinion-and-argument?level=c1" title="Topic opinion-and-argument"><span class="topic" href="l2:functions:opinion_and_argument?cefr=c1"><span class="topic_name">Opinion and argument</span><span class="topic_cefr">c1</span></span></a></span> </li></ol></span> <span class="idm-g" id="say_idmg_7" sk="isay"><div class="top-container"><div class="top-g" id="say_topg_9"><div class="webtop"><span class="idm" id="say_idm_7">I say</span> <span class="labels" hclass="labels" htag="span">(British English, old-fashioned, informal)</span></div></div></div><ol class="senses_multiple" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_13" sensenum="1"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used to express surprise, shock, etc.</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__106"><span class="x">I say! What a huge cake!</span></li></ul> <div class=""> <div class="am-default contentslot" id="ad_contentslot_5"></div></div> </li><li class="sense" hclass="sense" htag="li" id="say_sng_14" sensenum="2"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used to attract somebody’s attention or introduce a new subject of conversation</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__107"><span class="x">I say, can you lend me five pounds?</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_8" sk="itsayslotverylittlefor"><div class="top-container"><div class="top-g" id="say_topg_10"><div class="webtop"><span class="idm" id="say_idm_8">it says a lot, very little, etc. for somebody/something</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_15"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">it shows a good/bad quality that somebody/something has</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__108"><span class="x">It says a lot for her that she never lost her temper.</span></li><li class="" htag="li" id="OALD10_say__109"><span class="x">It didn't say much for their efficiency that the order arrived a week late.</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_9" sk="iwouldntsayno"><div class="top-container"><div class="top-g" id="say_topg_11"><div class="webtop"><span class="idm" id="say_idm_9">I wouldn’t say no (to something)</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_16"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">used to say that you would like something or to accept something that is offered</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__110"><span class="x">I wouldn't say no to a pizza.</span></li><li class="" htag="li" id="OALD10_say__111"><span class="x">‘Tea, Brian?’ ‘I wouldn't say no.’</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_37" sk="leastsaidsoonestmended"><div class="top-container"><div class="top-g" id="say_topg_51"><div class="webtop"><span class="idm" id="say_idm_49">least said soonest mended</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_44"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(British English, saying)</span></span> <span class="def" hclass="def" htag="span">a bad situation will pass or be forgotten most quickly if nothing more is said about it</span> </li></ol></span> <span class="idm-g" id="say_idmg_41" sk="lesssaidbetter"><div class="top-container"><div class="top-g" id="say_topg_52"><div class="webtop"><span class="idm" id="say_idm_50">the less/least said the better</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_52"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">the best thing to do is say as little as possible about something</span></span> <div class=""> <div class="am-default contentslot" id="ad_contentslot_6"></div></div> </li></ol></span> <span class="idm-g" id="say_idmg_42" new="y" sk="letsjustsay"><div class="top-container"><div class="top-g" id="say_topg_53"><div class="webtop"><span class="idm" id="say_idm_51">let's just say</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_53"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used when commenting on a situation to suggest that you could say something more or worse but prefer not to</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__112"><span class="x">Let's just say he wasn't very happy about the decision <span class="gloss" hclass="gloss" htag="span">(= he was very unhappy or angry about it)</span>.</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_43" sk="letussay"><div class="top-container"><div class="top-g" id="say_topg_54"><div class="webtop"><span class="idm" id="say_idm_52">let us say</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_54"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used when making a suggestion or giving an example</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__113"><span class="x">I can let you have it for, well let's say £100.</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_44" sk="meantosay"><div class="top-container"><div class="top-g" id="say_topg_55"><div class="webtop"><span class="idm" id="say_idm_53">mean to say</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_55"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used to emphasize what you are saying or to ask somebody if they really mean what they say</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__114"><span class="x">I mean to say, you should have known how he would react!</span></li><li class="" htag="li" id="OALD10_say__115"><span class="x">Do you mean to say you've lost it?</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_45" sk="needlesstosay"><div class="top-container"><div class="top-g" id="say_topg_56"><div class="webtop"><span class="idm" id="say_idm_54">needless to say</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_56"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used to emphasize that the information you are giving is obvious</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__116"><span class="x">The problem, needless to say, is the cost involved.</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_48" sk="neversaydie"><div class="top-container"><div class="top-g" id="say_topg_57"><div class="webtop"><span class="idm" id="say_idm_55">never say die</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_59"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(saying)</span></span> <span class="def" hclass="def" htag="span">do not stop hoping</span> </li></ol></span> <span class="idm-g" id="say_idmg_49" sk="nosoonersaidthandone"><div class="top-container"><div class="top-g" id="say_topg_58"><div class="webtop"><span class="idm" id="say_idm_56">no sooner said than done</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_60"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used to say that something was, or will be, done immediately</span></span> </li></ol></span> <span class="idm-g" id="word_idmg_36" sk="nothavegoodwordtosayfor"><div class="top-container"><div class="top-g" id="word_topg_14"><div class="webtop"><span class="idm" id="word_idm_12">not have a good word to say for somebody/something</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="word_sng_43"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">to never say anything good about somebody/something</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__117"><span class="x">Nobody had a good word to say about him.</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_50" sk="notsaybootogoose"><div class="top-container"><div class="top-g" id="say_topg_59"><div class="webtop"><span class="idm" id="say_idm_57">not say boo to a goose</span> <span class="labels" hclass="labels" htag="span">(British English)</span> <div class="variants" hclass="variants" htag="div" id="say_vgs_3" type="vf">(<span class="v-g" id="say_vg_3"><span class="labels" hclass="labels" htag="span">North American English</span> <span class="v" id="say_v_3">not say boo to anyone</span></span>)</div></div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_61"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to be very shy or gentle</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__118"><span class="x">He's so nervous he wouldn't say boo to a goose.</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_14" sk="nottosay"><div class="top-container"><div class="top-g" id="say_topg_16"><div class="webtop"><span class="idm" id="say_idm_14">not to say</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_21"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used to introduce a stronger way of describing something</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__119"><span class="x">a difficult, not to say impossible, task</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_15" sk="saycheese"><div class="top-container"><div class="top-g" id="say_topg_17"><div class="webtop"><span class="idm" id="say_idm_15">say cheese</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_22"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used to ask somebody to smile before you take their photograph</span></span> </li></ol></span> <span class="idm-g" id="say_idmg_52" sk="saymouthful"><div class="top-container"><div class="top-g" id="say_topg_60"><div class="webtop"><span class="idm" id="say_idm_58">say a mouthful</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_63"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(North American English, informal)</span></span> <span class="def" hclass="def" htag="span">to say something important</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__120"><span class="x">You said a mouthful there, buddy.</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_16" sk="sayno"><div class="top-container"><div class="top-g" id="say_topg_18"><div class="webtop"><span class="idm" id="say_idm_16">say no (to something)</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_23"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to refuse an offer, a suggestion, etc.</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__121"><span class="x">If you don't invest in this, you're saying no to a potential fortune.</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_17" sk="saynomore"><div class="top-container"><div class="top-g" id="say_topg_19"><div class="webtop"><span class="idm" id="say_idm_17">say no more</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_24"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">used to say that you understand exactly what somebody means or is trying to say, so it is unnecessary to say anything more</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__122"><span class="x">‘They went to Paris together.’ ‘Say no more!’</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_54" sk="saypiece"><div class="top-container"><div class="top-g" id="say_topg_61"><div class="webtop"><span class="idm" id="say_idm_59">say your piece</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_65"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to say exactly what you feel or think</span></span> </li></ol></span> <span class="idm-g" id="say_idmg_19" sk="saywhat"><div class="top-container"><div class="top-g" id="say_topg_21"><div class="webtop"><span class="idm" id="say_idm_19">say what?</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_26"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(North American English, informal)</span></span> <span class="def" hclass="def" htag="span">used to express surprise at what somebody has just said</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__123"><span class="x">‘He's getting married.’ ‘Say what?’</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_20" sk="saywhen"><div class="top-container"><div class="top-g" id="say_topg_22"><div class="webtop"><span class="idm" id="say_idm_20">say when</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_27"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used to ask somebody to tell you when you should stop pouring a drink or serving food for them because they have enough</span></span> </li></ol></span> <span class="idm-g" id="word_idmg_42" sk="sayword"><div class="top-container"><div class="top-g" id="word_topg_17"><div class="webtop"><span class="idm" id="word_idm_15">say/give the word</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="word_sng_49"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to give an order; to make a request</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__124"><span class="x">Just say the word, and I'll go.</span></li></ul> </li></ol></span> <span class="idm-g" id="suffice_idmg_2" sk="sufficetosay"><div class="top-container"><div class="top-g" id="suffice_topg_2"><div class="webtop"><span class="idm" id="suffice_idm_1">suffice (it) to say (that)…</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="suffice_sng_3"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used to suggest that although you could say more, what you do say will be enough to explain what you mean</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__125"><span class="x">I won’t go into all the details. Suffice it to say that the whole event was a complete disaster.</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_21" sk="thatistosay"><div class="top-container"><div class="top-g" id="say_topg_23"><div class="webtop"><span class="idm" id="say_idm_21">that is to say</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_28"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">in other words</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__126"><span class="x">three days from now, that is to say on Friday</span></li></ul> <span class="xrefs" hclass="xrefs" htag="span" xt="see"><span class="prefix">see also</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/i-e" title="i.e. definition"><span bord="n" class="xr-g" href="ie_e"><span class="xh">i.e.</span></span></a></span> </li></ol></span> <span class="idm-g" id="say_idmg_23" sk="thatsaid"><div class="top-container"><div class="top-g" id="say_topg_25"><div class="webtop"><span cefr="c1" class="idm" id="say_idm_23">that said <span class="idmsep">|</span> having said that</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" fkcefr="c1" hclass="sense" htag="li" id="say_sng_30"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used to introduce an opinion that makes what you have just said seem less strong</span></span><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/opinion-and-argument?level=c1" title="Topic opinion-and-argument"><span class="topic" href="l2:functions:opinion_and_argument?cefr=c1"><span class="topic_name">Opinion and argument</span><span class="topic_cefr">c1</span></span></a></span> </li></ol></span> <span class="idm-g" id="say_idmg_22" sk="thatsnotsayingmuch"><div class="top-container"><div class="top-g" id="say_topg_24"><div class="webtop"><span class="idm" id="say_idm_22">that’s not saying much</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_29"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used to say that something is not very unusual or special</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__127"><span class="x">She's a better player than me, but that's not saying much <span class="gloss" hclass="gloss" htag="span">(= because I am a very bad player)</span>.</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_24" sk="theresnosaying"><div class="top-container"><div class="top-g" id="say_topg_26"><div class="webtop"><span class="idm" id="say_idm_24">there’s no saying</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_31"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used to say that it is impossible to predict what might happen</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__128"><span class="x">There's no saying how he'll react.</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_25" sk="theressomethingnotmuchtobesaidforsth"><div class="top-container"><div class="top-g" id="say_topg_27"><div class="webtop"><span cefr="c2" class="idm" id="say_idm_25">there’s something, not much, etc. to be said for something/doing something</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" fkcefr="c2" hclass="sense" htag="li" id="say_sng_32"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">there are/are not good reasons for doing something, believing something or agreeing with something</span></span><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/opinion-and-argument?level=c2" title="Topic opinion-and-argument"><span class="topic" href="l2:functions:opinion_and_argument?cefr=c2"><span class="topic_name">Opinion and argument</span><span class="topic_cefr">c2</span></span></a></span> </li></ol></span> <span class="idm-g" id="say_idmg_59" sk="tosayleast"><div class="top-container"><div class="top-g" id="say_topg_62"><div class="webtop"><span class="idm" id="say_idm_61">to say the least</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_71"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">without making something seem better, worse, more serious, etc. than it really is</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__129"><span class="x">I was surprised, to say the least.</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_27" sk="tosaynothingof"><div class="top-container"><div class="top-g" id="say_topg_29"><div class="webtop"><span class="idm" id="say_idm_27">to say nothing of something</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_34"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used to introduce a further fact or thing in addition to those already mentioned</span></span> <span class="xrefs" hclass="xrefs" htag="span" xt="nsyn"><span class="prefix">synonym</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/mention#mention_idmg_2" title="not to mention definition"><span bord="n" class="xr-g" href="mention_idmg_2"><span class="xw">not to mention</span></span></a></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__130"><span class="x">It was too expensive, to say nothing of the time it wasted.</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_28" sk="wellsaid"><div class="top-container"><div class="top-g" id="say_topg_30"><div class="webtop"><span cefr="c2" class="idm" id="say_idm_28">well said!</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" fkcefr="c2" hclass="sense" htag="li" id="say_sng_35"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">I agree completely</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__131"><span class="x">‘We must stand up for ourselves.’ ‘Well said, John.’</span></li></ul><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/opinion-and-argument?level=c2" title="Topic opinion-and-argument"><span class="topic" href="l2:functions:opinion_and_argument?cefr=c2"><span class="topic_name">Opinion and argument</span><span class="topic_cefr">c2</span></span></a></span> </li></ol></span> <span class="idm-g" id="say_idmg_29" sk="whatdoyousay"><div class="top-container"><div class="top-g" id="say_topg_31"><div class="webtop"><span class="idm" id="say_idm_29">what do/would you say (to something/doing something)</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_36"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">would you like something/to do something?</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__132"><span class="x">What would you say to eating out tonight?</span></li><li class="" htag="li" id="OALD10_say__133"><span class="x">Let's go away for a weekend. What do you say?</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_31" sk="whateveryousay"><div class="top-container"><div class="top-g" id="say_topg_33"><div class="webtop"><span cefr="c2" class="idm" id="say_idm_31">whatever you say</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" fkcefr="c2" hclass="sense" htag="li" id="say_sng_38"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">used to agree to somebody’s suggestion because you do not want to argue</span><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/opinion-and-argument?level=c2" title="Topic opinion-and-argument"><span class="topic" href="l2:functions:opinion_and_argument?cefr=c2"><span class="topic_name">Opinion and argument</span><span class="topic_cefr">c2</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/suggestions-and-advice?level=c2" title="Topic suggestions-and-advice"><span class="topic" href="l2:functions:suggestions_and_advice?cefr=c2"><span class="topic_name">Suggestions and advice</span><span class="topic_cefr">c2</span></span></a></span> </li></ol></span> <span class="idm-g" id="say_idmg_30" sk="whatsaysgoes"><div class="top-container"><div class="top-g" id="say_topg_32"><div class="webtop"><span class="idm" id="say_idm_30">what/whatever somebody says, goes</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_37"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal, often humorous)</span></span> <span class="def" hclass="def" htag="span">a particular person must be obeyed</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__134"><span class="x">Sarah wanted the kitchen painted green, and what she says, goes.</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_32" sk="whenallissaidanddone"><div class="top-container"><div class="top-g" id="say_topg_34"><div class="webtop"><span class="idm" id="say_idm_32">when all is said and done</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_39"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">when everything is considered</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__135"><span class="x">I know you're upset, but when all's said and done it isn't exactly a disaster.</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_33" sk="whocansay"><div class="top-container"><div class="top-g" id="say_topg_35"><div class="webtop"><span class="idm" id="say_idm_33">who can say (…)?</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_40"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used to say that nobody knows the answer to a question</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__136"><span class="x">Who can say what will happen next year?</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_34" sk="whosays"><div class="top-container"><div class="top-g" id="say_topg_36"><div class="webtop"><span cefr="c2" class="idm" id="say_idm_34">who says (…)?</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" fkcefr="c2" hclass="sense" htag="li" id="say_sng_41"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">used to disagree with a statement or an opinion</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__137"><span class="x">Who says I can't do it?</span></li></ul><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/opinion-and-argument?level=c2" title="Topic opinion-and-argument"><span class="topic" href="l2:functions:opinion_and_argument?cefr=c2"><span class="topic_name">Opinion and argument</span><span class="topic_cefr">c2</span></span></a></span> </li></ol></span> <span class="idm-g" id="say_idmg_35" sk="whostosay"><div class="top-container"><div class="top-g" id="say_topg_37"><div class="webtop"><span class="idm" id="say_idm_35">who’s to say (…)?</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_42"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used to say that something might happen or might have happened in a particular way, because nobody really knows</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__138"><span class="x">Who's to say we would not have succeeded if we'd had more time?</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_36" sk="youcansaythatagain"><div class="top-container"><div class="top-g" id="say_topg_38"><div class="webtop"><span cefr="c1" class="idm" id="say_idm_36">you can say that again</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" fkcefr="c1" hclass="sense" htag="li" id="say_sng_43"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">I agree with you completely</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__139"><span class="x">‘He's in a bad mood today.’ ‘You can say that again!’</span></li></ul><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/opinion-and-argument?level=c1" title="Topic opinion-and-argument"><span class="topic" href="l2:functions:opinion_and_argument?cefr=c1"><span class="topic_name">Opinion and argument</span><span class="topic_cefr">c1</span></span></a></span> </li></ol></span> <span class="idm-g" id="say_idmg_60" sk="youcantsayfairer"><div class="top-container"><div class="top-g" id="say_topg_63"><div class="webtop"><span class="idm" id="say_idm_62">you can’t say fairer (than that)</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_72"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(British English, informal)</span></span> <span class="def" hclass="def" htag="span">used to say that you think the offer you are making is reasonable or generous</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__140"><span class="x">Look, I'll give you £100 for it. You can't say fairer than that.</span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_38" sk="youdontsay"><div class="top-container"><div class="top-g" id="say_topg_40"><div class="webtop"><span class="idm" id="say_idm_38">you don’t say!</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_45"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal, often ironic)</span></span> <span class="def" hclass="def" htag="span">used to express surprise</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__141"><span class="x">‘They left without us.’ ‘You don't say!’ <span class="gloss" hclass="gloss" htag="span">(= I'm not surprised)</span></span></li></ul> </li></ol></span> <span class="idm-g" id="say_idmg_39" sk="yousaidit"><div class="top-container"><div class="top-g" id="say_topg_41"><div class="webtop"><span class="idm" id="say_idm_39">you said it!</span> <span class="labels" hclass="labels" htag="span">(informal)</span></div></div></div><ol class="senses_multiple" htag="ol"><li cefr="c2" class="sense" hclass="sense" htag="li" id="say_sng_46" sensenum="1"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(British English)</span></span> <span class="def" hclass="def" htag="span">used to agree with somebody when they say something about themselves that you would not have been rude enough to say yourself</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__142"><span class="x">‘I know I'm not the world's greatest cook.’ ‘You said it!’</span></li></ul><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/opinion-and-argument?level=c2" title="Topic opinion-and-argument"><span class="topic" href="l2:functions:opinion_and_argument?cefr=c2"><span class="topic_name">Opinion and argument</span><span class="topic_cefr">c2</span></span></a></span> </li><li cefr="c2" class="sense" hclass="sense" htag="li" id="say_sng_47" sensenum="2"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(North American English)</span></span> <span class="def" hclass="def" htag="span">used to agree with somebody’s suggestion</span><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/opinion-and-argument?level=c2" title="Topic opinion-and-argument"><span class="topic" href="l2:functions:opinion_and_argument?cefr=c2"><span class="topic_name">Opinion and argument</span><span class="topic_cefr">c2</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/suggestions-and-advice?level=c2" title="Topic suggestions-and-advice"><span class="topic" href="l2:functions:suggestions_and_advice?cefr=c2"><span class="topic_name">Suggestions and advice</span><span class="topic_cefr">c2</span></span></a></span> </li></ol></span></div></div><div class="entry" hclass="entry" hlength="3" htag="section" id="say_2" idm_id="000051755" sk="say::2" sum="643"><div class="top-container"><div class="top-g" id="say_topg_42"><div class="webtop"><h1 class="headword" hclass="headword" htag="h1" id="say_h_2" ox5000="y" random="y">say</h1> <span class="pos" hclass="pos" htag="span">noun</span><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox5000&amp;level=c1"><span class="ox5ksym_c1"> </span></a></div><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="say"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_say__gb_1').play();" style="cursor: pointer" title="say pronunciationEnglish" valign="top"></a><span class="phon">/seɪ/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="say"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_say__us_1').play();" style="cursor: pointer" title="say pronunciationAmerican" valign="top"></a><span class="phon">/seɪ/</span></div></span><span class="grammar" hclass="grammar" htag="span">[singular, uncountable]</span><span class="jumplinks"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/say#say_idmgs_2" title="Idioms definition"><span class="jumplink" href="say_idmgs_2" name="Idioms">Idioms</span></a> </span></div></div></div><ol class="sense_single" htag="ol"><li class="sense" fkcefr="c1" fkox5000="y" hclass="sense" htag="li" id="say_sng_48"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">the right to influence something by giving your opinion before a decision is made</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__143"> <span class="cf" hclass="cf" htag="span">say (in something)</span> <span class="x">We had no say in the decision to sell the company.</span></li><li class="" htag="li" id="OALD10_say__144"><span class="x">People want a greater say in local government.</span></li><li class="" htag="li" id="OALD10_say__145"> <span class="cf" hclass="cf" htag="span">say on something</span> <span class="x">The judge has the final say on the sentence.</span></li></ul><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/preferences-and-decisions?level=c1" title="Topic preferences-and-decisions"><span class="topic" href="l2:functions:preferences_and_decisions?cefr=c1"><span class="topic_name">Preferences and decisions</span><span class="topic_cefr">c1</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/opinion-and-argument?level=c1" title="Topic opinion-and-argument"><span class="topic" href="l2:functions:opinion_and_argument?cefr=c1"><span class="topic_name">Opinion and argument</span><span class="topic_cefr">c1</span></span></a></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" dup="fky" id="say4_unbox_1" unbox="wordorigin"><span class="box_title" onclick="toggle_active(this)">Word Origin</span><span class="body"><span class="p">Old English <span class="ei">secgan</span>, of Germanic origin; related to Dutch <span class="ei">zeggen</span> and German <span class="ei">sagen</span>.</span></span></span></div> </li></ol><div class="idioms" hclass="idioms" htag="div" id="say_idmgs_2"><span class="idioms_heading">Idioms</span> <span class="idm-g" id="say_idmg_40" sk="havesay"><div class="top-container"><div class="top-g" id="say_topg_43"><div class="webtop"><span class="idm" id="say_idm_40">have your say</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_49"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">to have the opportunity to express yourself fully about something</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__146"><span class="x">She won't be happy until she's had her say.</span></li></ul> </li></ol></span> <span class="xrefs" hclass="xrefs" htag="span" id="say_xrgs_9" xt="see"><span class="prefix">see also</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/piece#say_idmg_55" title="say your piece definition"><span bord="n" class="xr-g" href="say_idmg_55" id="say_xrg_23"><span class="xw">say your piece</span></span></a></span></div></div><div class="entry" hclass="entry" hlength="3" htag="section" id="say_3" idm_id="000051756" sk="say::3" sum="304"><div class="top-container"><div class="top-g" id="say_topg_44"><div class="webtop"><h1 class="headword" hclass="headword" htag="h1" id="say_h_3" random="y">say</h1> <span class="pos" hclass="pos" htag="span">exclamation</span><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="say"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_say__gb_1').play();" style="cursor: pointer" title="say pronunciationEnglish" valign="top"></a><span class="phon">/seɪ/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="say"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_say__us_1').play();" style="cursor: pointer" title="say pronunciationAmerican" valign="top"></a><span class="phon">/seɪ/</span></div></span> <span class="labels" hclass="labels" htag="span">(North American English, informal)</span></div></div></div><ol class="senses_multiple" htag="ol"><li class="sense" hclass="sense" htag="li" id="say_sng_50" sensenum="1"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used for showing surprise or pleasure</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__147"><span class="x">Say, that's a nice haircut!</span></li></ul> </li><li cefr="c2" class="sense" hclass="sense" htag="li" id="say_sng_51" sensenum="2"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used for attracting somebody’s attention or for making a suggestion or comment</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_say__148"><span class="x">Say, how about going to a movie tonight?</span></li></ul><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/suggestions-and-advice?level=c2" title="Topic suggestions-and-advice"><span class="topic" href="l2:functions:suggestions_and_advice?cefr=c2"><span class="topic_name">Suggestions and advice</span><span class="topic_cefr">c2</span></span></a></span> </li><div class="collapse" hclass="collapse" htag="div"><span class="unbox" dup="fky" id="say5_unbox_1" unbox="wordorigin"><span class="box_title" onclick="toggle_active(this)">Word Origin</span><span class="body"><span class="p">Old English <span class="ei">secgan</span>, of Germanic origin; related to Dutch <span class="ei">zeggen</span> and German <span class="ei">sagen</span>.</span></span></span></div></ol></div></div></body></html><audio id="OALD10_pron_say__gb_1" preload="none" src="OALD10_pron_say__gb_1.mp3"></audio><audio id="OALD10_pron_say__us_1" preload="none" src="OALD10_pron_say__us_1.mp3"></audio><audio id="OALD10_pron_says__gb_1" preload="none" src="OALD10_pron_says__gb_1.mp3"></audio><audio id="OALD10_pron_says__us_1" preload="none" src="OALD10_pron_says__us_1.mp3"></audio><audio id="OALD10_pron_said__gb_1" preload="none" src="OALD10_pron_said__gb_1.mp3"></audio><audio id="OALD10_pron_said__us_1" preload="none" src="OALD10_pron_said__us_1.mp3"></audio><audio id="OALD10_pron_saying__gb_2" preload="none" src="OALD10_pron_saying__gb_2.mp3"></audio><audio id="OALD10_pron_saying__us_2" preload="none" src="OALD10_pron_saying__us_2.mp3"></audio>`;
