var custom_content = `<meta charset='utf-8'/><html><head></head><body><div class="oald" id="entryContent"><div class="entry" hclass="entry" hlength="5" htag="section" id="start_1" idm_id="000057035" sk="start: :0" sum="5350"><div class="top-container"><div class="top-g" id="start_topg_3"><div class="webtop"><h1 class="headword" hclass="headword" htag="h1" id="start_h_2" ox3000="y" random="y">start</h1> <span class="pos" hclass="pos" htag="span">verb</span><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a1"><span class="ox3ksym_a1"> </span></a></div><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="start"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_start__gb_1').play();" style="cursor: pointer" title="start pronunciationEnglish" valign="top"></a><span class="phon">/stɑːt/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="start"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_start__us_1').play();" style="cursor: pointer" title="start pronunciationAmerican" valign="top"></a><span class="phon">/stɑːrt/</span></div></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="start2_unbox_1" unbox="verbforms"><span class="box_title" onclick="toggle_active(this)">Verb Forms</span><span class="body"><table class="verb_forms_table"><tbody><tr class="verb_form" form="root"><td class="verb_form"> <span class="vf_prefix">present simple I / you / we / they</span> start</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="start"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_start__gb_1').play();" style="cursor: pointer" title="start pronunciationEnglish" valign="top"></a><span class="phon">/stɑːt/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="start"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_start__us_1').play();" style="cursor: pointer" title="start pronunciationAmerican" valign="top"></a><span class="phon">/stɑːrt/</span></div></span></td></tr><tr class="verb_form" form="thirdps"><td class="verb_form"> <span class="vf_prefix">he / she / it</span> starts</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="starts"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_starts__gb_1').play();" style="cursor: pointer" title="starts pronunciationEnglish" valign="top"></a><span class="phon">/stɑːts/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="starts"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_starts__us_1').play();" style="cursor: pointer" title="starts pronunciationAmerican" valign="top"></a><span class="phon">/stɑːrts/</span></div></span></td></tr><tr class="verb_form" form="past"><td class="verb_form"> <span class="vf_prefix">past simple</span> started</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="started"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_started__gb_1').play();" style="cursor: pointer" title="started pronunciationEnglish" valign="top"></a><span class="phon">/ˈstɑːtɪd/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="started"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_started__us_1').play();" style="cursor: pointer" title="started pronunciationAmerican" valign="top"></a><span class="phon">/ˈstɑːrtɪd/</span></div></span></td></tr><tr class="verb_form" form="pastpart"><td class="verb_form"> <span class="vf_prefix">past participle</span> started</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="started"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_started__gb_1').play();" style="cursor: pointer" title="started pronunciationEnglish" valign="top"></a><span class="phon">/ˈstɑːtɪd/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="started"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_started__us_1').play();" style="cursor: pointer" title="started pronunciationAmerican" valign="top"></a><span class="phon">/ˈstɑːrtɪd/</span></div></span></td></tr><tr class="verb_form" form="prespart"><td class="verb_form"> <span class="vf_prefix">-ing form</span> starting</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="starting"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_starting__gb_1').play();" style="cursor: pointer" title="starting pronunciationEnglish" valign="top"></a><span class="phon">/ˈstɑːtɪŋ/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="starting"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_starting__us_1').play();" style="cursor: pointer" title="starting pronunciationAmerican" valign="top"></a><span class="phon">/ˈstɑːrtɪŋ/</span></div></span></td></tr></tbody></table></span></span></div><span class="jumplinks"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/start#start_idmgs_1" title="Idioms definition"><span class="jumplink" href="start_idmgs_1" name="Idioms">Idioms</span></a> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/start#start_pvgs_1" title="Phrasal Verbs definition"><span class="jumplink" href="start_pvgs_1" name="Phrasal Verbs">Phrasal Verbs</span></a></span></div></div></div><ol class="senses_multiple" htag="ol"><span class="shcut-g" id="start_sngs_2"> <h2 class="shcut" hclass="shcut" htag="h2" id="start_shcut_1">doing something</h2><li cefr="a1" class="sense" hclass="sense" htag="li" id="start_sng_1" ox3000="y" sensenum="1"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a1"><span class="ox3ksym_a1"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[transitive, intransitive]</span> <span class="def" hclass="def" htag="span">to begin doing or using something</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__1"> <span class="cf" hclass="cf" htag="span">start (something)</span> <span class="x">I <span class="cl">start work</span> at nine.</span></li><li class="" htag="li" id="OALD10_start__2"><span class="x">He's just started a new job.</span></li><li class="" htag="li" id="OALD10_start__3"><span class="x">The kids <span class="cl">start school</span> next week.</span></li><li class="" htag="li" id="OALD10_start__4"><span class="x">I only started <span class="gloss" hclass="gloss" htag="span">(= began to read)</span> this book yesterday.</span></li><li class="" htag="li" id="OALD10_start__5"><span class="x">We need to start <span class="gloss" hclass="gloss" htag="span">(= begin using)</span> a new jar of coffee.</span></li><li class="" htag="li" id="OALD10_start__6"><span class="x">It's a long story. Where shall I start?</span></li><li class="" htag="li" id="OALD10_start__7"><span class="x">Can you start <span class="gloss" hclass="gloss" htag="span">(= a new job)</span> on Monday?</span></li><li class="" htag="li" id="OALD10_start__8"> <span class="cf" hclass="cf" htag="span">start to do something</span> <span class="x">It started to rain.</span></li><li class="" htag="li" id="OALD10_start__9"><span class="x">I was starting to feel tired.</span></li><li class="" htag="li" id="OALD10_start__10"><span class="x">Mistakes were starting to creep in.</span></li><li class="" htag="li" id="OALD10_start__11"> <span class="cf" hclass="cf" htag="span">start doing something</span> <span class="x">She started laughing.</span></li><li class="" htag="li" id="OALD10_start__12"><span class="x">Suddenly <span class="cl">things started</span> happening.</span></li><li class="" htag="li" id="OALD10_start__13"> <span class="cf" hclass="cf" htag="span">start on something</span> <span class="x">It's time you started on your homework.</span></li><li class="" htag="li" id="OALD10_start__14"> <span class="cf" hclass="cf" htag="span">start by doing something</span> <span class="x">Let's start by reviewing what we did last week.</span></li><li class="" htag="li" id="OALD10_start__15"><span class="x">If you want to find out more, this website is <span class="cl">a good place to start</span>.</span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="start2_unbox_2" unbox="express"><span class="box_title" onclick="toggle_active(this)">Express Yourself <span class="closed">Conversation openers</span></span><span class="body"><span class="unbox">Conversation openers</span><span class="p">What can you say when you have to speak to someone for the first time or when you have to open a meeting? Here are some possible ways of starting a conversation or getting the audience's attention before a talk or speech:</span><ul class="bullet"><li class="li"><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__16"><span class="unx"><span class="ebi">Do you mind if I sit here? </span></span></li></ul></li><li class="li"><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__17"><span class="unx"><span class="ebi">Hello, is this seat taken?</span></span></li></ul></li><li class="li"><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__18"><span class="unx"><span class="ebi">May I join you?</span> Can I get you a coffee?</span></li></ul></li><li class="li"><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__19"><span class="unx"><span class="ebi">Lovely weather we’re having!/Can you believe this rain/​wind/​cold/​sunshine?</span></span></li></ul></li><li class="li"><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__20"><span class="unx"><span class="ebi">Excuse me,</span> could I ask you a question?</span></li></ul></li><li class="li"><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__21"><span class="unx"><span class="ebi">Shall we make a start?</span> I think it's almost three o'clock.</span></li></ul> <span class="labels" hclass="labels" htag="span">(British English)</span></li><li class="li"><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__22"><span class="unx"><span class="ebi">Shall we get started?</span> I'd like to introduce our speaker.</span></li></ul><span class="labels" hclass="labels" htag="span">(especially North American English)</span></li><li class="li"><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__23"><span class="unx"><span class="ebi">I think everyone's here, so</span> I'd like to welcome you to this conference.</span></li></ul></li></ul></span></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="start2_unbox_3" unbox="which_word"><span class="box_title" onclick="toggle_active(this)">Which Word? <span class="closed">begin / start</span></span><span class="body"><span class="unbox">begin / start</span><ul class="bullet"><li class="li">There is not much difference in meaning between <span class="eb">begin</span> and <span class="eb">start</span>, though <span class="eb">start</span> is more common in spoken English:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__24"><span class="unx">What time does the concert start/​begin?</span></li><li class="" htag="li" id="OALD10_start__25"><span class="unx">She started/​began working here three months ago.</span></li></ul> <span class="eb">Begin</span> is often used when you are describing a series of events:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__26"><span class="unx">The story begins on the island of Corfu.</span></li></ul> <span class="eb">Start</span>, but not <span class="eb">begin</span>, can also mean ‘to start a journey’, ‘to start something happening’ or ‘to start a machine working’:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__27"><span class="unx">We’ll need to start at 7.00.</span></li><li class="" htag="li" id="OALD10_start__28"><span class="unx">Who do you think started the fire?</span></li><li class="" htag="li" id="OALD10_start__29"><span class="unx">The car won’t start.</span></li></ul></li><li class="li">You can use either an infinitive or a form with <span class="ei">-ing</span> after <span class="eb">begin</span> and <span class="eb">start</span>, with no difference in meaning:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__30"><span class="unx">I didn’t start worrying/​to worry until she was 2 hours late.</span></li></ul></li><li class="li">After the forms <span class="eb">beginning</span> and <span class="eb">starting</span>, the <span class="ei">-ing</span> form of the verb is not normally used:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__31"><span class="unx">It’s starting/​beginning to rain.</span></li><li class="" htag="li"><span class="wx">It’s starting/​beginning raining.</span></li></ul></li></ul></span></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="start2_unbox_4" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__32"><span class="unx">By early evening he was ready to start work.</span></li><li class="" htag="li" id="OALD10_start__33"><span class="unx">She started work in the mill when she was just 14 years old.</span></li><li class="" htag="li" id="OALD10_start__34"><span class="unx">When we started this project, we didn't know what to expect.</span></li><li class="" htag="li" id="OALD10_start__35"><span class="unx">At that point I just started to hate the man.</span></li><li class="" htag="li" id="OALD10_start__36"><span class="unx">The garden is starting to look very bare.</span></li><li class="" htag="li" id="OALD10_start__37"><span class="unx">I'm starting to think I should move somewhere else.</span></li><li class="" htag="li" id="OALD10_start__38"><span class="unx">She started to wonder whether she was imagining it all.</span></li><li class="" htag="li" id="OALD10_start__39"><span class="unx">Her heart suddenly started to race.</span></li><li class="" htag="li" id="OALD10_start__40"><span class="unx">He started to get angry.</span></li><li class="" htag="li" id="OALD10_start__41"><span class="unx">He started getting angry.</span></li><li class="" htag="li" id="OALD10_start__42"><span class="unx">The girl next to me started to talk to me.</span></li><li class="" htag="li" id="OALD10_start__43"><span class="unx">The girl next to me started talking to me.</span></li><li class="" htag="li" id="OALD10_start__44"><span class="unx">People have started saying that they don't want to use social media any more.</span></li><li class="" htag="li" id="OALD10_start__45"><span class="unx">Things were very different when I started working here 17 years ago.</span></li><li class="" htag="li" id="OALD10_start__46"><span class="unx">I need to start getting ready.</span></li><li class="" htag="li" id="OALD10_start__47"><span class="unx">It's time to start thinking about next year.</span></li><li class="" htag="li" id="OALD10_start__48"><span class="unx">Maybe you should start looking for a new job.</span></li><li class="" htag="li" id="OALD10_start__49"><span class="unx">When did you start playing the piano?</span></li><li class="" htag="li" id="OALD10_start__50"><span class="unx">You'd better start packing if you're to leave early tomorrow morning.</span></li><li class="" htag="li" id="OALD10_start__51"><span class="unx">We'll just have to start all over again.</span></li><li class="" htag="li" id="OALD10_start__52"><span class="unx">I've finished decorating the bathroom, so now I can start on the bedroom.</span></li><li class="" htag="li" id="OALD10_start__53"><span class="unx">He finished all his own food and started on mine.</span></li><li class="" htag="li" id="OALD10_start__54"><span class="unx">He has just started at school.</span></li><li class="" htag="li" id="OALD10_start__55"><span class="unx">I don't know where to start with all this work.</span></li><li class="" htag="li" id="OALD10_start__56"><span class="unx">Start from the beginning and tell me exactly what happened.</span></li><li class="" htag="li" id="OALD10_start__57"><span class="unx">I'll have to start again from scratch.</span></li><li class="" htag="li" id="OALD10_start__58"><span class="unx">We start by examining the evidence.</span></li><li class="" htag="li" id="OALD10_start__59"><span class="unx">Start by preparing the planting area.</span></li><li class="" htag="li" id="OALD10_start__60"><span class="unx">If you want to learn about frogs, this book is an excellent place to start.</span></li></ul></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="start2_unbox_5" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adverb</span><ul class="collocs_list"><li class="li">suddenly</li><li class="li">immediately</li><li class="li">just</li><li class="li">…</li></ul><span class="unbox">verb + start</span><ul class="collocs_list"><li class="li">decide to</li><li class="li">expect to</li><li class="li">hope to</li><li class="li">…</li></ul><span class="unbox">preposition</span><ul class="collocs_list"><li class="li">as</li><li class="li">by</li><li class="li">from</li><li class="li">…</li></ul><span class="unbox">phrases</span><ul class="collocs_list"><li class="li">get (somebody/​something) started</li><li class="li">a good, great, etc. place to start</li><li class="li">let’s start</li><li class="li">…</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/start_2" title=" definition in Collocations">full entry</a></span></span></span></div> </li></span><span class="shcut-g" id="start_sngs_3"> <h2 class="shcut" hclass="shcut" htag="h2" id="start_shcut_2">happening</h2><li cefr="a1" class="sense" hclass="sense" htag="li" id="start_sng_2" ox3000="y" sensenum="2"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a1"><span class="ox3ksym_a1"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[intransitive, transitive]</span> <span class="def" hclass="def" htag="span">to begin happening; to make something begin happening</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__61"><span class="x">Work is due to start this weekend.</span></li><li class="" htag="li" id="OALD10_start__62"><span class="x">What time does the show start?</span></li><li class="" htag="li" id="OALD10_start__63"><span class="x">Have you any idea where the rumour started?</span></li><li class="" htag="li" id="OALD10_start__64"> <span class="cf" hclass="cf" htag="span">start something</span> <span class="x">Who <span class="cl">started the fire</span>?</span></li><li class="" htag="li" id="OALD10_start__65"><span class="x">I'm ready to <span class="cl">start the process</span> of writing.</span></li><li class="" htag="li" id="OALD10_start__66"><span class="x">Do you <span class="cl">start the day</span> with a good breakfast?</span></li><li class="" htag="li" id="OALD10_start__67"><span class="x">You're always trying to start an argument.</span></li><li class="" htag="li" id="OALD10_start__68"> <span class="cf" hclass="cf" htag="span">start somebody/something doing something</span> <span class="x">The news started me thinking.</span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="start2_unbox_6" unbox="synonyms"><span class="box_title" onclick="toggle_active(this)">Synonyms <span class="closed">start</span></span><span class="body"><span class="unbox">start</span><ul class="inline"><li class="li">begin</li><li class="li">start off</li><li class="li">kick off</li><li class="li">commence</li><li class="li">open</li></ul><span class="p">These words are all used to talk about things happening from the beginning, or people doing the first part of something.</span><ul class="deflist"><li class="li"><span class="dt">start</span> <span class="dd">to begin to happen or exist; to begin in a particular way or from a particular point:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__69"><span class="unx">When does the class start?</span></li></ul></span></li><li class="li"><span class="dt">begin</span> <span class="dd">to start to happen or exist; to start in a particular way or from a particular point; to start speaking:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__70"><span class="unx">When does the concert begin?</span></li></ul></span></li></ul> <span class="un"><span class="h4">start or begin?</span><span class="p">There is not much difference in meaning between these words. <span class="eb">Start</span> is more frequent in spoken English and in business contexts; <span class="eb">begin</span> is more frequent in written English and is often used when you are describing a series of events: <span class="ei">The story begins on the island of Corfu.</span> <span class="eb">Start</span> is not used to mean ‘begin speaking’: <span class="wx">‘Ladies and gentlemen,’ he started.</span> </span></span><ul class="deflist"><li class="li"><span class="dt">start off</span> <span class="dd">(<span class="ei">rather informal</span>) to start happening or doing something; to start by doing or being something:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__71"><span class="unx">The discussion started off mildly enough.</span></li></ul></span></li><li class="li"><span class="dt">kick off</span> <span class="dd">(<span class="ei">informal</span>) to start an event or activity, especially in a particular way; (of an event, activity, etc.) to start, especially in a particular way:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__72"><span class="unx">Tom will kick off with a few comments.</span></li><li class="" htag="li" id="OALD10_start__73"><span class="unx">The festival kicks off on Monday, September 13.</span></li></ul></span></li><li class="li"><span class="dt">commence</span> <span class="dd">(<span class="ei">formal</span>) to start happening:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__74"><span class="unx">The meeting is scheduled to commence at noon.</span></li></ul></span></li><li class="li"><span class="dt">open</span> <span class="dd">to start an event or activity in a particular way; (of an event, a film or a book) to start, especially in a particular way:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__75"><span class="unx">The story opens with a murder.</span></li></ul></span></li></ul><span class="patterns">Patterns</span><ul class="bullet"><li class="li">to start/​begin/​start off/​kick off/​commence/​open <span class="eb">with</span> something</li><li class="li">to start/​begin/​start off/​kick off/​commence/​open <span class="eb">by</span> doing something</li><li class="li">to start/​begin/​start off/​commence <span class="eb">as</span> something</li><li class="li">a <span class="eb">campaign/​season/​meeting</span> starts/​begins/​starts off/​kicks off/​commences/​opens</li><li class="li">a <span class="eb">film/​book</span> starts/​begins/​starts off/​opens</li></ul></span></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="start2_unbox_7" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__76"><span class="unx">A new term was about to start.</span></li><li class="" htag="li" id="OALD10_start__77"><span class="unx">When does the class start?</span></li><li class="" htag="li" id="OALD10_start__78"><span class="unx">The new season starts on Monday.</span></li><li class="" htag="li" id="OALD10_start__79"><span class="unx">Her career in the civil service started in 1998.</span></li><li class="" htag="li" id="OALD10_start__80"><span class="unx">The fire is thought to have started in the kitchen.</span></li><li class="" htag="li" id="OALD10_start__81"><span class="unx">After the war started, many people tried to leave.</span></li><li class="" htag="li" id="OALD10_start__82"><span class="unx">It's a great opportunity to start a new career.</span></li><li class="" htag="li" id="OALD10_start__83"><span class="unx">These producers start their planting season a week or two earlier than normal.</span></li><li class="" htag="li" id="OALD10_start__84"><span class="unx">They started a war that wasn't justified.</span></li><li class="" htag="li" id="OALD10_start__85"><span class="unx">Before you start the game, you choose a character to play as.</span></li><li class="" htag="li" id="OALD10_start__86"><span class="unx">Who started the fight?</span></li><li class="" htag="li" id="OALD10_start__87"><span class="unx">It's his fault. He started it.</span></li><li class="" htag="li" id="OALD10_start__88"><span class="unx">A campaign was started for his release.</span></li><li class="" htag="li" id="OALD10_start__89"><span class="unx">Our goal is to start a conversation with our customers.</span></li><li class="" htag="li" id="OALD10_start__90"><span class="unx">Birmingham Royal Ballet start the new season with an intriguing triple bill next week.</span></li><li class="" htag="li" id="OALD10_start__91"><span class="unx">We want to start people talking about issues that matter to them in their community.</span></li><li class="" htag="li" id="OALD10_start__92"><span class="unx">It was Liz that started me listening to classical music.</span></li></ul></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="start2_unbox_8" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adverb</span><ul class="collocs_list"><li class="li">suddenly</li><li class="li">immediately</li><li class="li">just</li><li class="li">…</li></ul><span class="unbox">verb + start</span><ul class="collocs_list"><li class="li">decide to</li><li class="li">expect to</li><li class="li">hope to</li><li class="li">…</li></ul><span class="unbox">preposition</span><ul class="collocs_list"><li class="li">as</li><li class="li">by</li><li class="li">from</li><li class="li">…</li></ul><span class="unbox">phrases</span><ul class="collocs_list"><li class="li">get (somebody/​something) started</li><li class="li">a good, great, etc. place to start</li><li class="li">let’s start</li><li class="li">…</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/start_2" title=" definition in Collocations">full entry</a></span></span></span></div> <div class=""> <div class="am-default contentslot" id="ad_contentslot_1"></div></div> </li></span><span class="shcut-g" id="start_sngs_4"> <h2 class="shcut" hclass="shcut" htag="h2" id="start_shcut_3">machine/vehicle</h2><li cefr="a1" class="sense" hclass="sense" htag="li" id="start_sng_3" ox3000="y" sensenum="3"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a1"><span class="ox3ksym_a1"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[transitive, intransitive]</span> <span class="cf" hclass="cf" htag="span">start (something)</span> <span class="def" hclass="def" htag="span">when you <span class="eb">start</span> a machine or a vehicle or it <span class="eb">starts</span>, it begins to operate</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__93"><span class="x">Start the engines!</span></li><li class="" htag="li" id="OALD10_start__94"><span class="x">He started the recording.</span></li><li class="" htag="li" id="OALD10_start__95"><span class="x">I can't get the car started.</span></li><li class="" htag="li" id="OALD10_start__96"><span class="x">The car won't start.</span></li></ul> <span class="xrefs" hclass="xrefs" htag="span" xt="see"><span class="prefix">see also</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/jump-start" title="jump-start definition"><span bord="n" class="xr-g" href="jumpstart_e"><span class="xh">jump-start</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/kick-start#kick-start_2" title="kick-start definition"><span bord="n" class="xr-g" href="kickstart2_e"><span class="xh">kick-start</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/push-start#push-start_2" title="push-start definition"><span bord="n" class="xr-g" href="pushstart2_e"><span class="xh">push-start</span></span></a></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="start2_unbox_9" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__97"><span class="unx">He walked over to his truck, got in and started the engine.</span></li><li class="" htag="li" id="OALD10_start__98"><span class="unx">Pushing the ignition button starts the engine.</span></li><li class="" htag="li" id="OALD10_start__99"><span class="unx">Dan started the lawn mower again.</span></li><li class="" htag="li" id="OALD10_start__100"><span class="unx">Start the tape.</span></li><li class="" htag="li" id="OALD10_start__101"><span class="unx">I couldn't get the car to start.</span></li><li class="" htag="li" id="OALD10_start__102"><span class="unx">Her voice was drowned out when the car started.</span></li><li class="" htag="li" id="OALD10_start__103"><span class="unx">There was a delay because the motor wouldn't start.</span></li><li class="" htag="li" id="OALD10_start__104"><span class="unx">The blender won't start unless the lid is closed properly.</span></li></ul></span></div> </li></span><span class="shcut-g" id="start_sngs_5"> <h2 class="shcut" hclass="shcut" htag="h2" id="start_shcut_4">existing</h2><li cefr="a1" class="sense" hclass="sense" htag="li" id="start_sng_4" ox3000="y" sensenum="4"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a1"><span class="ox3ksym_a1"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[intransitive, transitive]</span> <span class="def" hclass="def" htag="span">to begin to exist; to make something begin to exist</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__105"><span class="x">The company started in 1894 in Pennsylvania.</span></li><li class="" htag="li" id="OALD10_start__106"> <span class="cf" hclass="cf" htag="span">start up</span> <span class="x">There are a lot of small businesses starting up in that area.</span></li><li class="" htag="li" id="OALD10_start__107"> <span class="cf" hclass="cf" htag="span">start something</span> <span class="x">She <span class="cl">started a business</span> while she was still at school.</span></li><li class="" htag="li" id="OALD10_start__108"><span class="x">I decided to start a fashion blog.</span></li><li class="" htag="li" id="OALD10_start__109"><span class="x">They want to <span class="cl">start a family</span> <span class="gloss" hclass="gloss" htag="span">(= to have children)</span>.</span></li><li class="" htag="li" id="OALD10_start__110"> <span class="cf" hclass="cf" htag="span">start something up</span> <span class="x">He decided to start up his own film festival.</span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="start2_unbox_10" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__111"><span class="unx">They decided to start a catering business.</span></li><li class="" htag="li" id="OALD10_start__112"><span class="unx">She started the company in the 1990s.</span></li><li class="" htag="li" id="OALD10_start__113"><span class="unx">She started a yoga class at work.</span></li><li class="" htag="li" id="OALD10_start__114"><span class="unx">Maria started a Spanish conversation class in the village hall.</span></li><li class="" htag="li" id="OALD10_start__115"><span class="unx">I made several attempts to start my own clothing company.</span></li><li class="" htag="li" id="OALD10_start__116"><span class="unx">If you're passionate about a hobby, why not start your own blog?</span></li><li class="" htag="li" id="OALD10_start__117"><span class="unx">When we started the band we never expected this.</span></li><li class="" htag="li" id="OALD10_start__118"><span class="unx">This was a club started by music fans, for music fans.</span></li><li class="" htag="li" id="OALD10_start__119"><span class="unx">They are offering incentives for new businesses to start up in the area.</span></li><li class="" htag="li" id="OALD10_start__120"><span class="unx">There are a lot of costs when you're just starting up.</span></li><li class="" htag="li" id="OALD10_start__121"><span class="unx">Some people have great ideas but start up without having a clear idea of how viable their businesses are. </span></li><li class="" htag="li" id="OALD10_start__122"><span class="unx">He started up his own chain of hotels.</span></li><li class="" htag="li" id="OALD10_start__123"><span class="unx">We started up this website as a way for people to make contact with fellow enthusiasts.</span></li><li class="" htag="li" id="OALD10_start__124"><span class="unx">What made you decide to start the business up?</span></li></ul></span></div> </li></span><span class="shcut-g" id="start_sngs_8"> <h2 class="shcut" hclass="shcut" htag="h2" id="start_shcut_7">from place/level/in particular way</h2><li cefr="a2" class="sense" hclass="sense" htag="li" id="start_sng_8" ox3000="y" sensenum="5"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a2"><span class="ox3ksym_a2"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[intransitive]</span> <span class="def" hclass="def" htag="span">to begin from a particular place, amount or situation; to begin in a particular way</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__125"> <span class="cf" hclass="cf" htag="span">+ adv./prep.</span> <span class="x">The trail starts just outside the town.</span></li><li class="" htag="li" id="OALD10_start__126"><span class="x">Hotel <span class="cl">prices start at</span> €80 for a double room.</span></li><li class="" htag="li" id="OALD10_start__127"><span class="x">A sentence always <span class="cl">starts with</span> a capital letter.</span></li><li class="" htag="li" id="OALD10_start__128"><span class="x">The evening started badly when the speaker failed to turn up.</span></li><li class="" htag="li" id="OALD10_start__129"> <span class="cf" hclass="cf" htag="span">+ adj.</span> <span class="x">The best professional musicians <span class="cl">start young</span>.</span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="start2_unbox_11" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__130"><span class="unx">The 109-mile bike race starts and finishes at the end of Market Street. </span></li><li class="" htag="li" id="OALD10_start__131"><span class="unx">The river starts in northwest Arkansas.</span></li><li class="" htag="li" id="OALD10_start__132"><span class="unx">Let's start with this first piece of music.</span></li><li class="" htag="li" id="OALD10_start__133"><span class="unx">The walk starts from the carpark on Nimmings Lane.</span></li><li class="" htag="li" id="OALD10_start__134"><span class="unx">He could not remember exactly where the taxi ride started.</span></li><li class="" htag="li" id="OALD10_start__135"><span class="unx">Your tour starts at the Cathedral.</span></li><li class="" htag="li" id="OALD10_start__136"><span class="unx">Players start in the centre of the board and move outwards.</span></li><li class="" htag="li" id="OALD10_start__137"><span class="unx">The prints are not expensive, starting at $35.</span></li><li class="" htag="li" id="OALD10_start__138"><span class="unx">We start from the premise that every child matters.</span></li><li class="" htag="li" id="OALD10_start__139"><span class="unx">We are starting from a difficult position because we have very little information.</span></li><li class="" htag="li" id="OALD10_start__140"><span class="unx">I started with the idea that I wanted to write about family.</span></li><li class="" htag="li" id="OALD10_start__141"><span class="unx">It often helps to start with a simple question.</span></li><li class="" htag="li" id="OALD10_start__142"><span class="unx">The story starts in the mid 1960s in a small English town.</span></li><li class="" htag="li" id="OALD10_start__143"><span class="unx">The show starts with a bang and maintains the excitement throughout.</span></li><li class="" htag="li" id="OALD10_start__144"><span class="unx">The film starts well, but halfway through the story starts to drag.</span></li><li class="" htag="li" id="OALD10_start__145"><span class="unx">The team started strongly but faded in the second half.</span></li><li class="" htag="li" id="OALD10_start__146"><span class="unx">It's better to start slowly and learn as you go.</span></li><li class="" htag="li" id="OALD10_start__147"><span class="unx">She started relatively late as an artist.</span></li><li class="" htag="li" id="OALD10_start__148"><span class="unx">James started small with $5,000 in contracts.</span></li><li class="" htag="li" id="OALD10_start__149"><span class="unx">I start simple, with just a geometric shape.</span></li><li class="" htag="li" id="OALD10_start__150"><span class="unx">He started full of confidence, but something went wrong.</span></li></ul></span></div> </li><li cefr="b1" class="sense" hclass="sense" htag="li" id="start_sng_7" ox3000="y" sensenum="6"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=b1"><span class="ox3ksym_b1"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[intransitive, transitive]</span> <span class="def" hclass="def" htag="span">to begin, or to begin something such as a career, in a particular way that changed later</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__151"> <span class="cf" hclass="cf" htag="span">start as something</span> <span class="x">She started as a secretary but ended up running the department.</span></li><li class="" htag="li" id="OALD10_start__152"><span class="x">It started as a hobby and grew from there.</span></li><li class="" htag="li" id="OALD10_start__153"> <span class="cf" hclass="cf" htag="span">start out/off</span> <span class="x">The company started out with 30 employees.</span></li><li class="" htag="li" id="OALD10_start__154"> <span class="cf" hclass="cf" htag="span">start out/off as something</span> <span class="x">I started off as a dishwasher making 25 cents an hour.</span></li><li class="" htag="li" id="OALD10_start__155"> <span class="cf" hclass="cf" htag="span">start something (as something)</span> <span class="x">He <span class="cl">started life</span> as a teacher before turning to journalism.</span></li><li class="" htag="li" id="OALD10_start__156"> <span class="cf" hclass="cf" htag="span">start something doing something</span> <span class="x">She <span class="cl">started her career</span> working for the BBC. </span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="start2_unbox_12" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__157"><span class="unx">I started as a trainee and ended up a supervisor.</span></li><li class="" htag="li" id="OALD10_start__158"><span class="unx">It started as a joke, but we soon realized people were taking it seriously.</span></li><li class="" htag="li" id="OALD10_start__159"><span class="unx">The World Wide Web started as a way for scientists and academics to share information.</span></li><li class="" htag="li" id="OALD10_start__160"><span class="unx">The film started as an experiment to see if I could do something new.</span></li><li class="" htag="li" id="OALD10_start__161"><span class="unx">I started out washing cars at the golf club.</span></li><li class="" htag="li" id="OALD10_start__162"><span class="unx">It's probably best to start out renting equipment.</span></li><li class="" htag="li" id="OALD10_start__163"><span class="unx">We started off with just a few product lines.</span></li><li class="" htag="li" id="OALD10_start__164"><span class="unx">He started work as a butcher's boy and now has his own shop.</span></li><li class="" htag="li" id="OALD10_start__165"><span class="unx">I started my working life as a studio photographer.</span></li><li class="" htag="li" id="OALD10_start__166"><span class="unx">The magazine started life as a comic book in 1952.</span></li><li class="" htag="li" id="OALD10_start__167"><span class="unx">The building started life as a prison but was converted to a luxury hotel.</span></li></ul></span></div> </li></span><span class="shcut-g" id="start_sngs_6"> <h2 class="shcut" hclass="shcut" htag="h2" id="start_shcut_5">journey</h2><li class="sense" hclass="sense" htag="li" id="start_sng_5" sensenum="7"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[intransitive]</span></span> <span class="cf" hclass="cf" htag="span">start (out)</span> <span class="def" hclass="def" htag="span">to begin a journey; to leave</span> <span class="xrefs" hclass="xrefs" htag="span" xt="syn"><span class="prefix">synonym</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/set-off" title="set off definition"><span bord="n" class="xr-g" href="set_pvg_17:set_topg_19"><span class="xw">set off</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/set-out" title="set out definition"><span bord="n" class="xr-g" href="set_pvg_22:set_topg_24"><span class="xw">set out</span></span></a></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__168"><span class="x">What time are we starting tomorrow?</span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="start2_unbox_13" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__169"><span class="unx">She had started out late for the meeting.</span></li><li class="" htag="li" id="OALD10_start__170"><span class="unx">They had started out from Saigon the previous day.</span></li><li class="" htag="li" id="OALD10_start__171"><span class="unx">I should get there by the afternoon if I start early.</span></li><li class="" htag="li" id="OALD10_start__172"><span class="unx">Make sure you've got everything before we start.</span></li><li class="" htag="li" id="OALD10_start__173"><span class="unx">They started out at five o'clock in the morning.</span></li><li class="" htag="li" id="OALD10_start__174"><span class="unx">What time do we start?</span></li></ul></span></div> <div class=""> <div class="am-default contentslot" id="ad_contentslot_2"></div></div> </li></span><span class="shcut-g" id="start_sngs_7"> <h2 class="shcut" hclass="shcut" htag="h2" id="start_shcut_6">going/walking</h2><li class="sense" hclass="sense" htag="li" id="start_sng_6" sensenum="8"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[intransitive]</span></span> <span class="cf" hclass="cf" htag="span">+ adv./prep.</span> <span class="def" hclass="def" htag="span">to begin to move in a particular direction</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__175"><span class="x">I started after her <span class="gloss" hclass="gloss" htag="span">(= began to follow her)</span> to tell her the news.</span></li><li class="" htag="li" id="OALD10_start__176"><span class="x">He started for the door, but I blocked his way.</span></li></ul> </li></span><span class="shcut-g" id="start_sngs_9"> <h2 class="shcut" hclass="shcut" htag="h2" id="start_shcut_8">move suddenly</h2><li cefr="c2" class="sense" hclass="sense" htag="li" id="start_sng_9" sensenum="9"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[intransitive]</span></span> <span class="def" hclass="def" htag="span">to move suddenly and quickly because you are surprised or afraid</span> <span class="xrefs" hclass="xrefs" htag="span" xt="syn"><span class="prefix">synonym</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/jump#jump_1" title="jump definition"><span bord="n" class="xr-g" href="jump_subentryg_1:jump_topg_2"><span class="xh">jump</span></span></a></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__177"><span class="x">The sudden noise made her start.</span></li></ul><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/feelings?level=c2" title="Topic feelings"><span class="topic" href="l2:people:feelings?cefr=c2"><span class="topic_name">Feelings</span><span class="topic_cefr">c2</span></span></a></span> </li></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="start2_unbox_14" unbox="wordorigin"><span class="box_title" onclick="toggle_active(this)">Word Origin</span><span class="body"><span class="p">Old English <span class="ei">styrtan</span> ‘to caper, leap’, of Germanic origin; related to Dutch <span class="ei">storten</span> ‘push’ and German <span class="ei">stürzen</span> ‘fall headlong, fling’. From the sense ‘sudden movement’ arose the sense ‘initiation of movement, setting out on a journey’ and hence ‘beginning of a process, etc.’.</span></span></span></div></ol><div class="idioms" hclass="idioms" htag="div" id="start_idmgs_1"><span class="idioms_heading">Idioms</span> <span class="idm-g" id="start_idmg_1" sk="dontstart"><div class="top-container"><div class="top-g" id="start_topg_4"><div class="webtop"><span class="idm" id="start_idm_1">don’t start <span class="idmsep">|</span> don't you start</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="start_sng_10"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">used to tell somebody not to complain or be critical</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__178"><span class="x">Don't start! I told you I'd be late.</span></li><li class="" htag="li" id="OALD10_start__179"><span class="x">Don't you start! It's hard enough managing the kids, without you complaining too.</span></li></ul> </li></ol></span> <span class="idm-g" id="start_idmg_9" sk="getballrolling"><div class="top-container"><div class="top-g" id="start_topg_2"><div class="webtop"><span class="idm" id="start_idm_8">get/set/start/keep the ball rolling</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="start_sng_18"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to make something start happening; to make sure that something continues to happen</span></span> </li></ol></span> <span class="idm-g" id="start_idmg_10" sk="getoffonrightfoot"><div class="top-container"><div class="top-g" id="start_topg_22"><div class="webtop"><span class="idm" id="start_idm_9">get/start off on the right/wrong foot (with somebody)</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="start_sng_19"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">to start a relationship well/badly</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__180"><span class="x">I seem to have got off on the wrong foot with the new boss.</span></li></ul> <div class=""> <div class="am-default contentslot" id="ad_contentslot_3"></div></div> </li></ol></span> <span class="idm-g" id="start_idmg_2" sk="getstarted"><div class="top-container"><div class="top-g" id="start_topg_5"><div class="webtop"><span class="idm" id="start_idm_2">get started</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="start_sng_11"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to begin doing something</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__181"><span class="x">It's nearly ten o'clock. Let's get started.</span></li><li class="" htag="li" id="OALD10_start__182"><span class="x">It's already late, so I think we should get started.</span></li></ul> </li></ol></span> <span class="idm-g" id="start_idmg_7" sk="startoffonrightfoot"><div class="top-container"><div class="top-g" id="start_topg_19"><div class="webtop"><span class="idm" id="start_idm_10">start/get off on the right/wrong foot (with somebody)</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="start_sng_16"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">to start a relationship well/badly</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__183"><span class="x">I seem to have started off on the wrong foot with the new boss.</span></li></ul> </li></ol></span> <span class="idm-g" id="start_idmg_4" sk="startsomething"><div class="top-container"><div class="top-g" id="start_topg_7"><div class="webtop"><span class="idm" id="start_idm_4">start something</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="start_sng_13"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">to cause trouble</span> </li></ol></span> <span class="idm-g" id="start_idmg_5" sk="tostartwith"><div class="top-container"><div class="top-g" id="start_topg_8"><div class="webtop"><span class="idm" id="start_idm_5">to start with</span> </div></div></div><ol class="senses_multiple" htag="ol"><li cefr="b2" class="sense" hclass="sense" htag="li" id="start_sng_14" sensenum="1"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">used when you are giving the first and most important reason for something</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__184"><span class="x">To start with it’s much too expensive…</span></li></ul><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/opinion-and-argument?level=b2" title="Topic opinion-and-argument"><span class="topic" href="l2:functions:opinion_and_argument?cefr=b2"><span class="topic_name">Opinion and argument</span><span class="topic_cefr">b2</span></span></a></span> </li><li class="sense" hclass="sense" htag="li" id="start_sng_15" sensenum="2"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">at the beginning</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__185"><span class="x">The club had only six members to start with.</span></li><li class="" htag="li" id="OALD10_start__186"><span class="x">I'll have melon to start with.</span></li><li class="" htag="li" id="OALD10_start__187"><span class="x">She wasn't keen on the idea to start with.</span></li></ul> <div class=""> <div class="am-default contentslot" id="ad_contentslot_4"></div></div> </li></ol></span> <span class="idm-g" id="start_idmg_3" sk="youheshestartedit"><div class="top-container"><div class="top-g" id="start_topg_6"><div class="webtop"><span cefr="c2" class="idm" id="start_idm_3">you, he, she, etc. started it</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" fkcefr="c2" hclass="sense" htag="li" id="start_sng_12"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">used to blame somebody for beginning a fight or an argument</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__188"><span class="x">‘Stop fighting, you two!’ ‘He started it!’</span></li></ul><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/opinion-and-argument?level=c2" title="Topic opinion-and-argument"><span class="topic" href="l2:functions:opinion_and_argument?cefr=c2"><span class="topic_name">Opinion and argument</span><span class="topic_cefr">c2</span></span></a></span> </li></ol></span></div><aside class="phrasal_verb_links" hclass="phrasal_verb_links" htag="aside" id="start_pvgs_1" xt="pvlist"><span class="unbox">Phrasal Verbs</span><ul class="pvrefs"><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/start-back#startback2_e" title="start back definition"><span bord="n" class="xr-g" href="startback2_e" id="start_xrg_4"><span class="xh">start back</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/start-off#startoff2_e" title="start off definition"><span bord="n" class="xr-g" href="startoff2_e" id="start_xrg_5"><span class="xh">start off</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/start-on#starton2_e" title="start on definition"><span bord="n" class="xr-g" href="starton2_e" id="start_xrg_6"><span class="xh">start on</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/start-on-about#startonabout_e" title="start on about definition"><span bord="n" class="xr-g" href="startonabout_e" id="start_xrg_7"><span class="xh">start on about</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/start-on-at#startonat2_e" title="start on at definition"><span bord="n" class="xr-g" href="startonat2_e" id="start_xrg_8"><span class="xh">start on at</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/start-out#startout2_e" title="start out definition"><span bord="n" class="xr-g" href="startout2_e" id="start_xrg_9"><span class="xh">start out</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/start-over#startover2_e" title="start over definition"><span bord="n" class="xr-g" href="startover2_e" id="start_xrg_10"><span class="xh">start over</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/start-up#startup5_e" title="start up definition"><span bord="n" class="xr-g" href="startup5_e" id="start_xrg_11"><span class="xh">start up</span></span></a></li></ul></aside></div><div class="entry" hclass="entry" hlength="5" htag="section" id="start_2" idm_id="000057036" sk="start: :0" sum="3333"><div class="top-container"><div class="top-g" id="start_topg_17"><div class="webtop"><h1 class="headword" hclass="headword" htag="h1" id="start_h_3" ox3000="y" random="y">start</h1> <span class="pos" hclass="pos" htag="span">noun</span><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a2"><span class="ox3ksym_a2"> </span></a></div><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="start"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_start__gb_1').play();" style="cursor: pointer" title="start pronunciationEnglish" valign="top"></a><span class="phon">/stɑːt/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="start"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_start__us_1').play();" style="cursor: pointer" title="start pronunciationAmerican" valign="top"></a><span class="phon">/stɑːrt/</span></div></span><span class="jumplinks"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/start#start_idmgs_2" title="Idioms definition"><span class="jumplink" href="start_idmgs_2" name="Idioms">Idioms</span></a> </span></div></div></div><ol class="senses_multiple" htag="ol"><span class="shcut-g" id="start_sngs_23"> <h2 class="shcut" hclass="shcut" htag="h2" id="start_shcut_9">beginning</h2><li cefr="a2" class="sense" hclass="sense" htag="li" id="start_sng_28" ox3000="y" sensenum="1"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a2"><span class="ox3ksym_a2"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[countable, usually singular]</span> <span class="def" hclass="def" htag="span">the point at which something begins</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__189"><span class="x">If we don't hurry, we'll miss the start.</span></li><li class="" htag="li" id="OALD10_start__190"><span class="x">They haven't won since the start of the season. </span></li><li class="" htag="li" id="OALD10_start__191"><span class="cf" hclass="cf" htag="span">at the start (of something)</span> <span class="x">Things didn't look too hopeful at the start of the year.</span></li><li class="" htag="li" id="OALD10_start__192"> <span class="cf" hclass="cf" htag="span">from the start</span> <span class="x">We've had problems <span class="cl">right from the start</span>.</span></li><li class="" htag="li" id="OALD10_start__193"><span class="x">We've had problems <span class="cl">from the very start</span>.</span></li><li class="" htag="li" id="OALD10_start__194"><span class="x">The trip was a disaster <span class="cl">from start to finish</span>.</span></li><li class="" htag="li" id="OALD10_start__195"> <span class="cf" hclass="cf" htag="span">a… start to something</span> <span class="x">a perfect start to the day</span></li><li class="" htag="li" id="OALD10_start__196"><span class="x">After a slow start to his career, things improved.</span></li><li class="" htag="li" id="OALD10_start__197"><span class="x">The campaign is <span class="cl">off to a promising start</span>.</span></li><li class="" htag="li" id="OALD10_start__198"><span class="x">a <span class="cl">shaky/rocky start</span></span></li><li class="" htag="li" id="OALD10_start__199"> <span class="labels" hclass="labels" htag="span">(informal)</span> <span class="x">This could be the start of something big.</span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="start_unbox_1" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__200"><span class="unx">They lived here before the start of the war.</span></li><li class="" htag="li" id="OALD10_start__201"><span class="unx">Temperatures were cooler at the start of the week.</span></li><li class="" htag="li" id="OALD10_start__202"><span class="unx">We make an estimate at the start and revise it later.</span></li><li class="" htag="li" id="OALD10_start__203"><span class="unx">How many more weeks until the start of term?</span></li><li class="" htag="li" id="OALD10_start__204"><span class="unx">She makes a brief appearance near the start of the film.</span></li><li class="" htag="li" id="OALD10_start__205"><span class="unx">Next week sees the start of the trial of four suspects in the bombing.</span></li><li class="" htag="li" id="OALD10_start__206"><span class="unx">September marks the start of Berlin's annual art festival.</span></li><li class="" htag="li" id="OALD10_start__207"><span class="unx">A rooster crows, symbolically signaling the start of the day.</span></li><li class="" htag="li" id="OALD10_start__208"><span class="unx">These environmental changes could herald the start of a new Ice Age.</span></li><li class="" htag="li" id="OALD10_start__209"><span class="unx">The start was delayed by bad weather.</span></li><li class="" htag="li" id="OALD10_start__210"><span class="unx">After a shaky start, it went on to become a hit show.</span></li><li class="" htag="li" id="OALD10_start__211"><span class="unx">The plot gets off to a rather slow start.</span></li><li class="" htag="li" id="OALD10_start__212"><span class="unx">Things got off to a bad start when I forgot to bring the lunch.</span></li><li class="" htag="li" id="OALD10_start__213"><span class="unx">I forgot his name, which wasn't a good start.</span></li><li class="" htag="li" id="OALD10_start__214"><span class="unx">Despite a bright start, Liverpool lost the match.</span></li><li class="" htag="li" id="OALD10_start__215"><span class="unx">Despite a promising start, we lost the game.</span></li><li class="" htag="li" id="OALD10_start__216"><span class="unx">This is a thoroughly good book from start to finish.</span></li><li class="" htag="li" id="OALD10_start__217"><span class="unx">At the very start of the movie we are told that his mother is dead.</span></li><li class="" htag="li" id="OALD10_start__218"><span class="unx">From the very start of the campaign he stressed its inclusive nature.</span></li><li class="" htag="li" id="OALD10_start__219"><span class="unx">Right from the start I knew this was a bad idea.</span></li><li class="" htag="li" id="OALD10_start__220"><span class="unx">She felt at home in her new job right from the start.</span></li><li class="" htag="li" id="OALD10_start__221"><span class="unx">Everyone was in a conciliatory mood at the start of the meeting.</span></li><li class="" htag="li" id="OALD10_start__222"><span class="unx">He joined the team prior to the start of the season.</span></li><li class="" htag="li" id="OALD10_start__223"><span class="unx">The company got off to an impressive start this year.</span></li><li class="" htag="li" id="OALD10_start__224"><span class="unx">Higher sales were predicted after a promising start to the year.</span></li><li class="" htag="li" id="OALD10_start__225"><span class="unx">An obvious typo on page one makes a bad start to the book.</span></li><li class="" htag="li" id="OALD10_start__226"><span class="unx">The team made a great comeback after a disastrous start to the tournament.</span></li><li class="" htag="li" id="OALD10_start__227"><span class="unx">The fine winter weather heralded a good start to the year.</span></li><li class="" htag="li" id="OALD10_start__228"><span class="unx">The project was doomed from the start.</span></li></ul></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="start_unbox_2" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adjective</span><ul class="collocs_list"><li class="li">auspicious</li><li class="li">bright</li><li class="li">decent</li><li class="li">…</li></ul><span class="unbox">verb + start</span><ul class="collocs_list"><li class="li">make</li><li class="li">get off to</li><li class="li">herald</li><li class="li">…</li></ul><span class="unbox">start + noun</span><ul class="collocs_list"><li class="li">date</li><li class="li">time</li><li class="li">button</li><li class="li">…</li></ul><span class="unbox">preposition</span><ul class="collocs_list"><li class="li">at the start (of)</li><li class="li">from the start</li><li class="li">prior to the start</li><li class="li">…</li></ul><span class="unbox">phrases</span><ul class="collocs_list"><li class="li">be doomed from the start</li><li class="li">from start to finish</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/start_1" title=" definition in Collocations">full entry</a></span></span></span></div> </li><li cefr="b2" class="sense" hclass="sense" htag="li" id="start_sng_29" ox3000="y" sensenum="2"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=b2"><span class="ox3ksym_b2"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[countable, usually singular]</span> <span class="def" hclass="def" htag="span">the act or process of beginning something</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__229"><span class="x">We need to <span class="cl">get</span> an early <span class="cl">start</span> in the morning.</span></li><li class="" htag="li" id="OALD10_start__230"> <span class="cf" hclass="cf" htag="span">start on something</span> <span class="x">I'll paint the ceiling if you <span class="cl">make a start</span> on the walls.</span></li><li class="" htag="li" id="OALD10_start__231"><span class="x">She's moving abroad to make a <span class="cl">fresh start</span> <span class="gloss" hclass="gloss" htag="span">(= to begin a new life)</span>.</span></li><li class="" htag="li" id="OALD10_start__232"><span class="x">I think it's time we made a start.</span></li><li class="" htag="li" id="OALD10_start__233"><span class="x">It's not much, but <span class="cl">it's a start</span> <span class="gloss" hclass="gloss" htag="span">(= something useful has been done, although there is a lot more still to do)</span>.</span></li></ul> <span class="xrefs" hclass="xrefs" htag="span" xt="see"><span class="prefix">see also</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/false-start" title="false start definition"><span bord="n" class="xr-g" href="falsestart_e"><span class="xh">false start</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/kick-start#kick-start_1" title="kick-start definition"><span bord="n" class="xr-g" href="kick_start_subentryg_2:kick_start_topg_3"><span class="xh">kick-start</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/push-start#push-start_1" title="push-start definition"><span bord="n" class="xr-g" href="pushstart_e"><span class="xw">push-start</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/running-start" title="running start definition"><span bord="n" class="xr-g" href="runningstart_e"><span class="xh">running start</span></span></a></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="start_unbox_3" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__234"><span class="unx">Unless I make a start, I'll never write this book.</span></li><li class="" htag="li" id="OALD10_start__235"><span class="unx">Let's make a start.</span></li><li class="" htag="li" id="OALD10_start__236"><span class="unx">Just pick the most urgent task and make a start on it now.</span></li><li class="" htag="li" id="OALD10_start__237"><span class="unx">There's a long way to go, but he's made a very encouraging start.</span></li><li class="" htag="li" id="OALD10_start__238"><span class="unx">This will make a start on paying off the mortgage.</span></li><li class="" htag="li" id="OALD10_start__239"><span class="unx">The authorities are making a start in dealing with the scourge of illegal drugs.</span></li><li class="" htag="li" id="OALD10_start__240"><span class="unx">He wanted to go to England and make a new start for his family.</span></li><li class="" htag="li" id="OALD10_start__241"><span class="unx">It's a time for new beginnings, for fresh starts.</span></li><li class="" htag="li" id="OALD10_start__242"><span class="unx">An early start is important if you want to avoid the traffic.</span></li><li class="" htag="li" id="OALD10_start__243"><span class="unx">I hope we can get a good start to the game and play really well.</span></li><li class="" htag="li" id="OALD10_start__244"><span class="unx">New housing starts <span class="gloss" hclass="gloss" htag="span">(= the number of new housing projects)</span> slowed at the end of the year.</span></li></ul></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="start_unbox_4" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adjective</span><ul class="collocs_list"><li class="li">auspicious</li><li class="li">bright</li><li class="li">decent</li><li class="li">…</li></ul><span class="unbox">verb + start</span><ul class="collocs_list"><li class="li">make</li><li class="li">get off to</li><li class="li">herald</li><li class="li">…</li></ul><span class="unbox">start + noun</span><ul class="collocs_list"><li class="li">date</li><li class="li">time</li><li class="li">button</li><li class="li">…</li></ul><span class="unbox">preposition</span><ul class="collocs_list"><li class="li">at the start (of)</li><li class="li">from the start</li><li class="li">prior to the start</li><li class="li">…</li></ul><span class="unbox">phrases</span><ul class="collocs_list"><li class="li">be doomed from the start</li><li class="li">from start to finish</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/start_1" title=" definition in Collocations">full entry</a></span></span></span></div> <div class=""> <div class="am-default contentslot" id="ad_contentslot_1"></div></div> </li></span><span class="shcut-g" id="start_sngs_24"> <h2 class="shcut" hclass="shcut" htag="h2" id="start_shcut_10">opportunity</h2><li cefr="b2" class="sense" hclass="sense" htag="li" id="start_sng_30" ox3000="y" sensenum="3"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=b2"><span class="ox3ksym_b2"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[countable, usually singular]</span> <span class="def" hclass="def" htag="span">the opportunity that you are given to begin something in a successful way</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__245"><span class="x">Many critics <span class="cl">got their start</span> writing for this magazine.</span></li><li class="" htag="li" id="OALD10_start__246"><span class="x">Moving to a different school gave Sally a <span class="cl">fresh start</span>.</span></li><li class="" htag="li" id="OALD10_start__247"> <span class="cf" hclass="cf" htag="span">start in something</span> <span class="x">They worked hard to give their children a good <span class="cl">start in life</span>.</span></li><li class="" htag="li" id="OALD10_start__248"><span class="x">The job gave him his start in journalism.</span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="start_unbox_5" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__249"><span class="unx">The Amato Theater is a place where opera performers get their start.</span></li><li class="" htag="li" id="OALD10_start__250"><span class="unx">The modern fair trade movement got its start in Europe.</span></li><li class="" htag="li" id="OALD10_start__251"><span class="unx">The DJ got his start as a teenaged janitor at Capitol Records. </span></li><li class="" htag="li" id="OALD10_start__252"><span class="unx">He got his start in the music business in the early 1990s.</span></li><li class="" htag="li" id="OALD10_start__253"><span class="unx">How did you get your start in politics?</span></li><li class="" htag="li" id="OALD10_start__254"><span class="unx">I feel for young people coming out of college and trying to get a start in the world.</span></li><li class="" htag="li" id="OALD10_start__255"><span class="unx">We aim to give every child the best start possible.</span></li><li class="" htag="li" id="OALD10_start__256"><span class="unx">This was a way for me to give these guys a good start in the business.</span></li><li class="" htag="li" id="OALD10_start__257"><span class="unx">She gave me my start as a performer.</span></li><li class="" htag="li" id="OALD10_start__258"><span class="unx">That was my start as a professional athlete.</span></li><li class="" htag="li" id="OALD10_start__259"><span class="unx">The children have overcome their difficult start in life.</span></li><li class="" htag="li" id="OALD10_start__260"><span class="unx">He didn't have the easiest of starts, but he has done incredibly well.</span></li></ul></span></div><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/success?level=b2" title="Topic success"><span class="topic" href="l2:notions:success?cefr=b2"><span class="topic_name">Success</span><span class="topic_cefr">b2</span></span></a></span> </li></span><span class="shcut-g" id="start_sngs_25"> <h2 class="shcut" hclass="shcut" htag="h2" id="start_shcut_11">in race</h2><li class="sense" hclass="sense" htag="li" id="start_sng_31" sensenum="4"><span class="sensetop" hclass="sensetop" htag="span"><div class="variants" hclass="variants" htag="div" type="alt"><span class="v-g"><span class="v">the start</span></span></div></span> <span class="grammar" hclass="grammar" htag="span">[singular]</span> <span class="def" hclass="def" htag="span">the place where a race begins</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__261"><span class="x">The runners lined up at the start.</span></li><li class="" htag="li" id="OALD10_start__262"><span class="x">The runners walked up to the start line.</span></li></ul> </li><li class="sense" hclass="sense" htag="li" id="start_sng_32" sensenum="5"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[countable, usually singular]</span></span> <span class="def" hclass="def" htag="span">an amount of time or distance that somebody has as an advantage over other people at the beginning of a race</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__263"><span class="x">I gave the younger children a start.</span></li><li class="" htag="li" id="OALD10_start__264"> <span class="cf" hclass="cf" htag="span">start on somebody</span> <span class="x">She went into the second round with a five-minute start on the rest of the cyclists.</span></li></ul> <span class="xrefs" hclass="xrefs" htag="span" xt="see"><span class="prefix">see also</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/head-start" title="head start definition"><span bord="n" class="xr-g" href="headstart_e"><span class="xh">head start</span></span></a></span> </li><li class="sense" hclass="sense" htag="li" id="start_sng_33" sensenum="6"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[countable, usually plural]</span></span> <span class="labels" hclass="labels" htag="span">(<span class="subj" subj="sport">sport</span>)</span> <span class="def" hclass="def" htag="span">a race or competition that somebody has taken part in</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__265"><span class="x">She has been beaten only once in six starts.</span></li></ul> </li></span><span class="shcut-g" id="start_sngs_26"> <h2 class="shcut" hclass="shcut" htag="h2" id="start_shcut_12">sudden movement</h2><li class="sense" hclass="sense" htag="li" id="start_sng_34" sensenum="7"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[countable, usually singular]</span></span> <span class="def" hclass="def" htag="span">an act of moving your body quickly and suddenly because you are surprised, afraid, etc.</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__266"><span class="x">You gave me quite a start!</span></li><li class="" htag="li" id="OALD10_start__267"> <span class="cf" hclass="cf" htag="span">with a start</span> <span class="x">She woke from the dream with a start.</span></li></ul> <div class=""> <div class="am-default contentslot" id="ad_contentslot_2"></div></div> </li></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" dup="fky" id="start_unbox_6" unbox="wordorigin"><span class="box_title" onclick="toggle_active(this)">Word Origin</span><span class="body"><span class="p">Old English <span class="ei">styrtan</span> ‘to caper, leap’, of Germanic origin; related to Dutch <span class="ei">storten</span> ‘push’ and German <span class="ei">stürzen</span> ‘fall headlong, fling’. From the sense ‘sudden movement’ arose the sense ‘initiation of movement, setting out on a journey’ and hence ‘beginning of a process, etc.’.</span></span></span></div></ol><div class="idioms" hclass="idioms" htag="div" id="start_idmgs_2"><span class="idioms_heading">Idioms</span> <span class="idm-g" id="start_idmg_11" sk="byfitsandstarts"><div class="top-container"><div class="top-g" id="start_topg_23"><div class="webtop"><span class="idm" id="start_idm_11">by/in fits and starts <span class="idmsep">|</span> in stops and starts</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="start_sng_20"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">frequently starting and stopping again; not continuously</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__268"><span class="x">Because of other commitments I can only write my book in fits and starts.</span></li></ul> </li></ol></span> <span class="idm-g" id="start_idmg_6" sk="forstart"><div class="top-container"><div class="top-g" id="start_topg_18"><div class="webtop"><span cefr="c2" class="idm" id="start_idm_6">for a start</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" fkcefr="c2" hclass="sense" htag="li" id="start_sng_35"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">used to emphasize the first of a list of reasons, opinions, etc.</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__269"><span class="x">I'm not working there—for a start, it's too far to travel.</span></li></ul><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/opinion-and-argument?level=c2" title="Topic opinion-and-argument"><span class="topic" href="l2:functions:opinion_and_argument?cefr=c2"><span class="topic_name">Opinion and argument</span><span class="topic_cefr">c2</span></span></a></span> </li></ol></span> <span class="idm-g" id="start_idmg_12" new="y" sk="fromstandingstart"><div class="top-container"><div class="top-g" id="start_topg_24"><div class="webtop"><span class="idm" id="start_idm_13">from a standing start</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="start_sng_21"><span class="sensetop" hclass="sensetop" htag="span"><span class="dis-g"><span class="wrap">(</span><span class="dtxt">used about beginning a project or task</span><span class="wrap">)</span></span></span> <span class="def" hclass="def" htag="span">from an initial position in which none of the necessary elements are in place</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__270"><span class="x">The space project went from a standing start to the moon in ten years.</span></li></ul> </li></ol></span> <span class="idm-g" id="start_idmg_13" sk="getofftoflyingstart"><div class="top-container"><div class="top-g" id="start_topg_25"><div class="webtop"><span class="idm" id="start_idm_14">get off to a flying start <span class="idmsep">|</span> get off to a flyer</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="start_sng_22"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to make a very good start; to begin something well</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_start__271"><span class="x">She’s got off to a flying start in her new career.</span></li></ul> </li></ol></span></div></div><div class="entry" hclass="entry" hlength="5" htag="section" id="start_3" idm_id="000057037" sk="start: :10" sum="55"><div class="top-container"><div class="top-g" id="start_topg_1"><div class="webtop"><h1 class="headword" hclass="headword" htag="h1" id="start_h_1">START</h1> <span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="START"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_start__gb_1').play();" style="cursor: pointer" title="START pronunciationEnglish" valign="top"></a><span class="phon">/stɑːt/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="START"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_start_1_us_1').play();" style="cursor: pointer" title="START pronunciationAmerican" valign="top"></a><span class="phon">/stɑːrt/</span></div></span></div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="start_sng_36"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span"><span class="xrefs" hclass="xrefs" htag="span" xt="defat"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/the-strategic-arms-reduction-talks" title="Strategic Arms Reduction Talks definition"><span class="xr-g" href="thestrategicarmsreductiontalks_ge"><span class="xh">Strategic Arms Reduction Talks</span></span></a></span></span></span></li></ol></div></div></body></html><audio id="OALD10_pron_start__gb_1" preload="none" src="OALD10_pron_start__gb_1.mp3"></audio><audio id="OALD10_pron_start__us_1" preload="none" src="OALD10_pron_start__us_1.mp3"></audio><audio id="OALD10_pron_starts__gb_1" preload="none" src="OALD10_pron_starts__gb_1.mp3"></audio><audio id="OALD10_pron_starts__us_1" preload="none" src="OALD10_pron_starts__us_1.mp3"></audio><audio id="OALD10_pron_started__gb_1" preload="none" src="OALD10_pron_started__gb_1.mp3"></audio><audio id="OALD10_pron_started__us_1" preload="none" src="OALD10_pron_started__us_1.mp3"></audio><audio id="OALD10_pron_starting__gb_1" preload="none" src="OALD10_pron_starting__gb_1.mp3"></audio><audio id="OALD10_pron_starting__us_1" preload="none" src="OALD10_pron_starting__us_1.mp3"></audio><audio id="OALD10_pron_start_1_us_1" preload="none" src="OALD10_pron_start_1_us_1.mp3"></audio>`;
