var custom_content = `<meta charset='utf-8'/><html><head></head><body><div class="oald" id="entryContent"><div class="entry" hclass="entry" hlength="5" htag="section" id="state_1" idm_id="000057074" sk="state: :0" sum="3270"><div class="top-container"><div class="top-g" id="state_topg_2"><div class="webtop"><h1 class="headword" hclass="headword" htag="h1" id="state_h_1" opal_spoken="y" opal_written="y" ox3000="y" random="y">state</h1> <span class="pos" hclass="pos" htag="span">noun</span><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a2"><span class="ox3ksym_a2"> </span></a><a href="https://www.oxfordlearnersdictionaries.com/wordlists/opal?dataset=english&amp;list=opal_written&amp;level=sublist_1"><span class="opal_symbol" href="OPAL_Written::Sublist_1">OPAL W</span></a><a href="https://www.oxfordlearnersdictionaries.com/wordlists/opal?dataset=english&amp;list=opal_spoken&amp;level=sublist_1"><span class="opal_symbol" href="OPAL_Spoken::Sublist_1">OPAL S</span></a></div><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="state"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_state__gb_2').play();" style="cursor: pointer" title="state pronunciationEnglish" valign="top"></a><span class="phon">/steɪt/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="state"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_state__us_1').play();" style="cursor: pointer" title="state pronunciationAmerican" valign="top"></a><span class="phon">/steɪt/</span></div></span><span class="jumplinks"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/state#state_idmgs_1" title="Idioms definition"><span class="jumplink" href="state_idmgs_1" name="Idioms">Idioms</span></a> </span></div></div></div><ol class="senses_multiple" htag="ol"><span class="shcut-g" id="state_sngs_2"> <h2 class="shcut" hclass="shcut" htag="h2" id="state_shcut_2">country</h2><li cefr="a2" class="sense" hclass="sense" htag="li" id="state_sng_2" ox3000="y" sensenum="1"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a2"><span class="ox3ksym_a2"> </span></a></div></span> <div class="variants" hclass="variants" htag="div" type="vs">(also <span class="v-g"><span class="v">State</span></span>)</div> <span class="grammar" hclass="grammar" htag="span">[countable]</span> <span class="def" hclass="def" htag="span">a country considered as an organized political community controlled by one government</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__1"><span class="x">the Baltic States</span></li><li class="" htag="li" id="OALD10_state__2"><span class="x">European Union <span class="cl">member states</span></span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="state3_unbox_1" unbox="which_word"><span class="box_title" onclick="toggle_active(this)">Which Word? <span class="closed">country / state</span></span><span class="body"><span class="unbox">country / state</span><ul class="bullet"><li class="li"><span class="eb">Country</span> is the most usual, neutral word for a geographical area that has or used to have its own government.</li><li class="li"><span class="eb">State</span> emphasizes the political organization of an area under an independent government. Especially in <span class="labels" hclass="labels" htag="span">British English</span>, it can also mean the government:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__3"><span class="unx">the member states of the EU</span></li><li class="" htag="li" id="OALD10_state__4"><span class="unx">The state provides free education.</span></li></ul> In <span class="labels" hclass="labels" htag="span">North American English</span> <span class="eb">the state</span> usually refers to one of the 50 states of the US, not to the government of the country as a whole.</li></ul></span></span></div> <span class="xrefs" hclass="xrefs" htag="span" xt="see"><span class="prefix">see also</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/chief-of-state" title="chief of state definition"><span bord="n" class="xr-g" href="chiefofstate_e"><span class="xh">chief of state</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/city-state" title="city state definition"><span bord="n" class="xr-g" href="citystate_e"><span class="xh">city state</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/client-state" title="client state definition"><span bord="y" class="xr-g" href="clientstate_e"><span class="xh">client state</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/failed-state" title="failed state definition"><span bord="n" class="xr-g" href="failedstate_e"><span class="xh">failed state</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/gulf-states#gulfstates_sng_1" title="Gulf States (1) definition"><span bord="n" class="xr-g" href="gulfstates_sng_1"><span class="xh">Gulf States</span> <span class="xs"><span class="wrap">(</span>1<span class="wrap">)</span></span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/head-of-state" title="head of state definition"><span bord="n" class="xr-g" href="headofstate_e"><span class="xh">head of state</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/nation-state" title="nation state definition"><span bord="n" class="xr-g" href="nationstate_e"><span class="xh">nation state</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/police-state" title="police state definition"><span bord="n" class="xr-g" href="policestate_e"><span class="xh">police state</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/welfare-state" title="welfare state definition"><span bord="n" class="xr-g" href="welfarestate_e"><span class="xh">welfare state</span></span></a></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="state3_unbox_2" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__5"><span class="unx">In 1949 China became a communist state.</span></li><li class="" htag="li" id="OALD10_state__6"><span class="unx">member states of the European Union</span></li><li class="" htag="li" id="OALD10_state__7"><span class="unx">rogue states that shelter terrorists</span></li><li class="" htag="li" id="OALD10_state__8"><span class="unx">Delegates from a number of Arab states attended the meeting.</span></li><li class="" htag="li" id="OALD10_state__9"><span class="unx">It has not yet been recognized as an independent sovereign state.</span></li><li class="" htag="li" id="OALD10_state__10"><span class="unx">Many Third World countries are one-party states of one type or another.</span></li><li class="" htag="li" id="OALD10_state__11"><span class="unx">The action was opposed by several UN member states.</span></li><li class="" htag="li" id="OALD10_state__12"><span class="unx">a newly independent state</span></li><li class="" htag="li" id="OALD10_state__13"><span class="unx">relations between the two states</span></li></ul></span></div><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/politics?level=a2" title="Topic politics"><span class="topic" href="l2:politics_and_society:politics?cefr=a2"><span class="topic_name">Politics</span><span class="topic_cefr">a2</span></span></a></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="state3_unbox_3" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adjective</span><ul class="collocs_list"><li class="li">city</li><li class="li">nation</li><li class="li">independent</li><li class="li">…</li></ul><span class="unbox">verb + state</span><ul class="collocs_list"><li class="li">create</li><li class="li">establish</li><li class="li">become</li><li class="li">…</li></ul><span class="unbox">state + noun</span><ul class="collocs_list"><li class="li">enterprise</li><li class="li">monopoly</li><li class="li">control</li><li class="li">…</li></ul><span class="unbox">phrases</span><ul class="collocs_list"><li class="li">affairs of state</li><li class="li">matters of state</li><li class="li">church and state</li><li class="li">…</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/state_1" title=" definition in Collocations">full entry</a></span></span></span></div> </li></span><span class="shcut-g" id="state_sngs_3"> <h2 class="shcut" hclass="shcut" htag="h2" id="state_shcut_3">part of country</h2><li cefr="a2" class="sense" hclass="sense" htag="li" id="state_sng_3" ox3000="y" sensenum="2"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a2"><span class="ox3ksym_a2"> </span></a></div></span> <div class="variants" hclass="variants" htag="div" type="vs">(also <span class="v-g"><span class="v">State</span></span>)</div> <span class="grammar" hclass="grammar" htag="span">[countable]</span> <div class="variants" hclass="variants" htag="div" type="ab">(abbreviation <span class="v-g"><span class="v">St.</span></span>)</div> <span class="def" hclass="def" htag="span">an organized political community forming part of a country</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__14"><span class="x">the states of Victoria and Western Australia</span></li><li class="" htag="li" id="OALD10_state__15"><span class="x">The hurricane swept across the southern states of the US.</span></li><li class="" htag="li" id="OALD10_state__16"><span class="x">Both candidates have been campaigning in key battleground states.</span></li><li class="" htag="li" id="OALD10_state__17"><span class="x">She decided to return to her <span class="cl">home state</span> of Ohio.</span></li></ul> <span class="xrefs" hclass="xrefs" htag="span" xt="see"><span class="prefix">see also</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/gulf-states#gulfstates_sng_2" title="Gulf States (2) definition"><span bord="n" class="xr-g" href="gulfstates_sng_2"><span class="xh">Gulf States</span> <span class="xs"><span class="wrap">(</span>2<span class="wrap">)</span></span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/slave-state" title="slave state definition"><span bord="n" class="xr-g" href="slavestate_ge"><span class="xh">slave state</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/swing-state" title="swing state definition"><span bord="n" class="xr-g" href="swingstate_e"><span class="xh">swing state</span></span></a></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="state3_unbox_4" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__18"><span class="unx">A handful of states have introduced similar legislation.</span></li><li class="" htag="li" id="OALD10_state__19"><span class="unx">George W. Bush won the state of Florida.</span></li><li class="" htag="li" id="OALD10_state__20"><span class="unx">He roamed throughout the state.</span></li><li class="" htag="li" id="OALD10_state__21"><span class="unx">I was a correctional officer out of Utah state prison.</span></li><li class="" htag="li" id="OALD10_state__22"><span class="unx">It can be a felony to transport wine across state lines.</span></li><li class="" htag="li" id="OALD10_state__23"><span class="unx">Most states require all drivers to carry a minimum level of insurance.</span></li><li class="" htag="li" id="OALD10_state__24"><span class="unx">She was selected to represent the state of Texas at the conference.</span></li><li class="" htag="li" id="OALD10_state__25"><span class="unx">Thirty states have passed similar legislation.</span></li><li class="" htag="li" id="OALD10_state__26"><span class="unx">The legislation must work at both the federal and state levels.</span></li><li class="" htag="li" id="OALD10_state__27"><span class="unx">a farm tucked into the far north-west corner of the state</span></li><li class="" htag="li" id="OALD10_state__28"><span class="unx">opposition to the Civil War in Kansas and the other border states</span></li><li class="" htag="li" id="OALD10_state__29"><span class="unx">red states for Republicans</span></li><li class="" htag="li" id="OALD10_state__30"><span class="unx">The Murray River is the boundary between the states of Victoria and New South Wales.</span></li><li class="" htag="li" id="OALD10_state__31"><span class="unx">These data may have importance at the state or national level.</span></li><li class="" htag="li" id="OALD10_state__32"><span class="unx">There are threats of power cuts across the state.</span></li><li class="" htag="li" id="OALD10_state__33"><span class="unx">State legislatures around the country debated these labeling laws.</span></li><li class="" htag="li" id="OALD10_state__34"><span class="unx">State law requires directors to be elected by a majority.</span></li><li class="" htag="li" id="OALD10_state__35"><span class="unx">state budget cuts</span></li></ul></span></div><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/politics?level=a2" title="Topic politics"><span class="topic" href="l2:politics_and_society:politics?cefr=a2"><span class="topic_name">Politics</span><span class="topic_cefr">a2</span></span></a></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="state3_unbox_5" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adjective</span><ul class="collocs_list"><li class="li">battleground</li><li class="li">swing</li><li class="li">blue</li><li class="li">…</li></ul><span class="unbox">… of states</span><ul class="collocs_list"><li class="li">handful</li></ul><span class="unbox">verb + state</span><ul class="collocs_list"><li class="li">represent</li><li class="li">win</li></ul><span class="unbox">state + verb</span><ul class="collocs_list"><li class="li">allow something</li><li class="li">require something</li><li class="li">pass something</li><li class="li">…</li></ul><span class="unbox">state + noun</span><ul class="collocs_list"><li class="li">capital</li><li class="li">line</li><li class="li">court</li><li class="li">…</li></ul><span class="unbox">preposition</span><ul class="collocs_list"><li class="li">across the state</li><li class="li">throughout the state</li></ul><span class="unbox">phrases</span><ul class="collocs_list"><li class="li">at the state level</li><li class="li">on the state level</li><li class="li">a corner of the state</li><li class="li">…</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/state_1" title=" definition in Collocations">full entry</a></span></span></span></div> <div class=""> <div class="am-default contentslot" id="ad_contentslot_1"></div></div> </li></span><span class="shcut-g" id="state_sngs_4"> <h2 class="shcut" hclass="shcut" htag="h2" id="state_shcut_4">government</h2><li cefr="b1" class="sense" hclass="sense" htag="li" id="state_sng_4" ox3000="y" sensenum="3"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=b1"><span class="ox3ksym_b1"> </span></a></div></span> <div class="variants" hclass="variants" htag="div" type="vs">(also <span class="v-g"><span class="v">the State</span></span>)</div> <span class="grammar" hclass="grammar" htag="span">[uncountable, singular]</span> <span class="def" hclass="def" htag="span">the government of a country</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__36"><span class="x">matters/affairs of state</span></li><li class="" htag="li" id="OALD10_state__37"><span class="x">people who are financially dependent on the state</span></li><li class="" htag="li" id="OALD10_state__38"><span class="x">a state-owned company</span></li><li class="" htag="li" id="OALD10_state__39"><span class="x">They wish to limit the power of the State.</span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="state3_unbox_6" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__40"><span class="unx">The president's wife is said to have a powerful hand in affairs of state.</span></li><li class="" htag="li" id="OALD10_state__41"><span class="unx">the separation of church and state</span></li></ul></span></div><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/politics?level=b1" title="Topic politics"><span class="topic" href="l2:politics_and_society:politics?cefr=b1"><span class="topic_name">Politics</span><span class="topic_cefr">b1</span></span></a></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="state3_unbox_7" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adjective</span><ul class="collocs_list"><li class="li">city</li><li class="li">nation</li><li class="li">independent</li><li class="li">…</li></ul><span class="unbox">verb + state</span><ul class="collocs_list"><li class="li">create</li><li class="li">establish</li><li class="li">become</li><li class="li">…</li></ul><span class="unbox">state + noun</span><ul class="collocs_list"><li class="li">enterprise</li><li class="li">monopoly</li><li class="li">control</li><li class="li">…</li></ul><span class="unbox">phrases</span><ul class="collocs_list"><li class="li">affairs of state</li><li class="li">matters of state</li><li class="li">church and state</li><li class="li">…</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/state_1" title=" definition in Collocations">full entry</a></span></span></span></div> </li></span><span class="shcut-g" id="state_sngs_1"> <h2 class="shcut" hclass="shcut" htag="h2" id="state_shcut_1">condition of somebody/something</h2><li cefr="b2" class="sense" hclass="sense" htag="li" id="state_sng_1" ox3000="y" sensenum="4"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=b2"><span class="ox3ksym_b2"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[countable]</span> <span class="def" hclass="def" htag="span">the mental, emotional or physical condition that a person or thing is in</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__42"><span class="x">a confused <span class="cl">mental state</span></span></li><li class="" htag="li" id="OALD10_state__43"> <span class="cf" hclass="cf" htag="span">state of something</span> <span class="x">a confused <span class="cl">state of mind</span></span></li><li class="" htag="li" id="OALD10_state__44"><span class="x">anxieties about the state of the country’s economy</span></li><li class="" htag="li" id="OALD10_state__45"><span class="x">The government has <span class="cl">declared a state of emergency</span> in the flooded regions.</span></li><li class="" htag="li" id="OALD10_state__46"><span class="x">Given the <span class="cl">current state</span> of knowledge, it will take years to find a solution.</span></li><li class="" htag="li" id="OALD10_state__47"><span class="x">A report condemned the state of prisoners' cells.</span></li><li class="" htag="li" id="OALD10_state__48"> <span class="cf" hclass="cf" htag="span">in a… state</span> <span class="x">water in a liquid state</span></li><li class="" htag="li" id="OALD10_state__49"><span class="x">He was in a terrible state by the time he got home.</span></li><li class="" htag="li" id="OALD10_state__50"> <span class="cf" hclass="cf" htag="span">in a… state of something</span> <span class="x">She was in a <span class="cl">state of shock</span>.</span></li><li class="" htag="li" id="OALD10_state__51"><span class="x">The building is in a bad <span class="cl">state of repair</span> <span class="gloss" hclass="gloss" htag="span">(= needs to be repaired)</span>.</span></li><li class="" htag="li" id="OALD10_state__52"><span class="x">He was in a state of permanent depression.</span></li><li class="" htag="li" id="OALD10_state__53"> <span class="cf" hclass="cf" htag="span">in a … state to do something</span> <span class="x">You're <span class="cl">not in a fit state</span> to drive.</span></li><li class="" htag="li" id="OALD10_state__54"> <span class="labels" hclass="labels" htag="span">(British English, informal)</span> <span class="x">Look at the state of you! You can't go out looking like that.</span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="state3_unbox_8" unbox="which_word"><span class="box_title" onclick="toggle_active(this)">Which Word? <span class="closed">condition / state</span></span><span class="body"><span class="unbox">condition / state</span><span class="p">The following adjectives are frequently used with these nouns:</span><table class="table"><tbody><tr class="tr"><th class="th" display="table-cell">~ condition</th><th class="th" display="table-cell">~ state</th></tr><tr class="tr"><td class="td" display="table-cell">good</td><td class="td" display="table-cell">present</td></tr><tr class="tr"><td class="td" display="table-cell">excellent</td><td class="td" display="table-cell">current</td></tr><tr class="tr"><td class="td" display="table-cell">physical</td><td class="td" display="table-cell">mental</td></tr><tr class="tr"><td class="td" display="table-cell">poor</td><td class="td" display="table-cell">no</td></tr><tr class="tr"><td class="td" display="table-cell">human</td><td class="td" display="table-cell">emotional</td></tr><tr class="tr"><td class="td" display="table-cell">perfect</td><td class="td" display="table-cell">physical</td></tr><tr class="tr"><td class="td" display="table-cell">no</td><td class="td" display="table-cell">natural</td></tr><tr class="tr"><td class="td" display="table-cell">better</td><td class="td" display="table-cell">original</td></tr></tbody></table><ul class="bullet"><li class="li"><span class="eb">State</span> is a more general word than <span class="eb">condition</span> and is used for the condition that something is in at a particular time. It can be used without an adjective:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__55"><span class="unx">the present state of medical knowledge</span></li><li class="" htag="li" id="OALD10_state__56"><span class="unx">We’re worried about his mental state.</span></li><li class="" htag="li" id="OALD10_state__57"><span class="unx">What a state this room is in <span class="gloss" hclass="gloss" htag="span">(= very bad)</span>.</span></li></ul></li><li class="li"><span class="eb">Condition</span> is used with an adjective and refers especially to the appearance, quality or working order of somebody or something:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__58"><span class="unx">The car is in excellent condition.</span></li></ul></li></ul></span></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="state3_unbox_9" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__59"><span class="unx">Babies can cry themselves into a state of frenzy.</span></li><li class="" htag="li" id="OALD10_state__60"><span class="unx">He goes into a trance-like state when he plays the guitar.</span></li><li class="" htag="li" id="OALD10_state__61"><span class="unx">He's concerned about his mother's state of health.</span></li><li class="" htag="li" id="OALD10_state__62"><span class="unx">He's not in a fit enough mental state to drive.</span></li><li class="" htag="li" id="OALD10_state__63"><span class="unx">His life seems to be in a constant state of chaos.</span></li><li class="" htag="li" id="OALD10_state__64"><span class="unx">Public speaking can produce a state of mind akin to panic.</span></li><li class="" htag="li" id="OALD10_state__65"><span class="unx">She continues to live in a state of denial.</span></li><li class="" htag="li" id="OALD10_state__66"><span class="unx">She managed to get the company's finances into a healthy state.</span></li><li class="" htag="li" id="OALD10_state__67"><span class="unx">She slipped into a state of unconsciousness.</span></li><li class="" htag="li" id="OALD10_state__68"><span class="unx">She uses music to create a particular emotional state.</span></li><li class="" htag="li" id="OALD10_state__69"><span class="unx">The brothers' refusal to work together had brought about this sad state of affairs.</span></li><li class="" htag="li" id="OALD10_state__70"><span class="unx">The country is in a perpetual state of anarchy.</span></li><li class="" htag="li" id="OALD10_state__71"><span class="unx">The driver was just sitting there in a state of shock.</span></li><li class="" htag="li" id="OALD10_state__72"><span class="unx">The education system is still in a state of flux following the recent reform.</span></li><li class="" htag="li" id="OALD10_state__73"><span class="unx">The house was in a poor state of repair when we bought it.</span></li><li class="" htag="li" id="OALD10_state__74"><span class="unx">The inspectors assess the physical state of schools and equipment.</span></li><li class="" htag="li" id="OALD10_state__75"><span class="unx">His teeth were in an appalling state.</span></li><li class="" htag="li" id="OALD10_state__76"><span class="unx">The soul continues to be reborn until it has reached a state of perfection.</span></li><li class="" htag="li" id="OALD10_state__77"><span class="unx">We were shocked at the run-down state of the hospital.</span></li><li class="" htag="li" id="OALD10_state__78"><span class="unx">You can either varnish the wood or leave it in its natural state.</span></li><li class="" htag="li" id="OALD10_state__79"><span class="unx">a drug that produces altered states of consciousness</span></li><li class="" htag="li" id="OALD10_state__80"><span class="unx">an advanced state of dehydration</span></li><li class="" htag="li" id="OALD10_state__81"><span class="unx">Look at the state of this room!</span></li><li class="" htag="li" id="OALD10_state__82"><span class="unx">The business is in a pretty good financial state.</span></li><li class="" htag="li" id="OALD10_state__83"><span class="unx">There are serious concerns about the current state of the economy.</span></li><li class="" htag="li" id="OALD10_state__84"><span class="unx">We would prefer to keep the garden in its natural state.</span></li><li class="" htag="li" id="OALD10_state__85"><span class="unx">What kind of state are the roads in?</span></li><li class="" htag="li" id="OALD10_state__86"><span class="unx">Distilled water is produced by condensing steam from boiled water back into its liquid state.</span></li><li class="" htag="li" id="OALD10_state__87"><span class="unx">I cannot afford it, given the present state of our finances.</span></li><li class="" htag="li" id="OALD10_state__88"><span class="unx">The 18th-century farmhouse is in a state of disrepair. </span></li><li class="" htag="li" id="OALD10_state__89"><span class="unx">This work is an artistic statement of all that I feel about the state of the world.</span></li></ul></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="state3_unbox_10" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adjective</span><ul class="collocs_list"><li class="li">acceptable</li><li class="li">fit</li><li class="li">good</li><li class="li">…</li></ul><span class="unbox">verb + state</span><ul class="collocs_list"><li class="li">get into</li><li class="li">go into</li><li class="li">reach</li><li class="li">…</li></ul><span class="unbox">preposition</span><ul class="collocs_list"><li class="li">in a state</li><li class="li">into a state</li><li class="li">state of</li><li class="li">…</li></ul><span class="unbox">phrases</span><ul class="collocs_list"><li class="li">be in a good, bad, etc. state of repair</li><li class="li">given the state of something</li><li class="li">a state of affairs</li><li class="li">…</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/state_1" title=" definition in Collocations">full entry</a></span></span></span></div> </li></span><span class="shcut-g" id="state_sngs_5"> <h2 class="shcut" hclass="shcut" htag="h2" id="state_shcut_5">official ceremony</h2><li class="sense" hclass="sense" htag="li" id="state_sng_5" sensenum="5"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[uncountable]</span></span> <span class="def" hclass="def" htag="span">the formal ceremonies connected with high levels of government or with kings and queens</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__90"> <span class="cf" hclass="cf" htag="span">in state</span> <span class="x">The president was driven in state through the streets.</span></li></ul> <span class="xrefs" hclass="xrefs" htag="span" xt="see"><span class="prefix">see also</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/lying-in-state" title="lying-in-state definition"><span bord="n" class="xr-g" href="lyinginstate_e"><span class="xh">lying-in-state</span></span></a></span> </li></span><span class="shcut-g" id="state_sngs_6"> <h2 class="shcut" hclass="shcut" htag="h2" id="state_shcut_6">the US</h2><li class="sense" hclass="sense" htag="li" id="state_sng_6" sensenum="6"><span class="sensetop" hclass="sensetop" htag="span"><div class="variants" hclass="variants" htag="div" type="alt"><span class="v-g"><span class="v">the States</span></span></div></span> <span class="grammar" hclass="grammar" htag="span">[plural]</span> <span class="labels" hclass="labels" htag="span">(informal)</span> <span class="def" hclass="def" htag="span">the United States of America</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__91"><span class="x">I've never been to the States.</span></li></ul> </li></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="state3_unbox_11" unbox="wordorigin"><span class="box_title" onclick="toggle_active(this)">Word Origin</span><span class="body"><span class="p">Middle English (as a noun): partly a shortening of <span class="eb">estate</span>, partly from Latin <span class="ei">status</span> ‘manner of standing, condition’, from <span class="ei">stare</span> ‘to stand’. The current verb senses date from the mid 17th cent.</span></span></span></div></ol><div class="idioms" hclass="idioms" htag="div" id="state_idmgs_1"><span class="idioms_heading">Idioms</span> <span class="idm-g" id="state_idmg_1" sk="beinintostate"><div class="top-container"><div class="top-g" id="state_topg_3"><div class="webtop"><span class="idm" id="state_idm_1">be in/get into a state</span> <span class="labels" hclass="labels" htag="span">(informal, especially British English)</span></div></div></div><ol class="senses_multiple" htag="ol"><li class="sense" hclass="sense" htag="li" id="state_sng_7" sensenum="1"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to be/become excited or anxious</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__92"><span class="x">She was in a real state about her exams.</span></li><li class="" htag="li" id="OALD10_state__93"><span class="x">He made a mistake and got into a terrible state about it.</span></li><li class="" htag="li" id="OALD10_state__94"><span class="x">Jane was in a terrible state after losing her job.</span></li></ul> <div class=""> <div class="am-default contentslot" id="ad_contentslot_2"></div></div> </li><li class="sense" hclass="sense" htag="li" id="state_sng_8" sensenum="2"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to be dirty or untidy</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__95"><span class="x">What a state this place is in!</span></li><li class="" htag="li" id="OALD10_state__96"><span class="x">How did the carpet get into that state?</span></li></ul> </li></ol></span> <span bord="y" class="idm-g" id="state_idmg_2" sk="instateofgrace"><div class="top-container"><div class="top-g" id="state_topg_1"><div class="webtop"><span class="idm" id="state_idm_5">in a state of grace</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="state_sng_9"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">(in the Roman Catholic Church) having been forgiven by God for the wrong or evil things you have done</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__97"><span class="x">He died in a state of grace.</span></li></ul> </li></ol></span> <span class="idm-g" id="state_idmg_3" sk="stateofaffairs"><div class="top-container"><div class="top-g" id="state_topg_9"><div class="webtop"><span class="idm" id="state_idm_6">a state of affairs</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="state_sng_10"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">a situation</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__98"><span class="x">This state of affairs can no longer be ignored.</span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="state3_unbox_12" unbox="synonyms"><span class="box_title" onclick="toggle_active(this)">Synonyms <span class="closed">situation</span></span><span class="body"><span class="unbox">situation</span><ul class="inline"><li class="li">circumstances</li><li class="li">position</li><li class="li">conditions</li><li class="li">things</li><li class="li">the case</li><li class="li">state of affairs</li></ul><span class="p">These are all words for the conditions and facts that are connected with and affect the way things are.</span><ul class="deflist"><li class="li"><span class="dt">situation</span> <span class="dd">all the things that are happening at a particular time and in a particular place:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__99"><span class="unx">the present economic situation</span></li></ul></span></li><li class="li"><span class="dt">circumstances</span> <span class="dd">the facts that are connected with and affect a situation, an event or an action; the conditions of a person’s life, especially the money they have:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__100"><span class="unx">The ship sank in mysterious circumstances.</span></li></ul></span></li><li class="li"><span class="dt">position</span> <span class="dd">the situation that somebody is in, especially when it affects what they can and cannot do:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__101"><span class="unx">She felt she was in a position of power.</span></li></ul></span></li><li class="li"><span class="dt">conditions</span> <span class="dd">the circumstances in which people live, work or do things; the physical situation that affects how something happens:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__102"><span class="unx">We were forced to work outside in freezing conditions.</span></li></ul></span></li></ul> <span class="un"><span class="h4">circumstances or conditions?</span><span class="p"><span class="eb">Circumstances</span> refers to somebody’s financial situation; <span class="eb">conditions</span> are things such as the quality and amount of food or shelter they have. The <span class="eb">circumstances</span> that affect an event are the facts surrounding it; the <span class="eb">conditions</span> that affect it are usually physical ones, such as the weather.</span></span><ul class="deflist"><li class="li"><span class="dt">things</span> <span class="dd">(<span class="ei">rather informal</span>) the general situation, as it affects somebody:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__103"><span class="unx">Hi, Jane! <span class="eb">How are things?</span></span></li><li class="" htag="li" id="OALD10_state__104"><span class="unx">Think things over before you decide.</span></li></ul></span></li><li class="li"><span class="dt">the case</span> <span class="dd">the true situation:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__105"><span class="unx">If that is the case <span class="gloss" hclass="gloss" htag="span">(= if the situation described is true)</span>, we need more staff.</span></li></ul></span></li><li class="li"><span class="dt">state of affairs</span> <span class="dd">a situation:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__106"><span class="unx">How did this unhappy state of affairs come about?</span></li></ul></span></li></ul> <span class="un"><span class="h4">situation or state of affairs?</span><span class="p"><span class="eb">State of affairs</span> is mostly used with <span class="ei">this</span>. It is also used with adjectives describing how good or bad a situation is, such as <span class="ei">happy</span>, <span class="ei">sorry</span>, <span class="ei">shocking</span>, <span class="ei">sad</span> and <span class="ei">unhappy</span>, as well as those relating to time, such as <span class="ei">present</span> and <span class="ei">current</span>. <span class="eb">Situation</span> is much more frequent and is used in a wider variety of contexts.</span></span><span class="patterns">Patterns</span><ul class="bullet"><li class="li"><span class="eb">in</span> (a) particular situation/​circumstances/​position/​state of affairs</li><li class="li">the/​somebody’s <span class="eb">economic/​financial/​social</span> situation/​circumstances/​position/​conditions</li><li class="li">(a/​an) <span class="eb">happy/​unhappy</span> situation/​circumstances/​position/​state of affairs</li><li class="li">to <span class="eb">look at/​review</span> the situation/​the circumstances/​the conditions/​things</li></ul></span></span></div> </li></ol></span> <span class="idm-g" id="state_idmg_4" sk="stateofplay"><div class="top-container"><div class="top-g" id="state_topg_10"><div class="webtop"><span class="idm" id="state_idm_7">the state of play</span> </div></div></div><ol class="senses_multiple" htag="ol"><li class="sense" hclass="sense" htag="li" id="state_sng_11" sensenum="1"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">the stage that has been reached in a process, etc. which has not yet been completed</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__107"><span class="x">What is the current state of play in the peace talks?</span></li></ul> </li><li class="sense" hclass="sense" htag="li" id="state_sng_12" sensenum="2"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(especially British English)</span></span> <span class="def" hclass="def" htag="span">the score in a sports match, especially in <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/cricket" title="cricket definition"><span class="ndv">cricket</span></a></span> </li></ol></span></div></div><div class="entry" hclass="entry" hlength="5" htag="section" id="state_2" idm_id="000057075" sk="state::2" sum="974"><div class="top-container"><div class="top-g" id="state_topg_7"><div class="webtop"><h1 class="headword" hclass="headword" htag="h1" id="state_h_2" ox3000="y" random="y">state</h1> <span class="pos" hclass="pos" htag="span">adjective</span><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=b1"><span class="ox3ksym_b1"> </span></a></div><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="state"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_state__gb_2').play();" style="cursor: pointer" title="state pronunciationEnglish" valign="top"></a><span class="phon">/steɪt/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="state"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_state__us_1').play();" style="cursor: pointer" title="state pronunciationAmerican" valign="top"></a><span class="phon">/steɪt/</span></div></span><div class="variants" hclass="variants" htag="div" id="state_vgs_6" type="vf">(also <span class="v-g" id="state_vg_6"><span class="v" id="state_v_6">State</span></span>)</div> <span class="grammar" hclass="grammar" htag="span">[only before noun]</span></div></div></div><ol class="senses_multiple" htag="ol"><span class="shcut-g" id="state_sngs_11"> <h2 class="shcut" hclass="shcut" htag="h2" id="state_shcut_7">government</h2><li cefr="b1" class="sense" hclass="sense" htag="li" id="state_sng_13" ox3000="y" sensenum="1"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=b1"><span class="ox3ksym_b1"> </span></a></div></span> <span class="def" hclass="def" htag="span">provided or controlled by the government of a country</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__108"><span class="x"><span class="cl">state officials/agencies</span></span></li><li class="" htag="li" id="OALD10_state__109"><span class="x">state education</span></li><li class="" htag="li" id="OALD10_state__110"><span class="x">families dependent on state benefits <span class="gloss" hclass="gloss" htag="span">(= in Britain, money given by the government to people who are poor)</span></span></li><li class="" htag="li" id="OALD10_state__111"><span class="x">state secrets <span class="gloss" hclass="gloss" htag="span">(= information that could be harmful to a country if it were discovered by an enemy)</span></span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="state4_unbox_1" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__112"><span class="unx">The law applies only to schools within the state system.</span></li><li class="" htag="li" id="OALD10_state__113"><span class="unx">Charities are required to register with a state agency.</span></li><li class="" htag="li" id="OALD10_state__114"><span class="unx">Every citizen could buy shares in privatized state property.</span></li><li class="" htag="li" id="OALD10_state__115"><span class="unx">He was shot for passing state secrets to foreign powers.</span></li><li class="" htag="li" id="OALD10_state__116"><span class="unx">Some prefer tax cuts to greater state spending on health and social services.</span></li><li class="" htag="li" id="OALD10_state__117"><span class="unx">Teachers in the state sector are asking for a 7% pay rise.</span></li><li class="" htag="li" id="OALD10_state__118"><span class="unx">The state pension is barely enough to live on.</span></li><li class="" htag="li" id="OALD10_state__119"><span class="unx">The telephone network is still under state control.</span></li><li class="" htag="li" id="OALD10_state__120"><span class="unx">large-scale state intervention in industry</span></li><li class="" htag="li" id="OALD10_state__121"><span class="unx">the legitimate exercise of state power</span></li><li class="" htag="li" id="OALD10_state__122"><span class="unx">schools outside the state system</span></li><li class="" htag="li" id="OALD10_state__123"><span class="unx">unemployed people living on state benefits</span></li><li class="" htag="li" id="OALD10_state__124"><span class="unx">A change in state funding will require the museum to pay for the restoration.</span></li><li class="" htag="li" id="OALD10_state__125"><span class="unx">The film was shown on state television. </span></li><li class="" htag="li" id="OALD10_state__126"><span class="unx">Chinese state media has condemned the attack.</span></li></ul></span></div><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/politics?level=b1" title="Topic politics"><span class="topic" href="l2:politics_and_society:politics?cefr=b1"><span class="topic_name">Politics</span><span class="topic_cefr">b1</span></span></a></span> </li></span><span class="shcut-g" id="state_sngs_13"> <h2 class="shcut" hclass="shcut" htag="h2" id="state_shcut_9">part of country</h2><li cefr="b1" class="sense" hclass="sense" htag="li" id="state_sng_15" ox3000="y" sensenum="2"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=b1"><span class="ox3ksym_b1"> </span></a></div></span> <span class="def" hclass="def" htag="span">provided or controlled by a particular state of a country, especially in the US</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__127"><span class="x">the <span class="cl">state government/legislature</span></span></li><li class="" htag="li" id="OALD10_state__128"><span class="x">California <span class="cl">state law</span></span></li><li class="" htag="li" id="OALD10_state__129"><span class="x">a state prison/hospital/university</span></li><li class="" htag="li" id="OALD10_state__130"><span class="x">state police/troopers</span></li><li class="" htag="li" id="OALD10_state__131"><span class="x">a state tax</span></li><li class="" htag="li" id="OALD10_state__132"><span class="x">federal and state government policies</span></li></ul> <div class=""> <div class="am-default contentslot" id="ad_contentslot_1"></div></div> </li></span><span class="shcut-g" id="state_sngs_12"> <h2 class="shcut" hclass="shcut" htag="h2" id="state_shcut_8">official</h2><li cefr="b2" class="sense" hclass="sense" htag="li" id="state_sng_14" sensenum="3"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">connected with the leader of a country attending an official ceremony</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__133"><span class="x">The queen is on a <span class="cl">state visit</span> to Berlin.</span></li><li class="" htag="li" id="OALD10_state__134"><span class="x">the state opening of Parliament</span></li><li class="" htag="li" id="OALD10_state__135"><span class="x">the state apartments <span class="gloss" hclass="gloss" htag="span">(= used for official ceremonies)</span></span></li></ul><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/politics?level=b2" title="Topic politics"><span class="topic" href="l2:politics_and_society:politics?cefr=b2"><span class="topic_name">Politics</span><span class="topic_cefr">b2</span></span></a></span> </li></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" dup="fky" id="state_unbox_5" unbox="wordorigin"><span class="box_title" onclick="toggle_active(this)">Word Origin</span><span class="body"><span class="p">Middle English (as a noun): partly a shortening of <span class="eb">estate</span>, partly from Latin <span class="ei">status</span> ‘manner of standing, condition’, from <span class="ei">stare</span> ‘to stand’. The current verb senses date from the mid 17th cent.</span></span></span></div></ol></div><div class="entry" hclass="entry" hlength="5" htag="section" id="state_3" idm_id="000057076" sk="state::3" sum="1224"><div class="top-container"><div class="top-g" id="state_topg_8"><div class="webtop"><h1 class="headword" hclass="headword" htag="h1" id="state_h_3" opal_spoken="y" opal_written="y" ox3000="y" random="y">state</h1> <span class="pos" hclass="pos" htag="span">verb</span><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=b1"><span class="ox3ksym_b1"> </span></a><a href="https://www.oxfordlearnersdictionaries.com/wordlists/opal?dataset=english&amp;list=opal_written&amp;level=sublist_1"><span class="opal_symbol" href="OPAL_Written::Sublist_1">OPAL W</span></a><a href="https://www.oxfordlearnersdictionaries.com/wordlists/opal?dataset=english&amp;list=opal_spoken&amp;level=sublist_1"><span class="opal_symbol" href="OPAL_Spoken::Sublist_1">OPAL S</span></a></div><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="state"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_state__gb_2').play();" style="cursor: pointer" title="state pronunciationEnglish" valign="top"></a><span class="phon">/steɪt/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="state"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_state__us_1').play();" style="cursor: pointer" title="state pronunciationAmerican" valign="top"></a><span class="phon">/steɪt/</span></div></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="state5_unbox_1" unbox="verbforms"><span class="box_title" onclick="toggle_active(this)">Verb Forms</span><span class="body"><table class="verb_forms_table"><tbody><tr class="verb_form" form="root"><td class="verb_form"> <span class="vf_prefix">present simple I / you / we / they</span> state</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="state"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_state__gb_2').play();" style="cursor: pointer" title="state pronunciationEnglish" valign="top"></a><span class="phon">/steɪt/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="state"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_state__us_1').play();" style="cursor: pointer" title="state pronunciationAmerican" valign="top"></a><span class="phon">/steɪt/</span></div></span></td></tr><tr class="verb_form" form="thirdps"><td class="verb_form"> <span class="vf_prefix">he / she / it</span> states</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="states"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_states__gb_1').play();" style="cursor: pointer" title="states pronunciationEnglish" valign="top"></a><span class="phon">/steɪts/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="states"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_states__us_1').play();" style="cursor: pointer" title="states pronunciationAmerican" valign="top"></a><span class="phon">/steɪts/</span></div></span></td></tr><tr class="verb_form" form="past"><td class="verb_form"> <span class="vf_prefix">past simple</span> stated</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="stated"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_stated__gb_1').play();" style="cursor: pointer" title="stated pronunciationEnglish" valign="top"></a><span class="phon">/ˈsteɪtɪd/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="stated"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_stated__us_1').play();" style="cursor: pointer" title="stated pronunciationAmerican" valign="top"></a><span class="phon">/ˈsteɪtɪd/</span></div></span></td></tr><tr class="verb_form" form="pastpart"><td class="verb_form"> <span class="vf_prefix">past participle</span> stated</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="stated"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_stated__gb_1').play();" style="cursor: pointer" title="stated pronunciationEnglish" valign="top"></a><span class="phon">/ˈsteɪtɪd/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="stated"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_stated__us_1').play();" style="cursor: pointer" title="stated pronunciationAmerican" valign="top"></a><span class="phon">/ˈsteɪtɪd/</span></div></span></td></tr><tr class="verb_form" form="prespart"><td class="verb_form"> <span class="vf_prefix">-ing form</span> stating</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="stating"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_stating__gb_1').play();" style="cursor: pointer" title="stating pronunciationEnglish" valign="top"></a><span class="phon">/ˈsteɪtɪŋ/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="stating"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_stating__us_1').play();" style="cursor: pointer" title="stating pronunciationAmerican" valign="top"></a><span class="phon">/ˈsteɪtɪŋ/</span></div></span></td></tr></tbody></table></span></span></div></div></div></div><ol class="senses_multiple" htag="ol"><li cefr="b1" class="sense" hclass="sense" htag="li" id="state_sng_16" ox3000="y" sensenum="1"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=b1"><span class="ox3ksym_b1"> </span></a></div></span> <span class="def" hclass="def" htag="span">to formally write or say something, especially in a careful and clear way</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__136"> <span class="cf" hclass="cf" htag="span">state something</span> <span class="x">He has already stated his intention to run for election.</span></li><li class="" htag="li" id="OALD10_state__137"><span class="x">The facts are <span class="cl">clearly stated</span> in the report.</span></li><li class="" htag="li" id="OALD10_state__138"><span class="x">There is no need to <span class="cl">state the obvious</span> <span class="gloss" hclass="gloss" htag="span">(= to say something that everyone already knows)</span>.</span></li><li class="" htag="li" id="OALD10_state__139"> <span class="cf" hclass="cf" htag="span">state how, what, etc…</span> <span class="x">State clearly how many tickets you require.</span></li><li class="" htag="li" id="OALD10_state__140"><span class="x">Each child was asked in turn to state what he or she wanted from the situation.</span></li><li class="" htag="li" id="OALD10_state__141"> <span class="cf" hclass="cf" htag="span">state that…</span> <span class="x">He stated categorically that he knew nothing about the deal.</span></li><li class="" htag="li" id="OALD10_state__142"> <span class="cf" hclass="cf" htag="span">it is stated that…</span> <span class="x">It was stated that standards at the hospital were dropping.</span></li><li class="" htag="li" id="OALD10_state__143"><span class="x">All the photographs in this book, <span class="cl">unless otherwise stated</span>, date from the 1950s.</span></li><li class="" htag="li" id="OALD10_state__144"> <span class="cf" hclass="cf" htag="span">something/somebody is stated to be/have something</span> <span class="x">The contract was stated to be invalid.</span></li><li class="" htag="li" id="OALD10_state__145"><span class="x">The driver is stated to have three previous convictions.</span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="state5_unbox_2" unbox="synonyms"><span class="box_title" onclick="toggle_active(this)">Synonyms <span class="closed">declare</span></span><span class="body"><span class="unbox">declare</span><ul class="inline"><li class="li">state</li><li class="li">indicate</li><li class="li">announce</li></ul><span class="p">These words all mean to say something, usually clearly and definitely and often in public.</span><ul class="deflist"><li class="li"><span class="dt">declare</span> <span class="dd">(<span class="ei">rather formal</span>) to say something officially or publicly; to state something clearly and definitely:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__146"><span class="unx">to declare war</span></li><li class="" htag="li" id="OALD10_state__147"><span class="unx">The painting was declared to be a forgery.</span></li></ul></span></li><li class="li"><span class="dt">state</span> <span class="dd">(<span class="ei">rather formal</span>) to formally write or say something, especially in a careful and clear way:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__148"><span class="unx">He has already stated his intention to run for election.</span></li></ul></span></li><li class="li"><span class="dt">indicate</span> <span class="dd">(<span class="ei">rather formal</span>) to state something, sometimes in a way that is slightly indirect:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__149"><span class="unx">During our meeting, he indicated his willingness to cooperate.</span></li></ul></span></li><li class="li"><span class="dt">announce</span> <span class="dd">to tell people officially about a decision or plans; to give information about something in a public place, especially through a loudspeaker; to say something in a loud and/​or serious way:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__150"><span class="unx">They haven’t formally announced their engagement yet.</span></li><li class="" htag="li" id="OALD10_state__151"><span class="unx">Has our flight been announced yet?</span></li></ul></span></li></ul> <span class="un"><span class="h4">declare or announce?</span><span class="p"><span class="eb">Declare</span> is used more often for giving judgements; <span class="eb">announce</span> is used more often for giving facts: <span class="wx">The painting was announced to be a forgery.</span> • <span class="wx">They haven’t formally declared their engagement yet.</span> </span></span><span class="patterns">Patterns</span><ul class="bullet"><li class="li">to declare/​state/​indicate/​announce <span class="eb">that…</span></li><li class="li">to declare/​state somebody/​something <span class="eb">to be</span> something</li><li class="li">to declare/​state/​indicate/​announce <span class="eb">your intention</span> to do something</li><li class="li">to declare/​state/​announce something <span class="eb">formally/​publicly/​officially</span></li><li class="li">to declare/​state/​announce something <span class="eb">firmly/​confidently</span></li></ul></span></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="state5_unbox_3" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__152"><span class="unx">‘Alcohol doesn't solve problems,’ she stated flatly.</span></li><li class="" htag="li" id="OALD10_state__153"><span class="unx">As previously stated, the phrase ‘value for money’ has an ambiguous meaning.</span></li><li class="" htag="li" id="OALD10_state__154"><span class="unx">At the risk of stating the obvious, people who have not paid cannot be admitted.</span></li><li class="" htag="li" id="OALD10_state__155"><span class="unx">He stated his own views quite openly.</span></li><li class="" htag="li" id="OALD10_state__156"><span class="unx">I am merely stating the facts.</span></li><li class="" htag="li" id="OALD10_state__157"><span class="unx">Let me state at the outset that this report contains little that is new.</span></li><li class="" htag="li" id="OALD10_state__158"><span class="unx">Let me state for the record that my knowledge of wine is almost non-existent.</span></li><li class="" htag="li" id="OALD10_state__159"><span class="unx">She stated categorically that she had no intention of leaving.</span></li><li class="" htag="li" id="OALD10_state__160"><span class="unx">The committee failed to state their reasons for this decision.</span></li><li class="" htag="li" id="OALD10_state__161"><span class="unx">The demands are more easily stated than met.</span></li><li class="" htag="li" id="OALD10_state__162"><span class="unx">The report goes on to state that…</span></li><li class="" htag="li" id="OALD10_state__163"><span class="unx">These facts were nowhere explicitly stated.</span></li><li class="" htag="li" id="OALD10_state__164"><span class="unx">We cannot accept this proposal for the reasons stated above.</span></li><li class="" htag="li" id="OALD10_state__165"><span class="unx">At the meeting, he stated his case for investing.</span></li><li class="" htag="li" id="OALD10_state__166"><span class="unx">No such thing was stated or even implied by the professor.</span></li><li class="" htag="li" id="OALD10_state__167"><span class="unx">The association has publicly stated its support for the proposal. </span></li><li class="" htag="li" id="OALD10_state__168"><span class="unx">Simply stated, our goal is to take the business to new heights.</span></li><li class="" htag="li" id="OALD10_state__169"><span class="unx">The organization refuses to state a clear position for or against.</span></li></ul></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="state5_unbox_4" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adverb</span><ul class="collocs_list"><li class="li">clearly</li><li class="li">plainly</li><li class="li">exactly</li><li class="li">…</li></ul><span class="unbox">verb + state</span><ul class="collocs_list"><li class="li">go on to</li><li class="li">fail to</li><li class="li">let somebody</li><li class="li">…</li></ul><span class="unbox">phrases</span><ul class="collocs_list"><li class="li">as previously stated</li><li class="li">as stated earlier</li><li class="li">as stated previously</li><li class="li">…</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/state_2" title=" definition in Collocations">full entry</a></span></span></span></div> </li><li class="sense" hclass="sense" htag="li" id="state_sng_17" sensenum="2"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[usually passive]</span></span> <span class="cf" hclass="cf" htag="span">state something</span> <span class="def" hclass="def" htag="span">to fix or announce the details of something, especially on a written document</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_state__170"><span class="x">This is not one of their stated aims.</span></li><li class="" htag="li" id="OALD10_state__171"><span class="x">You must arrive at the time stated.</span></li><li class="" htag="li" id="OALD10_state__172"><span class="x">Do not exceed the stated dose <span class="gloss" hclass="gloss" htag="span">(= of medicine)</span>.</span></li></ul> </li><div class="collapse" hclass="collapse" htag="div"><span class="unbox" dup="fky" id="state_unbox_3" unbox="wordorigin"><span class="box_title" onclick="toggle_active(this)">Word Origin</span><span class="body"><span class="p">Middle English (as a noun): partly a shortening of <span class="eb">estate</span>, partly from Latin <span class="ei">status</span> ‘manner of standing, condition’, from <span class="ei">stare</span> ‘to stand’. The current verb senses date from the mid 17th cent.</span></span></span></div></ol></div></div></body></html><audio id="OALD10_pron_state__gb_2" preload="none" src="OALD10_pron_state__gb_2.mp3"></audio><audio id="OALD10_pron_state__us_1" preload="none" src="OALD10_pron_state__us_1.mp3"></audio><audio id="OALD10_pron_states__gb_1" preload="none" src="OALD10_pron_states__gb_1.mp3"></audio><audio id="OALD10_pron_states__us_1" preload="none" src="OALD10_pron_states__us_1.mp3"></audio><audio id="OALD10_pron_stated__gb_1" preload="none" src="OALD10_pron_stated__gb_1.mp3"></audio><audio id="OALD10_pron_stated__us_1" preload="none" src="OALD10_pron_stated__us_1.mp3"></audio><audio id="OALD10_pron_stating__gb_1" preload="none" src="OALD10_pron_stating__gb_1.mp3"></audio><audio id="OALD10_pron_stating__us_1" preload="none" src="OALD10_pron_stating__us_1.mp3"></audio>`;
