var custom_content = `<meta charset='utf-8'/><html><head></head><body><div class="oald" id="entryContent"><div class="entry" hclass="entry" hlength="4" htag="section" id="talk_1" idm_id="000059371" sk="talk: :0" sum="7925"><div class="top-container"><div class="top-g" id="talk_topg_2"><div class="webtop"><h1 class="headword" hclass="headword" htag="h1" id="talk_h_1" opal_spoken="y" ox3000="y">talk</h1> <span class="pos" hclass="pos" htag="span">verb</span><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a1"><span class="ox3ksym_a1"> </span></a><a href="https://www.oxfordlearnersdictionaries.com/wordlists/opal?dataset=english&amp;list=opal_spoken&amp;level=sublist_2"><span class="opal_symbol" href="OPAL_Spoken::Sublist_2">OPAL S</span></a></div><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="talk"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_talk__gb_1').play();" style="cursor: pointer" title="talk pronunciationEnglish" valign="top"></a><span class="phon">/tɔːk/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="talk"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_talk__us_1').play();" style="cursor: pointer" title="talk pronunciationAmerican" valign="top"></a><span class="phon">/tɔːk/</span></div></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="talk2_unbox_1" unbox="verbforms"><span class="box_title" onclick="toggle_active(this)">Verb Forms</span><span class="body"><table class="verb_forms_table"><tbody><tr class="verb_form" form="root"><td class="verb_form"> <span class="vf_prefix">present simple I / you / we / they</span> talk</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="talk"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_talk__gb_1').play();" style="cursor: pointer" title="talk pronunciationEnglish" valign="top"></a><span class="phon">/tɔːk/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="talk"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_talk__us_1').play();" style="cursor: pointer" title="talk pronunciationAmerican" valign="top"></a><span class="phon">/tɔːk/</span></div></span></td></tr><tr class="verb_form" form="thirdps"><td class="verb_form"> <span class="vf_prefix">he / she / it</span> talks</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="talks"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_talks__gb_1').play();" style="cursor: pointer" title="talks pronunciationEnglish" valign="top"></a><span class="phon">/tɔːks/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="talks"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_talks__us_1').play();" style="cursor: pointer" title="talks pronunciationAmerican" valign="top"></a><span class="phon">/tɔːks/</span></div></span></td></tr><tr class="verb_form" form="past"><td class="verb_form"> <span class="vf_prefix">past simple</span> talked</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="talked"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_talked__gb_1').play();" style="cursor: pointer" title="talked pronunciationEnglish" valign="top"></a><span class="phon">/tɔːkt/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="talked"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_talked__us_1').play();" style="cursor: pointer" title="talked pronunciationAmerican" valign="top"></a><span class="phon">/tɔːkt/</span></div></span></td></tr><tr class="verb_form" form="pastpart"><td class="verb_form"> <span class="vf_prefix">past participle</span> talked</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="talked"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_talked__gb_1').play();" style="cursor: pointer" title="talked pronunciationEnglish" valign="top"></a><span class="phon">/tɔːkt/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="talked"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_talked__us_1').play();" style="cursor: pointer" title="talked pronunciationAmerican" valign="top"></a><span class="phon">/tɔːkt/</span></div></span></td></tr><tr class="verb_form" form="prespart"><td class="verb_form"> <span class="vf_prefix">-ing form</span> talking</td><td class="verb_phons"><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="talking"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_talking__gb_1').play();" style="cursor: pointer" title="talking pronunciationEnglish" valign="top"></a><span class="phon">/ˈtɔːkɪŋ/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="talking"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_talking__us_2').play();" style="cursor: pointer" title="talking pronunciationAmerican" valign="top"></a><span class="phon">/ˈtɔːkɪŋ/</span></div></span></td></tr></tbody></table></span></span></div><span class="jumplinks"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/talk#talk_idmgs_1" title="Idioms definition"><span class="jumplink" href="talk_idmgs_1" name="Idioms">Idioms</span></a> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/talk#talk_pvgs_1" title="Phrasal Verbs definition"><span class="jumplink" href="talk_pvgs_1" name="Phrasal Verbs">Phrasal Verbs</span></a></span></div></div></div><ol class="senses_multiple" htag="ol"><span class="shcut-g" id="talk_sngs_1"> <h2 class="shcut" hclass="shcut" htag="h2" id="talk_shcut_1">speak to somebody</h2><li cefr="a1" class="sense" hclass="sense" htag="li" id="talk_sng_1" ox3000="y" sensenum="1"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a1"><span class="ox3ksym_a1"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[intransitive, transitive]</span> <span class="def" hclass="def" htag="span">to say things; to speak in order to give information or to express feelings, ideas, etc.</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__1"><span class="x">Stop talking and listen!</span></li><li class="" htag="li" id="OALD10_talk__2"><span class="x">We <span class="cl">talked on the phone</span> for over an hour.</span></li><li class="" htag="li" id="OALD10_talk__3"> <span class="cf" hclass="cf" htag="span">talk to somebody </span><span class="x">Who were you talking to just now?</span></li><li class="" htag="li" id="OALD10_talk__4"><span class="x">Ann and Joe <span class="cl">aren't talking</span> to each other right now <span class="gloss" hclass="gloss" htag="span">(= they refuse to speak to each other because they have argued)</span>.</span></li><li class="" htag="li" id="OALD10_talk__5"> <span class="cf" hclass="cf" htag="span">talk to yourself</span> <span class="x">Watch the scene where Don talks to himself in the mirror while shaving.</span></li><li class="" htag="li" id="OALD10_talk__6"> <span class="cf" hclass="cf" htag="span">talk with somebody</span> <span class="x">We looked around the school and talked with the principal.</span></li><li class="" htag="li" id="OALD10_talk__7"> <span class="cf" hclass="cf" htag="span">talk about somebody/something</span> <span class="x">They <span class="cl">talk a lot</span> about the inspiration for their music.</span></li><li class="" htag="li" id="OALD10_talk__8"><span class="x">When they get together, all they talk about is football.</span></li><li class="" htag="li" id="OALD10_talk__9"><span class="x"><span class="cl">What are you talking about?</span> <span class="gloss" hclass="gloss" htag="span">(= used when you are surprised, annoyed and/or worried by something that somebody has just said)</span></span></li><li class="" htag="li" id="OALD10_talk__10"><span class="x"><span class="cl">I don't know what you're talking about</span> <span class="gloss" hclass="gloss" htag="span">(= used to say that you did not do something that somebody has accused you of)</span>.</span></li><li class="" htag="li" id="OALD10_talk__11"><span class="x">to talk openly/freely/publicly about something</span></li><li class="" htag="li" id="OALD10_talk__12"> <span class="cf" hclass="cf" htag="span">talk to/with somebody about somebody/something</span> <span class="x">She started talking loudly to her friends about the film they'd just seen.</span></li><li class="" htag="li" id="OALD10_talk__13"> <span class="cf" hclass="cf" htag="span">talk of doing something</span> <span class="x">Mary is talking of looking for another job.</span></li><li class="" htag="li" id="OALD10_talk__14"> <span class="cf" hclass="cf" htag="span">talk of something</span> <span class="x">We often talked of the war.</span></li><li class="" htag="li" id="OALD10_talk__15"> <span class="cf" hclass="cf" htag="span">talk yourself + adj.</span> <span class="x">We talked ourselves hoarse, catching up on all the news. </span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="talk2_unbox_2" unbox="wordfinder"><span class="box_title" onclick="toggle_active(this)">Wordfinder</span><span class="body"><ul class="bullet"><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/conference#conference_sng_1" title="conference definition"><span class="xref">conference</span></a></li><li class="li"><span class="xref" href="delegate_subentryg_1:delegate_topg_2" tofix="">delegate</span></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/exhibition#exhibition_sng_1" title="exhibition definition"><span class="xref">exhibition</span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/name-tag" title="name tag definition"><span class="xref">name tag</span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/plenary#plenary_1" title="plenary definition"><span class="xref">plenary</span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/register#register_sng_1" title="register definition"><span class="xref">register</span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/speaker#speaker_sng_1" title="speaker definition"><span class="xref">speaker</span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/talk#talk_1" title="talk definition"><span class="xref">talk</span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/venue" title="venue definition"><span class="xref">venue</span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/workshop#workshop_sng_2" title="workshop definition"><span class="xref">workshop</span></a></li></ul></span></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="talk2_unbox_3" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__16"><span class="unx">He was so easy to talk to.</span></li><li class="" htag="li" id="OALD10_talk__17"><span class="unx">I think you'd better talk directly to my manager.</span></li><li class="" htag="li" id="OALD10_talk__18"><span class="unx">I'll talk to John this afternoon.</span></li><li class="" htag="li" id="OALD10_talk__19"><span class="unx">I can't talk about it just now.</span></li><li class="" htag="li" id="OALD10_talk__20"><span class="unx">Bruce Springsteen has agreed to talk exclusively to our reporter about his life.</span></li><li class="" htag="li" id="OALD10_talk__21"><span class="unx">I loved to hear him talk about the old days.</span></li><li class="" htag="li" id="OALD10_talk__22"><span class="unx">She talked quite freely about her work.</span></li><li class="" htag="li" id="OALD10_talk__23"><span class="unx">He had talked vaguely of going to work abroad.</span></li><li class="" htag="li" id="OALD10_talk__24"><span class="unx">He talked excitedly of his plans.</span></li></ul></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="talk2_unbox_4" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adverb</span><ul class="collocs_list"><li class="li">loudly</li><li class="li">quietly</li><li class="li">softly</li><li class="li">…</li></ul><span class="unbox">verb + talk</span><ul class="collocs_list"><li class="li">be able to</li><li class="li">can</li><li class="li">need to</li><li class="li">…</li></ul><span class="unbox">preposition</span><ul class="collocs_list"><li class="li">about</li><li class="li">of</li><li class="li">to</li><li class="li">…</li></ul><span class="unbox">phrases</span><ul class="collocs_list"><li class="li">start talking</li><li class="li">stop talking</li><li class="li">talk a good game</li><li class="li">…</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/talk_2" title=" definition in Collocations">full entry</a></span></span></span></div> </li></span><span class="shcut-g" id="talk_sngs_2"> <h2 class="shcut" hclass="shcut" htag="h2" id="talk_shcut_2">discuss</h2><li cefr="a1" class="sense" hclass="sense" htag="li" id="talk_sng_2" ox3000="y" sensenum="2"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a1"><span class="ox3ksym_a1"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[intransitive, transitive]</span> <span class="def" hclass="def" htag="span">to discuss something, usually something serious or important</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__25"><span class="x">This situation can't go on. We need to talk.</span></li><li class="" htag="li" id="OALD10_talk__26"><span class="x">The two sides in the dispute are finally willing to talk.</span></li><li class="" htag="li" id="OALD10_talk__27"> <span class="cf" hclass="cf" htag="span">talk to/with somebody </span><span class="x">Talk with your doctor before radically changing your diet.</span></li><li class="" htag="li" id="OALD10_talk__28"> <span class="cf" hclass="cf" htag="span">talk about something</span> <span class="x">Is there anything you'd like to talk about?</span></li><li class="" htag="li" id="OALD10_talk__29"> <span class="cf" hclass="cf" htag="span">talk to/with somebody about something </span><span class="x">We want to talk to people about this issue.</span></li><li class="" htag="li" id="OALD10_talk__30"> <span class="cf" hclass="cf" htag="span">talk something</span> <span class="x">to <span class="cl">talk politics</span></span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="talk2_unbox_5" unbox="synonyms"><span class="box_title" onclick="toggle_active(this)">Synonyms <span class="closed">talk</span></span><span class="body"><span class="unbox">talk</span><ul class="inline"><li class="li">discuss</li><li class="li">speak</li><li class="li">communicate</li><li class="li">debate</li><li class="li">consult</li></ul><span class="p">These words all mean to share news, information, ideas or feelings with another person or other people, especially by talking with them.</span><ul class="deflist"><li class="li"><span class="dt">talk</span> <span class="dd">to speak in order to give information, express feelings or share ideas:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__31"><span class="unx">We talked on the phone for over an hour.</span></li></ul></span></li><li class="li"><span class="dt">discuss</span> <span class="dd">(<span class="ei">rather formal</span>) to talk and share ideas on a subject or problem with other people, especially in order to decide something:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__32"><span class="unx">Have you discussed the problem with anyone? </span></li></ul><span class="un" un="note">You cannot say ‘discuss about something’: <span class="wx">I’m not prepared to discuss about this on the phone.</span> </span></span></li><li class="li"><span class="dt">speak</span> <span class="dd">to talk to somebody about something; to have a conversation with somebody:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__33"><span class="unx">I’ve spoken to the manager about it.</span></li><li class="" htag="li" id="OALD10_talk__34"><span class="unx">‘Can I speak to Susan?’ ‘Speaking.’ <span class="gloss" hclass="gloss" htag="span">(= at the beginning of a phone conversation)</span></span></li></ul></span></li></ul> <span class="un"><span class="h4">talk or speak?</span><span class="p"><span class="eb">Speak</span> can suggest a more formal level of communication than <span class="eb">talk</span>. You <span class="eb">speak</span> to somebody about something to try to achieve a particular goal or to tell them to do something. You <span class="eb">talk</span> to somebody in order to be friendly or to ask their advice: <span class="ei">Have you talked to your parents about the problems you’re having?</span> • <span class="ei">I’ve spoken to Ed about it and he’s promised not to let it happen again.</span> </span></span><ul class="deflist"><li class="li"><span class="dt">communicate</span> <span class="dd">(<span class="ei">rather formal</span>) to exchange information or ideas with somebody:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__35"><span class="unx">We only communicate by email.</span></li><li class="" htag="li" id="OALD10_talk__36"><span class="unx">Dolphins use sound to communicate with each other.</span></li></ul> <span class="un" un="note"><span class="eb">Communicate</span> is often used when the speaker wants to draw attention to the means of communication used.</span></span></li><li class="li"><span class="dt">debate</span> <span class="dd">to discuss something, especially formally, before making a decision or finding a solution:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__37"><span class="unx">Politicians will be debating the bill later this week.</span></li></ul></span></li><li class="li"><span class="dt">consult</span> <span class="dd">(<span class="ei">rather formal</span>) to discuss something with somebody in order to get their permission for something, or to help you make a decision:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__38"><span class="unx">You shouldn’t have done it without consulting me.</span></li></ul></span></li></ul><span class="patterns">Patterns</span><ul class="bullet"><li class="li">to talk/​discuss something/​speak/​communicate/​debate/​consult <span class="eb">with</span> somebody </li><li class="li">to talk/​speak <span class="eb">to</span> somebody</li><li class="li">to talk/​speak to somebody/​consult somebody <span class="eb">about</span> something</li><li class="li">to talk/​speak <span class="eb">of </span>something</li></ul></span></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="talk2_unbox_6" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adverb</span><ul class="collocs_list"><li class="li">loudly</li><li class="li">quietly</li><li class="li">softly</li><li class="li">…</li></ul><span class="unbox">verb + talk</span><ul class="collocs_list"><li class="li">be able to</li><li class="li">can</li><li class="li">need to</li><li class="li">…</li></ul><span class="unbox">preposition</span><ul class="collocs_list"><li class="li">about</li><li class="li">of</li><li class="li">to</li><li class="li">…</li></ul><span class="unbox">phrases</span><ul class="collocs_list"><li class="li">start talking</li><li class="li">stop talking</li><li class="li">talk a good game</li><li class="li">…</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/talk_2" title=" definition in Collocations">full entry</a></span></span></span></div> <div class=""> <div class="am-default contentslot" id="ad_contentslot_1"></div></div> </li></span><span class="shcut-g" id="talk_sngs_3"> <h2 class="shcut" hclass="shcut" htag="h2" id="talk_shcut_3">say words</h2><li cefr="a1" class="sense" hclass="sense" htag="li" id="talk_sng_3" ox3000="y" sensenum="3"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a1"><span class="ox3ksym_a1"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[intransitive, transitive]</span> <span class="def" hclass="def" htag="span">to say words in a language</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__39"><span class="x">The baby is just starting to talk.</span></li><li class="" htag="li" id="OALD10_talk__40"><span class="x">Alex can't talk yet—he's only just one year old.</span></li><li class="" htag="li" id="OALD10_talk__41"> <span class="cf" hclass="cf" htag="span">talk in something</span> <span class="x">We couldn't understand them because they were talking in Chinese.</span></li><li class="" htag="li" id="OALD10_talk__42"> <span class="cf" hclass="cf" htag="span">talk something</span> <span class="x">Are they talking Swedish or Danish?</span></li></ul> </li></span><span class="shcut-g" id="talk_sngs_4"> <h2 class="shcut" hclass="shcut" htag="h2" id="talk_shcut_4">sense/nonsense</h2><li class="sense" hclass="sense" htag="li" id="talk_sng_4" sensenum="4"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[transitive]</span></span> <span class="cf" hclass="cf" htag="span">talk something</span> <span class="def" hclass="def" htag="span">to say things that are/are not sensible</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__43"><span class="x">She talks a lot of sense.</span></li><li class="" htag="li" id="OALD10_talk__44"> <span class="labels" hclass="labels" htag="span">(British English)</span> <span class="x">You're talking rubbish!</span></li><li class="" htag="li" id="OALD10_talk__45"><span class="x">See if you can <span class="cl">talk some sense into him</span> <span class="gloss" hclass="gloss" htag="span">(= persuade him to be sensible)</span>.</span></li></ul> </li></span><span class="shcut-g" id="talk_sngs_5"> <h2 class="shcut" hclass="shcut" htag="h2" id="talk_shcut_5">for emphasis</h2><li class="sense" hclass="sense" htag="li" id="talk_sng_5" sensenum="5"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[transitive]</span></span> <div class="variants" hclass="variants" htag="div" type="alt"><span class="v-g"><span class="v">be talking something</span></span></div> <span class="labels" hclass="labels" htag="span">(informal)</span> <span class="def" hclass="def" htag="span">used to emphasize an amount of money, how serious something is, etc.</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__46"><span class="x">We're talking £500 for three hours' work.</span></li><li class="" htag="li" id="OALD10_talk__47"><span class="x">Do you know what this will cost? We’re talking megabucks here.</span></li></ul> </li></span><span class="shcut-g" id="talk_sngs_6"> <h2 class="shcut" hclass="shcut" htag="h2" id="talk_shcut_6">about private life</h2><li class="sense" hclass="sense" htag="li" id="talk_sng_6" sensenum="6"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[intransitive]</span></span> <span class="def" hclass="def" htag="span">to talk about a person’s private life</span> <span class="xrefs" hclass="xrefs" htag="span" xt="nsyn"><span class="prefix">synonym</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/gossip#gossip_2" title="gossip definition"><span bord="n" class="xr-g" href="gossip_subentryg_2:gossip_topg_3"><span class="xh">gossip</span></span></a></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__48"><span class="x">Don't phone me at work—<span class="cl">people will talk</span>.</span></li></ul> </li></span><span class="shcut-g" id="talk_sngs_7"> <h2 class="shcut" hclass="shcut" htag="h2" id="talk_shcut_7">give information</h2><li class="sense" hclass="sense" htag="li" id="talk_sng_7" sensenum="7"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[intransitive]</span></span> <span class="def" hclass="def" htag="span">to give information to somebody, especially unwillingly</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__49"><span class="x">The police questioned him but he refused to talk.</span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="talk2_unbox_7" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adverb</span><ul class="collocs_list"><li class="li">loudly</li><li class="li">quietly</li><li class="li">softly</li><li class="li">…</li></ul><span class="unbox">verb + talk</span><ul class="collocs_list"><li class="li">be able to</li><li class="li">can</li><li class="li">need to</li><li class="li">…</li></ul><span class="unbox">preposition</span><ul class="collocs_list"><li class="li">about</li><li class="li">of</li><li class="li">to</li><li class="li">…</li></ul><span class="unbox">phrases</span><ul class="collocs_list"><li class="li">start talking</li><li class="li">stop talking</li><li class="li">talk a good game</li><li class="li">…</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/talk_2" title=" definition in Collocations">full entry</a></span></span></span></div> <div class=""> <div class="am-default contentslot" id="ad_contentslot_2"></div></div> </li></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="talk2_unbox_8" unbox="wordorigin"><span class="box_title" onclick="toggle_active(this)">Word Origin</span><span class="body"><span class="p">Middle English: frequentative verb from the Germanic base of <span class="eb">tale</span> or <span class="eb">tell</span>.</span></span></span></div></ol><div class="idioms" hclass="idioms" htag="div" id="talk_idmgs_1"><span class="idioms_heading">Idioms</span> <span class="idm-g" id="talk_idmg_6" sk="knocksomesenseinto"><div class="top-container"><div class="top-g" id="talk_topg_34"><div class="webtop"><span class="idm" id="talk_idm_20">knock/talk some sense into somebody</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="talk_sng_13"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to try and persuade somebody to stop behaving in a stupid way, sometimes using rough or violent methods</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__50"><span class="x">Try and talk some sense into her before she makes the wrong decision.</span></li><li class="" htag="li" id="OALD10_talk__51"><span class="x">Where would I be without you to knock some sense into my head?</span></li></ul> </li></ol></span> <span class="idm-g" id="talk_idmg_8" sk="knowwhatyouretalkingabout"><div class="top-container"><div class="top-g" id="talk_topg_35"><div class="webtop"><span class="idm" id="talk_idm_21">know what you’re talking about</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="talk_sng_15"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">to have knowledge about something from your own experience</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__52"><span class="x">I’ve lived in China, so I know what I’m talking about.</span></li></ul> </li></ol></span> <span class="idm-g" id="talk_idmg_1" sk="lookwhostalking"><div class="top-container"><div class="top-g" id="talk_topg_3"><div class="webtop"><span class="idm" id="talk_idm_1">look who’s talking <span class="idmsep">|</span> you can/can’t talk <span class="idmsep">|</span> you’re a fine one to talk</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="talk_sng_8"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">used to tell somebody that they should not criticize somebody else for something because they do the same things too</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__53"><span class="x">‘George is so careless with money.’ ‘Look who's talking!’</span></li></ul> </li></ol></span> <span class="idm-g" id="talk_idmg_10" sk="moneytalks"><div class="top-container"><div class="top-g" id="talk_topg_36"><div class="webtop"><span class="idm" id="talk_idm_22">money talks</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="talk_sng_17"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(saying)</span></span> <span class="def" hclass="def" htag="span">people who have a lot of money have more power and influence than others</span> </li></ol></span> <span class="idm-g" id="talk_idmg_2" sk="nowyouretalking"><div class="top-container"><div class="top-g" id="talk_topg_4"><div class="webtop"><span cefr="c2" class="idm" id="talk_idm_4">now you’re talking</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" fkcefr="c2" hclass="sense" htag="li" id="talk_sng_9"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">used when you like what somebody has suggested very much</span><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/suggestions-and-advice?level=c2" title="Topic suggestions-and-advice"><span class="topic" href="l2:functions:suggestions_and_advice?cefr=c2"><span class="topic_name">Suggestions and advice</span><span class="topic_cefr">c2</span></span></a></span> <div class=""> <div class="am-default contentslot" id="ad_contentslot_3"></div></div> </li></ol></span> <span class="idm-g" id="talk_idmg_17" sk="speakofdevil"><div class="top-container"><div class="top-g" id="talk_topg_37"><div class="webtop"><span class="idm" id="talk_idm_23">speak/talk of the devil</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="talk_sng_23"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">people say <span class="eb">speak/talk of the devil</span> when somebody they have been talking about appears unexpectedly</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__54"><span class="x">Well, speak of the devil—here's Alice now!</span></li></ul> </li></ol></span> <span class="idm-g" id="talk_idmg_18" sk="speakoutofturn"><div class="top-container"><div class="top-g" id="talk_topg_38"><div class="webtop"><span class="idm" id="talk_idm_24">speak/talk out of turn</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="talk_sng_24"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to say something that you should not because it is the wrong situation or because it offends somebody</span></span> </li></ol></span> <span class="idm-g" id="talk_idmg_19" sk="speaksamelanguage"><div class="top-container"><div class="top-g" id="talk_topg_39"><div class="webtop"><span cefr="c2" class="idm" id="talk_idm_25">speak/talk the same language</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" fkcefr="c2" hclass="sense" htag="li" id="talk_sng_25"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to be able to communicate easily with another person because you share similar opinions and experience</span></span><span class="topic-g"><span class="prefix">Topics </span><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/topic/opinion-and-argument?level=c2" title="Topic opinion-and-argument"><span class="topic" href="l2:functions:opinion_and_argument?cefr=c2"><span class="topic_name">Opinion and argument</span><span class="topic_cefr">c2</span></span></a></span> </li></ol></span> <span class="idm-g" id="talk_idmg_3" sk="talkabout"><div class="top-container"><div class="top-g" id="talk_topg_5"><div class="webtop"><span class="idm" id="talk_idm_5">talk about…</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="talk_sng_10"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">used to emphasize something</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__55"><span class="x">Talk about mean! She didn't even buy me a card.</span></li></ul> </li></ol></span> <span class="idm-g" id="talk_idmg_20" sk="talkdirty"><div class="top-container"><div class="top-g" id="talk_topg_40"><div class="webtop"><span class="idm" id="talk_idm_26">talk dirty</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="talk_sng_26"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">to talk to somebody about sex in order to make them sexually excited</span> <div class=""> <div class="am-default contentslot" id="ad_contentslot_4"></div></div> </li></ol></span> <span class="idm-g" id="talk_idmg_21" sk="talkgoodgame"><div class="top-container"><div class="top-g" id="talk_topg_41"><div class="webtop"><span class="idm" id="talk_idm_27">talk a good game</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="talk_sng_27"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to talk in a way that sounds convincing, but may not be sincere</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__56"><span class="x">The senior managers talk a good game about customer relations, but really they don't care about the customer.</span></li></ul> </li></ol></span> <span class="idm-g" id="talk_idmg_22" sk="talkhindlegsoffdonkey"><div class="top-container"><div class="top-g" id="talk_topg_42"><div class="webtop"><span class="idm" id="talk_idm_28">talk the hind legs off a donkey</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="talk_sng_28"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">to talk too much, especially about things that are boring or not important</span> </li></ol></span> <span class="idm-g" id="talk_idmg_7" sk="talkingof"><div class="top-container"><div class="top-g" id="talk_topg_9"><div class="webtop"><span class="idm" id="talk_idm_9">talking of somebody/something</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="talk_sng_14"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal, especially British English)</span></span> <span class="def" hclass="def" htag="span">used when you are going to say more about a subject that has already been mentioned</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__57"><span class="x">Talking of Sue, I met her new boyfriend last week.</span></li></ul> </li></ol></span> <span class="idm-g" id="talk_idmg_4" sk="talkofdevil"><div class="top-container"><div class="top-g" id="talk_topg_33"><div class="webtop"><span class="idm" id="talk_idm_29">talk/speak of the devil</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="talk_sng_11"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">people say <span class="eb">talk/speak of the devil</span> when somebody they have been talking about appears unexpectedly</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__58"><span class="x">Well, talk of the devil—here's Alice now!</span></li></ul> </li></ol></span> <span class="idm-g" id="talk_idmg_23" sk="talkshop"><div class="top-container"><div class="top-g" id="talk_topg_43"><div class="webtop"><span class="idm" id="talk_idm_30">talk shop</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="talk_sng_29"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(usually disapproving)</span></span> <span class="def" hclass="def" htag="span">to talk about your work with the people you work with, especially when you are also with other people who are not connected with or interested in it</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__59"><span class="x">Whenever we meet up with Clive and Sue they always end up talking shop.</span></li></ul> <div class=""> <div class="am-default contentslot" id="ad_contentslot_5"></div></div> </li></ol></span> <span class="idm-g" id="talk_idmg_9" sk="talktalk"><div class="top-container"><div class="top-g" id="talk_topg_11"><div class="webtop"><span class="idm" id="talk_idm_11">talk the talk</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="talk_sng_16"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal, sometimes disapproving)</span></span> <span class="def" hclass="def" htag="span">to be able to talk in a confident way that makes people think you are good at what you do</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__60"><span class="x">You can talk the talk, but can you walk the walk? <span class="gloss" hclass="gloss" htag="span">(= can you act in a way that matches your words?)</span></span></li></ul> </li></ol></span> <span class="idm-g" id="talk_idmg_29" sk="talkthroughhat"><div class="top-container"><div class="top-g" id="talk_topg_44"><div class="webtop"><span class="idm" id="talk_idm_31">talk through your hat</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="talk_sng_30"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(old-fashioned, informal)</span></span> <span class="def" hclass="def" htag="span">to say silly things while you are talking about a subject you do not understand</span> </li></ol></span> <span class="idm-g" id="talk_idmg_11" sk="talktough"><div class="top-container"><div class="top-g" id="talk_topg_13"><div class="webtop"><span class="idm" id="talk_idm_13">talk tough (on something)</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="talk_sng_18"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal, especially North American English)</span></span> <span class="def" hclass="def" htag="span">to tell people very strongly what you want</span> </li></ol></span> <span class="idm-g" id="talk_idmg_12" sk="talkturkey"><div class="top-container"><div class="top-g" id="talk_topg_14"><div class="webtop"><span class="idm" id="talk_idm_14">talk turkey</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="talk_sng_19"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal, especially North American English)</span></span> <span class="def" hclass="def" htag="span">to talk about something seriously</span> </li></ol></span> <span class="idm-g" id="talk_idmg_13" sk="talkwayoutofdoingsth"><div class="top-container"><div class="top-g" id="talk_topg_15"><div class="webtop"><span class="idm" id="talk_idm_15">talk your way out of something/of doing something</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="talk_sng_20"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">to make excuses and give reasons for not doing something; to manage to get yourself out of a difficult situation</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__61"><span class="x">I managed to talk my way out of having to give a speech.</span></li></ul> <div class=""> <div class="am-default contentslot" id="ad_contentslot_6"></div></div> </li></ol></span> <span class="idm-g" id="talk_idmg_14" sk="youcantalk"><div class="top-container"><div class="top-g" id="talk_topg_16"><div class="webtop"><span class="idm" id="talk_idm_32">you can/can't talk <span class="idmsep">|</span> look who's talking <span class="idmsep">|</span> you're a fine one to talk</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="talk_sng_21"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">used to tell somebody that they should not criticize somebody else for something because they do the same things too</span> </li></ol></span> <span class="idm-g" id="talk_idmg_15" sk="yourefineonetotalk"><div class="top-container"><div class="top-g" id="talk_topg_17"><div class="webtop"><span class="idm" id="talk_idm_35">you're a fine one to talk <span class="idmsep">|</span> look who's talking <span class="idmsep">|</span> you can/can't talk</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="talk_sng_22"><span class="sensetop" hclass="sensetop" htag="span"><span class="labels" hclass="labels" htag="span">(informal)</span></span> <span class="def" hclass="def" htag="span">used to tell somebody that they should not criticize somebody else for something because they do the same things too</span> </li></ol></span></div><aside class="phrasal_verb_links" hclass="phrasal_verb_links" htag="aside" id="talk_pvgs_1" xt="pvlist"><span class="unbox">Phrasal Verbs</span><ul class="pvrefs"><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/talk-around#talkaround2_e" title="talk around definition"><span bord="n" class="xr-g" href="talkaround2_e" id="talk_xrg_20"><span class="xh">talk around</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/talk-at#talkat2_e" title="talk at definition"><span bord="n" class="xr-g" href="talkat2_e" id="talk_xrg_22"><span class="xh">talk at</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/talk-back#talkback3_e" title="talk back definition"><span bord="n" class="xr-g" href="talkback3_e" id="talk_xrg_23"><span class="xh">talk back</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/talk-down#talkdown2_e" title="talk down definition"><span bord="n" class="xr-g" href="talkdown2_e" id="talk_xrg_24"><span class="xh">talk down</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/talk-down-to#talkdownto2_e" title="talk down to definition"><span bord="n" class="xr-g" href="talkdownto2_e" id="talk_xrg_25"><span class="xh">talk down to</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/talk-into#talkinto2_e" title="talk into definition"><span bord="n" class="xr-g" href="talkinto2_e" id="talk_xrg_26"><span class="xh">talk into</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/talk-out#talkout2_e" title="talk out definition"><span bord="n" class="xr-g" href="talkout2_e" id="talk_xrg_28"><span class="xh">talk out</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/talk-out-of#talkoutof2_e" title="talk out of definition"><span bord="n" class="xr-g" href="talkoutof2_e" id="talk_xrg_30"><span class="xh">talk out of</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/talk-over#talkover2_e" title="talk over definition"><span bord="n" class="xr-g" href="talkover2_e" id="talk_xrg_31"><span class="xh">talk over</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/talk-round#talkround2_e" title="talk round definition"><span bord="n" class="xr-g" href="talkround2_e" id="talk_xrg_32"><span class="xh">talk round</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/talk-through#talkthrough2_e" title="talk through definition"><span bord="n" class="xr-g" href="talkthrough2_e" id="talk_xrg_33"><span class="xh">talk through</span></span></a></li><li class="li"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/talk-up#talkup2_e" title="talk up definition"><span bord="n" class="xr-g" href="talkup2_e" id="talk_xrg_34"><span class="xh">talk up</span></span></a></li></ul></aside></div><div class="entry" hclass="entry" hlength="4" htag="section" id="talk_2" idm_id="000059372" sk="talk::2" sum="2960"><div class="top-container"><div class="top-g" id="talk_topg_31"><div class="webtop"><h1 class="headword" hclass="headword" htag="h1" id="talk_h_2" opal_spoken="y" ox3000="y">talk</h1> <span class="pos" hclass="pos" htag="span">noun</span><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a2"><span class="ox3ksym_a2"> </span></a><a href="https://www.oxfordlearnersdictionaries.com/wordlists/opal?dataset=english&amp;list=opal_spoken&amp;level=sublist_2"><span class="opal_symbol" href="OPAL_Spoken::Sublist_2">OPAL S</span></a></div><span class="phonetics"> <div class="phons_br" geo="br" hclass="phons_br" htag="div" wd="talk"><a class="sound audio_play_button pron-uk icon-audio" onclick="document.getElementById('OALD10_pron_talk__gb_1').play();" style="cursor: pointer" title="talk pronunciationEnglish" valign="top"></a><span class="phon">/tɔːk/</span></div> <div class="phons_n_am" geo="n_am" hclass="phons_n_am" htag="div" wd="talk"><a class="sound audio_play_button pron-us icon-audio" onclick="document.getElementById('OALD10_pron_talk__us_1').play();" style="cursor: pointer" title="talk pronunciationAmerican" valign="top"></a><span class="phon">/tɔːk/</span></div></span><span class="jumplinks"><a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/talk#talk_idmgs_2" title="Idioms definition"><span class="jumplink" href="talk_idmgs_2" name="Idioms">Idioms</span></a> </span></div></div></div><ol class="senses_multiple" htag="ol"><span class="shcut-g" id="talk_sngs_38"> <h2 class="shcut" hclass="shcut" htag="h2" id="talk_shcut_10">speech</h2><li cefr="a2" class="sense" hclass="sense" htag="li" id="talk_sng_38" ox3000="y" sensenum="1"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=a2"><span class="ox3ksym_a2"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[countable]</span> <span class="def" hclass="def" htag="span">a speech or lecture on a particular subject</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__62"><span class="x">I went to several interesting talks at the conference.</span></li><li class="" htag="li" id="OALD10_talk__63"> <span class="cf" hclass="cf" htag="span">talk on something</span> <span class="x">She <span class="cl">gave a talk</span> on her visit to China.</span></li><li class="" htag="li" id="OALD10_talk__64"> <span class="cf" hclass="cf" htag="span">talk about something</span> <span class="x">I <span class="cl">heard a talk</span> about the history of printing.</span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="talk3_unbox_1" unbox="synonyms"><span class="box_title" onclick="toggle_active(this)">Synonyms <span class="closed">speech</span></span><span class="body"><span class="unbox">speech</span><ul class="inline"><li class="li">lecture</li><li class="li">address</li><li class="li">talk</li><li class="li">sermon</li></ul><span class="p">These are all words for a talk given to an audience.</span><ul class="deflist"><li class="li"><span class="dt">speech</span> <span class="dd">a formal talk given to an audience:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__65"><span class="unx">Several people made speeches at the wedding.</span></li></ul></span></li><li class="li"><span class="dt">lecture</span> <span class="dd">a talk given to a group of people to tell them about a particular subject, often as part of a university or college course:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__66"><span class="unx">a lecture on the Roman army</span></li><li class="" htag="li" id="OALD10_talk__67"><span class="unx">a course/​series of lectures</span></li></ul></span></li><li class="li"><span class="dt">address</span> <span class="dd">a formal speech given to an audience:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__68"><span class="unx">a televised presidential address</span></li></ul></span></li></ul> <span class="un"><span class="h4">speech or address?</span><span class="p">A <span class="eb">speech</span> can be given on a public or private occasion; an <span class="eb">address</span> is always public: <span class="wx">He gave an address at the wedding.</span> </span></span><ul class="deflist"><li class="li"><span class="dt">talk</span> <span class="dd">a fairly informal session in which somebody tells a group of people about a subject:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__69"><span class="unx">She gave an interesting talk on her visit to China.</span></li></ul></span></li><li class="li"><span class="dt">sermon</span> <span class="dd">a talk on a moral or religious subject, usually given by a religious leader during a service:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__70"><span class="unx">to preach a sermon</span></li></ul></span></li></ul><span class="patterns">Patterns</span><ul class="bullet"><li class="li">a <span class="eb">long/​short</span> speech/​lecture/​address/​talk/​sermon</li><li class="li">a <span class="eb">keynote</span> speech/​lecture/​address</li><li class="li">to <span class="eb">write/​prepare/​give/​deliver/​hear</span> a(n) speech/​lecture/​address/​talk/​sermon</li><li class="li">to <span class="eb">attend/​go to</span> a lecture/​talk</li></ul></span></span></div> <span class="xrefs" hclass="xrefs" htag="span" xt="see"><span class="prefix">see also</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/chalk-talk" title="chalk talk definition"><span bord="n" class="xr-g" href="chalktalk_e"><span class="xh">chalk talk</span></span></a></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="talk3_unbox_2" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__71"><span class="unx">I found the sales talk very persuasive.</span></li><li class="" htag="li" id="OALD10_talk__72"><span class="unx">She gave an illustrated talk on Roman architecture.</span></li><li class="" htag="li" id="OALD10_talk__73"><span class="unx">Let me end my talk with a prediction.</span></li><li class="" htag="li" id="OALD10_talk__74"><span class="unx">Did you go to the talk on Peru?</span></li></ul></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="talk3_unbox_3" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adjective</span><ul class="collocs_list"><li class="li">entertaining</li><li class="li">informative</li><li class="li">interesting</li><li class="li">…</li></ul><span class="unbox">verb + talk</span><ul class="collocs_list"><li class="li">deliver</li><li class="li">give</li><li class="li">present</li><li class="li">…</li></ul><span class="unbox">talk + verb</span><ul class="collocs_list"><li class="li">be entitled something</li><li class="li">be titled something</li></ul><span class="unbox">preposition</span><ul class="collocs_list"><li class="li">talk about</li><li class="li">talk on</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/talk_1" title=" definition in Collocations">full entry</a></span></span></span></div> </li></span><span class="shcut-g" id="talk_sngs_36"> <h2 class="shcut" hclass="shcut" htag="h2" id="talk_shcut_8">conversation</h2><li cefr="b1" class="sense" hclass="sense" htag="li" id="talk_sng_36" ox3000="y" sensenum="2"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=b1"><span class="ox3ksym_b1"> </span></a></div></span> <span class="grammar" hclass="grammar" htag="span">[countable]</span> <span class="def" hclass="def" htag="span">a conversation or discussion</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__75"><span class="x">She looked worried so we <span class="cl">had a talk</span>.</span></li><li class="" htag="li" id="OALD10_talk__76"> <span class="cf" hclass="cf" htag="span">talk about something</span> <span class="x">We need to have a serious talk about money matters.</span></li><li class="" htag="li" id="OALD10_talk__77"> <span class="cf" hclass="cf" htag="span">talk with somebody</span> <span class="x">I had to have a heart-to-heart talk with her.</span></li><li class="" htag="li" id="OALD10_talk__78"><span class="x">I will have to <span class="cl">have a little talk with</span> that young lady <span class="gloss" hclass="gloss" htag="span">(= talk to her about something she has done wrong)</span>.</span></li><li class="" htag="li" id="OALD10_talk__79"> <span class="cf" hclass="cf" htag="span">talk with somebody about something</span> <span class="x">I had a long talk with my boss about my career prospects.</span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="talk3_unbox_4" unbox="synonyms"><span class="box_title" onclick="toggle_active(this)">Synonyms <span class="closed">discussion</span></span><span class="body"><span class="unbox">discussion</span><ul class="inline"><li class="li">conversation</li><li class="li">dialogue</li><li class="li">talk</li><li class="li">debate</li><li class="li">consultation</li><li class="li">chat</li><li class="li">gossip</li></ul><span class="p">These are all words for an occasion when people talk about something.</span><ul class="deflist"><li class="li"><span class="dt">discussion</span> <span class="dd">a detailed conversation about something that is considered to be important:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__80"><span class="unx">Discussions are still taking place between the two leaders.</span></li></ul></span></li><li class="li"><span class="dt">conversation</span> <span class="dd">a talk, usually a private or informal one, involving two people or a small group; the activity of talking in this way:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__81"><span class="unx">a telephone conversation</span></li></ul></span></li><li class="li"><span class="dt">dialogue</span> <span class="dd">conversations in a book, play or film:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__82"><span class="unx">The novel has long descriptions and not much dialogue.</span></li></ul> A <span class="eb">dialogue</span> is also a formal discussion between two groups, especially when they are trying to solve a problem or end a dispute:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__83"><span class="unx">The President told waiting reporters there had been a constructive dialogue.</span></li></ul></span></li><li class="li"><span class="dt">talk</span> <span class="dd">a conversation or discussion, often one about a problem or something important for the people involved:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__84"><span class="unx">I had a long talk with my boss about my career prospects.</span></li></ul></span></li><li class="li"><span class="dt">debate</span> <span class="dd">a formal discussion of an issue at a public meeting or in a parliament. In a debate two or more speakers express opposing views and then there is often a vote on the issue:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__85"><span class="unx">a debate on prison reform</span></li></ul></span></li><li class="li"><span class="dt">consultation</span> <span class="dd">a formal discussion between groups of people before a decision is made about something:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__86"><span class="unx">There have been extensive consultations between the two countries.</span></li></ul></span></li><li class="li"><span class="dt">chat</span> <span class="dd">a friendly informal conversation; informal talking. <span class="un" un="note">The countable use of <span class="eb">chat</span> is especially British English: <span class="ei">I just called in for a chat about the kids.</span> </span></span></li><li class="li"><span class="dt">gossip</span> <span class="dd">a conversation about other people and their private lives:<ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__87"><span class="unx">We had a good gossip about the boss.</span></li></ul></span></li></ul><span class="patterns">Patterns</span><ul class="bullet"><li class="li">a discussion/​conversation/​dialogue/​talk/​debate/​consultation/​chat/​gossip <span class="eb">about</span> something</li><li class="li">a discussion/​conversation/​dialogue/​debate/​consultation <span class="eb">on</span> something</li><li class="li"><span class="eb">in (close)</span> discussion/​conversation/​dialogue/​debate/​consultation <span class="eb">with</span> somebody</li><li class="li">to <span class="eb">have</span> a discussion/​conversation/​dialogue/​talk/​debate/​consultation/​chat/​gossip <span class="eb">with</span> somebody</li><li class="li">to <span class="eb">hold</span> a discussion/​conversation/​debate/​consultation</li></ul></span></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="talk3_unbox_5" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__88"><span class="unx">I enjoyed our talk about the old days.</span></li><li class="" htag="li" id="OALD10_talk__89"><span class="unx">Every week the sales reps go to their manager's office for a team talk.</span></li></ul></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="talk3_unbox_6" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adjective</span><ul class="collocs_list"><li class="li">brief</li><li class="li">little</li><li class="li">short</li><li class="li">…</li></ul><span class="unbox">verb + talk</span><ul class="collocs_list"><li class="li">have</li></ul><span class="unbox">talk + noun</span><ul class="collocs_list"><li class="li">show</li><li class="li">radio</li></ul><span class="unbox">preposition</span><ul class="collocs_list"><li class="li">talk about</li><li class="li">talk with</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/talk_1" title=" definition in Collocations">full entry</a></span></span></span></div> <div class=""> <div class="am-default contentslot" id="ad_contentslot_1"></div></div> </li></span><span class="shcut-g" id="talk_sngs_37"> <h2 class="shcut" hclass="shcut" htag="h2" id="talk_shcut_9">formal discussions</h2><li cefr="b1" class="sense" hclass="sense" htag="li" id="talk_sng_37" ox3000="y" sensenum="3"><span class="sensetop" hclass="sensetop" htag="span"><div class="symbols" hclass="symbols" htag="div"><a href="https://www.oxfordlearnersdictionaries.com/wordlists/oxford3000-5000?dataset=english&amp;list=ox3000&amp;level=b1"><span class="ox3ksym_b1"> </span></a></div></span> <div class="variants" hclass="variants" htag="div" type="alt"><span class="v-g"><span class="v">talks</span></span></div> <span class="grammar" hclass="grammar" htag="span">[plural]</span> <span class="def" hclass="def" htag="span">formal discussions between governments or organizations</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__90"><span class="x"><span class="cl">peace/trade talks</span></span></li><li class="" htag="li" id="OALD10_talk__91"><span class="x">to <span class="cl">hold talks</span></span></li><li class="" htag="li" id="OALD10_talk__92"><span class="x">to engage in/enter into talks</span></li><li class="" htag="li" id="OALD10_talk__93"><span class="x">to pull out of talks</span></li><li class="" htag="li" id="OALD10_talk__94"> <span class="cf" hclass="cf" htag="span">talks with somebody/something</span> <span class="x">The delegation arrived for talks with their government.</span></li><li class="" htag="li" id="OALD10_talk__95"> <span class="cf" hclass="cf" htag="span">in talks with somebody/something</span> <span class="x">He is currently in talks with two football clubs.</span></li><li class="" htag="li" id="OALD10_talk__96"> <span class="cf" hclass="cf" htag="span">talks between A and B</span> <span class="x">Talks between management and workers broke down over the issue of holiday pay.</span></li><li class="" htag="li" id="OALD10_talk__97"> <span class="cf" hclass="cf" htag="span">talks on something</span> <span class="x">Leaders will <span class="cl">resume talks</span> on the trade deal next month.</span></li><li class="" htag="li" id="OALD10_talk__98"> <span class="cf" hclass="cf" htag="span">talks over something</span> <span class="x">Talks continue over the political future of the province.</span></li><li class="" htag="li" id="OALD10_talk__99"><span class="x">A further <span class="cl">round of talks</span> will be needed if the dispute is to be resolved.</span></li><li class="" htag="li" id="OALD10_talk__100"><span class="x">Union representatives walked out of emergency talks aimed at reaching a pay settlement.</span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="talk3_unbox_7" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__101"><span class="unx">Talks produced agreement on an end to the occupation.</span></li><li class="" htag="li" id="OALD10_talk__102"><span class="unx">The peace talks will take place in Cairo.</span></li><li class="" htag="li" id="OALD10_talk__103"><span class="unx">The talks centred on bilateral trade.</span></li><li class="" htag="li" id="OALD10_talk__104"><span class="unx">The talks foundered on the issue of compensation.</span></li><li class="" htag="li" id="OALD10_talk__105"><span class="unx">The two governments held secret talks on the nuclear threat.</span></li><li class="" htag="li" id="OALD10_talk__106"><span class="unx">The talks remain deadlocked over spending plans.</span></li></ul></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="talk3_unbox_8" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adjective</span><ul class="collocs_list"><li class="li">lengthy</li><li class="li">high-level</li><li class="li">top-level</li><li class="li">…</li></ul><span class="unbox">… of talks</span><ul class="collocs_list"><li class="li">round</li></ul><span class="unbox">verb + talk</span><ul class="collocs_list"><li class="li">conduct</li><li class="li">have</li><li class="li">hold</li><li class="li">…</li></ul><span class="unbox">talk + verb</span><ul class="collocs_list"><li class="li">be scheduled</li><li class="li">take place</li><li class="li">begin</li><li class="li">…</li></ul><span class="unbox">preposition</span><ul class="collocs_list"><li class="li">during talk</li><li class="li">in talk</li><li class="li">talk about</li><li class="li">…</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/talk_1" title=" definition in Collocations">full entry</a></span></span></span></div> </li></span><span class="shcut-g" id="talk_sngs_39"> <h2 class="shcut" hclass="shcut" htag="h2" id="talk_shcut_11">words without actions</h2><li class="sense" hclass="sense" htag="li" id="talk_sng_39" sensenum="4"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[uncountable]</span></span> <span class="labels" hclass="labels" htag="span">(informal)</span> <span class="def" hclass="def" htag="span">words that are spoken, but without the necessary facts or actions to support them</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__107"><span class="x">It's just talk. He'd never carry out his threats.</span></li><li class="" htag="li" id="OALD10_talk__108"><span class="x">Don't pay any attention to her—she's <span class="cl">all talk</span>.</span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="talk3_unbox_9" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__109"><span class="unx">For all their talk of equality, the boys ended up not doing any cooking.</span></li><li class="" htag="li" id="OALD10_talk__110"><span class="unx">The chairman's boasts about future profits was just cheap talk.</span></li><li class="" htag="li" id="OALD10_talk__111"><span class="unx">What all the fine talk came down to was hard cash.</span></li></ul></span></div> </li></span><span class="shcut-g" id="talk_sngs_40"> <h2 class="shcut" hclass="shcut" htag="h2" id="talk_shcut_12">stories/rumours</h2><li class="sense" hclass="sense" htag="li" id="talk_sng_40" sensenum="5"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[uncountable]</span></span> <span class="def" hclass="def" htag="span">stories that suggest a particular thing might be true or might happen in the future</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__112"><span class="x">She dismissed the stories of her resignation as newspaper talk.</span></li><li class="" htag="li" id="OALD10_talk__113"> <span class="cf" hclass="cf" htag="span">talk of (doing) something</span> <span class="x">There was talk in Washington of sending in troops.</span></li><li class="" htag="li" id="OALD10_talk__114"> <span class="cf" hclass="cf" htag="span">talk that…</span> <span class="x">There’s talk that he’s a difficult actor to work with.</span></li></ul><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="talk3_unbox_10" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__115"><span class="unx">All this talk of the boss resigning is nonsense!</span></li><li class="" htag="li" id="OALD10_talk__116"><span class="unx">There is ridiculous talk of her breaking the world record soon.</span></li><li class="" htag="li" id="OALD10_talk__117"><span class="unx">There was excited talk of emigrating to America.</span></li><li class="" htag="li" id="OALD10_talk__118"><span class="unx">You often hear talk of the north-south divide.</span></li><li class="" htag="li" id="OALD10_talk__119"><span class="unx">There is open talk of a leadership challenge.</span></li><li class="" htag="li" id="OALD10_talk__120"><span class="unx">There has been much public talk about bilingual education.</span></li></ul></span></div> </li></span><span class="shcut-g" id="talk_sngs_41"> <h2 class="shcut" hclass="shcut" htag="h2" id="talk_shcut_13">topic/way of speaking</h2><li class="sense" hclass="sense" htag="li" id="talk_sng_41" sensenum="6"><span class="sensetop" hclass="sensetop" htag="span"><span class="grammar" hclass="grammar" htag="span">[uncountable]</span></span> <span class="use"><span class="wrap">(</span>often in compounds<span class="wrap">)</span></span> <span class="def" hclass="def" htag="span">a topic of conversation or a way of speaking</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__121"><span class="x">business talk</span></li><li class="" htag="li" id="OALD10_talk__122"><span class="x">She said it was just <span class="cl">girl talk</span> that a man wouldn't understand.</span></li><li class="" htag="li" id="OALD10_talk__123"><span class="x">The book teaches you how to understand Spanish <span class="cl">street talk</span> <span class="gloss" hclass="gloss" htag="span">(= slang)</span>.</span></li><li class="" htag="li" id="OALD10_talk__124"><span class="x">It was tough talk, coming from a man who had begun the year in a hospital bed.</span></li></ul> <span class="xrefs" hclass="xrefs" htag="span" xt="see"><span class="prefix">see also</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/baby-talk" title="baby talk definition"><span bord="n" class="xr-g" href="babytalk_e"><span class="xh">baby talk</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/back-talk" title="back talk definition"><span bord="n" class="xr-g" href="backtalk_e"><span class="xh">back talk</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/pep-talk" title="pep talk definition"><span bord="n" class="xr-g" href="peptalk_e"><span class="xh">pep talk</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/sales-talk" title="sales talk definition"><span bord="n" class="xr-g" href="salestalk_e"><span class="xh">sales talk</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/shop-talk" title="shop talk definition"><span bord="n" class="xr-g" href="shoptalk_e"><span class="xh">shop talk</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/small-talk" title="small talk definition"><span bord="n" class="xr-g" href="smalltalk_e"><span class="xh">small talk</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/sweet-talk#sweet-talk_1" title="sweet talk definition"><span bord="n" class="xr-g" href="sweettalk_e"><span class="xw">sweet talk</span></span></a><span class="sep">,</span> <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/english/trash-talk" title="trash talk definition"><span bord="n" class="xr-g" href="trashtalk_e"><span class="xh">trash talk</span></span></a></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="talk3_unbox_11" unbox="extra_examples"><span class="box_title" onclick="toggle_active(this)">Extra Examples</span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__125"><span class="unx">She likes straight talk and hates hypocrites.</span></li><li class="" htag="li" id="OALD10_talk__126"><span class="unx">The president's true agenda was hidden in political double talk.</span></li><li class="" htag="li" id="OALD10_talk__127"><span class="unx">The US authorities have increased the war talk.</span></li><li class="" htag="li" id="OALD10_talk__128"><span class="unx">the crusade against dirty talk on TV</span></li><li class="" htag="li" id="OALD10_talk__129"><span class="unx">the pillow talk of lovers</span></li><li class="" htag="li" id="OALD10_talk__130"><span class="unx">Talk turned to money and tempers began to fray.</span></li></ul></span></div><div class="collapse" hclass="collapse" htag="div"><span class="unbox" id="talk3_unbox_12" unbox="snippet"><span class="box_title" onclick="toggle_active(this)">Oxford Collocations Dictionary</span><span class="body"><span class="unbox">adjective</span><ul class="collocs_list"><li class="li">excited</li><li class="li">crazy</li><li class="li">wild</li><li class="li">…</li></ul><span class="unbox">verb + talk</span><ul class="collocs_list"><li class="li">hear</li></ul><span class="unbox">talk + verb</span><ul class="collocs_list"><li class="li">turn to something</li></ul><span class="unbox">preposition</span><ul class="collocs_list"><li class="li">talk about/​of</li></ul><span class="unbox">phrases</span><ul class="collocs_list"><li class="li">be all talk</li><li class="li">be just talk</li><li class="li">for all the talk of something</li><li class="li">…</li></ul><span class="xref_to_full_entry">See <a class="Ref" href="https://www.oxfordlearnersdictionaries.com/definition/collocations/talk_1" title=" definition in Collocations">full entry</a></span></span></span></div> </li></span><div class="collapse" hclass="collapse" htag="div"><span class="unbox" dup="fky" id="talk3_unbox_13" unbox="wordorigin"><span class="box_title" onclick="toggle_active(this)">Word Origin</span><span class="body"><span class="p">Middle English: frequentative verb from the Germanic base of <span class="eb">tale</span> or <span class="eb">tell</span>.</span></span></span></div></ol><div class="idioms" hclass="idioms" htag="div" id="talk_idmgs_2"><span class="idioms_heading">Idioms</span> <span class="idm-g" id="talk_idmg_5" sk="fightingtalk"><div class="top-container"><div class="top-g" id="talk_topg_1"><div class="webtop"><span class="idm" id="talk_idm_19">fighting talk</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="talk_sng_12"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">comments or remarks that show that you are ready to fight very hard for something</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__131"><span class="x">What we want from the management is fighting talk.</span></li></ul> <div class=""> <div class="am-default contentslot" id="ad_contentslot_2"></div></div> </li></ol></span> <span class="idm-g" id="talk_idmg_16" sk="talkof"><div class="top-container"><div class="top-g" id="talk_topg_32"><div class="webtop"><span class="idm" id="talk_idm_18">the talk of something</span> </div></div></div><ol class="sense_single" htag="ol"><li class="sense" hclass="sense" htag="li" id="talk_sng_42"><span class="sensetop" hclass="sensetop" htag="span"><span class="def" hclass="def" htag="span">the person or thing that everyone is talking about in a particular place</span></span><ul class="examples" hclass="examples" htag="ul"><li class="" htag="li" id="OALD10_talk__132"><span class="x">Overnight, she became <span class="cl">the talk of the town</span> <span class="gloss" hclass="gloss" htag="span">(= very famous)</span>.</span></li></ul> </li></ol></span></div></div></div></body></html><audio id="OALD10_pron_talk__gb_1" preload="none" src="OALD10_pron_talk__gb_1.mp3"></audio><audio id="OALD10_pron_talk__us_1" preload="none" src="OALD10_pron_talk__us_1.mp3"></audio><audio id="OALD10_pron_talks__gb_1" preload="none" src="OALD10_pron_talks__gb_1.mp3"></audio><audio id="OALD10_pron_talks__us_1" preload="none" src="OALD10_pron_talks__us_1.mp3"></audio><audio id="OALD10_pron_talked__gb_1" preload="none" src="OALD10_pron_talked__gb_1.mp3"></audio><audio id="OALD10_pron_talked__us_1" preload="none" src="OALD10_pron_talked__us_1.mp3"></audio><audio id="OALD10_pron_talking__gb_1" preload="none" src="OALD10_pron_talking__gb_1.mp3"></audio><audio id="OALD10_pron_talking__us_2" preload="none" src="OALD10_pron_talking__us_2.mp3"></audio>`;
